INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (14, '00120', 'Primary bile acid biosynthesis', 'Bile acids are steroid carboxylic acids derived from cholesterol in vertebrates. The primary bile acids, cholic acid and chenodeoxycholic acid, are synthesized in the liver and conjugated with taurine or glycine before secretion via bile into the intestine. The conversion from cholesterol to cholic and chenodeoxycholic acids involves four steps: 1) the initiation of synthesis by 7alpha-hydroxylation of sterol precursors, 2) further modifications to the ring structures, 3) side-chain oxidation and shortening (cleavage) by three carbons, and 4) conjugation of the bile acid with taurine or glycine.', 'Metabolism; Lipid metabolism', 'Primary bile acid biosynthesis', 'Bile acid biosynthesis, cholesterol => cholate/chenodeoxycholate [PATH:map00120], Conjugated bile acid biosynthesis, cholate => taurocholate/glycocholate [PATH:map00120], beta-Oxidation, peroxisome, tri/dihydroxycholestanoyl-CoA => choloyl/chenodeoxycholoyl-CoA [PATH:map00120]', 'Cerebrotendinous xanthomatosis, Peroxisomal beta-oxidation enzyme deficiency, Congenital bile acid synthesis defect, Leukoencephalopathy with dystonia and motor neuropathy, Perrault syndrome, D-bifunctional protein deficiency, Alpha-methylacyl-CoA racemase deficiency', 'ko00120', 'list(REFERENCE = \"PMID:12543708\", AUTHORS = \"Russell DW\", TITLE = \"The enzymes, regulation, and genetics of bile acid synthesis.\", JOURNAL = c(\"Annu Rev Biochem 72:137-74 (2003)\", \"DOI:10.1146/annurev.biochem.72.121801.161712\")), list(REFERENCE = \"PMID:17346171\", AUTHORS = \"Norlin M, Wikvall K\", TITLE = \"Enzymes in the conversion of cholesterol into bile acids.\", JOURNAL = c