INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (19, '00195', 'Photosynthesis', 'Photosynthesis in green plants and specialized bacteria is the process of utilizing light energy to synthesize organic compounds from carbon dioxide and water. It consists of the light dependent part (light reaction) and the light independent part (dark reaction, carbon fixation). The light reaction takes place in thylakoid, a membrane-bound compartment inside chloroplasts and cyanobacteria. The light energy is used by photosystems I and II to generate proton motive force and reducing power (NADPH or NADH). The proton motive force is used by ATP synthase to generate ATP, essentially in the same way as the mitochondrial respiratory chain. The supplies of ATP and NAD(P)H are then used to fix carbon dioxide.', 'Metabolism; Energy metabolism', 'Photosynthesis', 'F-type ATPase, prokaryotes and chloroplasts [PATH:map00195], Photosystem II [PATH:map00195], Cytochrome b6f complex [PATH:map00195], Photosystem I [PATH:map00195]', 'ko00195', 'list(REFERENCE = \"PMID:14764885\", AUTHORS = \"Ferreira KN, Iverson TM, Maghlaoui K, Barber J, Iwata S.\", TITLE = \"Architecture of the photosynthetic oxygen-evolving center.\", JOURNAL = c(\"Science 303:1831-8 (2004)\", \"DOI:10.1126/science.1093087\")), list(REFERENCE = \"PMID:12518057\", AUTHORS = \"Kamiya N, Shen JR.\", TITLE = \"Crystal structure of oxygen-evolving photosystem II from Thermosynechococcus vulcanus at 3.7-A resolution.\", JOURNAL = c(\"Proc Natl Acad Sci U S A 100:98-103 (2003)\", \"DOI:10.1073/pnas.0135651100\")), list(REFERENCE = \"PMID:11217865\", AUTHORS = \"Zouni A, Witt HT, Kern J, Fromme P, Krauss N, Saenger W, Orth P.\", TITLE = \"Crystal structure of photosys