INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (53, '00402', 'Benzoxazinoid biosynthesis', 'Benzoxazinoids (hydroxamic acids) are plant secondary metabolites that serve as important factors for host resistance against microbial pathogens and insects and for allelopathic effects. They are found in grass family and some eudicot families. The predominant benzoxazinoids are DIBOA and its 7-methoxy derivative DIMBOA, which are stored as glucosides in vacuoles. In maize, benzoxazinoid biosynthesis branches off from tryptophan biosynthesis at indole-3-glycerol phosphate, which is converted to indole by indole-3-glycerol phosphate lyase, BX1. Subsequently four cytochrome P450 monooxygenases (BX2-BX5) catalyze the introduction of four oxygen atoms into the indole moiety, yielding DIBOA. After glucosylation by UDP-glucosyltransferase (BX8/BX9), the glucoside is further modified by hydroxylation and O-methylation at C-7 to form DIMBOA-glucoside.', 'Metabolism; Biosynthesis of other secondary metabolites', 'Benzoxazinoid biosynthesis', 'ko00402', 'list(REFERENCE = \"PMID:11106389\", AUTHORS = \"Frey M, Stettner C, Pare PW, Schmelz EA, Tumlinson JH, Gierl A.\", TITLE = \"An herbivore elicitor activates the gene for indole emission in maize.\", JOURNAL = c(\"Proc Natl Acad Sci U S A 97:14801-6 (2000)\", \"DOI:10.1073/pnas.260499897\")), list(REFERENCE = \"PMID:11556781\", AUTHORS = \"Gierl A, Frey M.\", TITLE = \"Evolution of benzoxazinone biosynthesis and indole production in maize.\", JOURNAL = c(\"Planta 213:493-8 (2001)\", \"DOI:10.1007/s004250100594\")), list(REFERENCE = \"PMID:16260753\", AUTHORS = \"Nomura T, Ishihara A, Yanagita RC, Endo TR, Iwamura H.\", TITLE = \"Three genomes differentially contribute to the biosynthesis of benzoxazinones in hexaploid wheat.\", JOURNAL = c(\"Proc Natl Acad Sci U S A 102:16490-5 (2005)\", \"DOI:10.1073/pnas.0505156102\")), list(REFERENCE = \"PMID:9235894\", AUTHORS = \"Frey M, Chomet P, Glawischnig E, Stettner C, Grun S, Winklmair A, Eisenreich W, Bacher A, Meeley RB, Briggs SP, Simcox K, Gierl A.\", TITLE = \"Analysis of a chemical plant defense mechanism in grasses.\", JOURNAL = c(\"Science 277:696-9 (1997)\", \"DOI:10.1126/science.277.5326.696\")), list(REFERENCE = \"PMID:12620350\", AUTHORS = \"Frey M, Huber K, Park WJ, Sicker D, Lindberg P, Meeley RB, Simmons CR, Yalpani N, Gierl A.\", TITLE = \"A 2-oxoglutarate-dependent dioxygenase is integrated in DIMBOA-biosynthesis.\", JOURNAL = c(\"Phytochemistry 62:371-6 (2003)\", \"DOI:10.1016/S0031-9422(02)00556-3\")), list(REFERENCE = \"PMID:11851909\", AUTHORS = \"von Rad U, Huttl R, Lottspeich F, Gierl A, Frey M.\", TITLE = \"Two glucosyltransferases are involved in detoxification of benzoxazinoids in maize.\", JOURNAL = c(\"Plant J 28:633-42 (2001)\", \"DOI:10.1046/j.1365-313x.2001.01161.x\")), list(REFERENCE = \"PMID:16666853\", AUTHORS = \"Bailey BA, Larson RL\", TITLE = \"Hydroxamic Acid glucosyltransferases from maize seedlings.\", JOURNAL = c(\"Plant Physiol 90:1071-6 (1989)\", \"DOI:10.1104/pp.90.3.1071\")), list(REFERENCE = \"PMID:18192444\", AUTHORS = \"Jonczyk R, Schmidt H, Osterrieder A, Fiesselmann A, Schullehner K, Haslbeck M, Sicker D, Hofmann D, Yalpani N, Simmons C, Frey M, Gierl A\", TITLE = \"Elucidation of the final reactions of DIMBOA-glucoside biosynthesis in maize: characterization of Bx6 and Bx7.\", JOURNAL = c(\"Plant Physiol 146:1053-63 (2008)\", \"DOI:10.1104/pp.107.111237\")), list(REFERENCE = \"PMID:19577780\", AUTHORS = \"Frey M, Schullehner K, Dick R, Fiesselmann A, Gierl A\", TITLE = \"Benzoxazinoid biosynthesis, a model for evolution of secondary metabolic pathways in plants.\", JOURNAL = c(\"Phytochemistry 70:1645-51 (2009)\", \"DOI:10.1016/j.phytochem.2009.05.012\"))', 'map00402', 'Benzoxazinoid biosynthesis', 'Benzoxazinoids (hydroxamic acids) are plant secondary metabolites that serve as important factors for host resistance against microbial pathogens and insects and for allelopathic effects. They are found in grass family and some eudicot families. The predominant benzoxazinoids are DIBOA and its 7-methoxy derivative DIMBOA, which are stored as glucosides in vacuoles. In maize, benzoxazinoid biosynthesis branches off from tryptophan biosynthesis at indole-3-glycerol phosphate, which is converted to indole by indole-3-glycerol phosphate lyase, BX1. Subsequently four cytochrome P450 monooxygenases (BX2-BX5) catalyze the introduction of four oxygen atoms into the indole moiety, yielding DIBOA. After glucosylation by UDP-glucosyltransferase (BX8/BX9), the glucoside is further modified by hydroxylation and O-methylation at C-7 to form DIMBOA-glucoside.', 4, 0, 0, 4, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (54, '00403', 'Indole diterpene alkaloid biosynthesis', 'Indole diterpene alkaloids are a group of meroterpenoids consisting of a partial diterpene structure with four isoprene units and an indole moiety. The common cyclic diterpene skeleton is derived from geranylgeranyl diphosphate (GGPP) and an indole moiety is derived from tryptophan or a tryptophan precursor. They are combined in the initial step of biosynthetic reactions that generate paspaline, the first stable and less modified form required for other forms. Most indole diterpene alkaloids have potent mammalian tremorgenic activities, such as ryegrass staggers in livestock, and some have anti-insect properties. This map shows examples of indole diterpene alkaloids isolated from filamentous fungi.', 'Metabolism; Biosynthesis of other secondary metabolites', 'Indole diterpene alkaloid biosynthesis', 'Paspaline biosynthesis, geranylgeranyl-PP + indoleglycerol phosphate => paspaline [PATH:map00403]', 'ko00403', 'list(REFERENCE = \"PMID:18262778\", AUTHORS = \"Saikia S, Nicholson MJ, Young C, Parker EJ, Scott B\", TITLE = \"The genetic basis for indole-diterpene chemical diversity in filamentous fungi.\", JOURNAL = c(\"Mycol Res 112:184-99 (2008)\", \"DOI:10.1016/j.mycres.2007.06.015\")), list(REFERENCE = \"PMID:16494875\", AUTHORS = \"Saikia S, Parker EJ, Koulman A, Scott B\", TITLE = \"Four gene products are required for the fungal synthesis of the indole-diterpene, paspaline.\", JOURNAL = c(\"FEBS Lett 580:1625-30 (2006)\", \"DOI:10.1016/j.febslet.2006.02.008\")), list(REFERENCE = \"PMID:17428785\", AUTHORS = \"Saikia S, Parker EJ, Koulman A, Scott B\", TITLE = \"Defining paxilline biosynthesi