INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (59, '00440', 'Phosphonate and phosphinate metabolism', 'Natural products containing carbon-phosphorous bonds, so-called C-P compounds, are derivatives of phosphonate and phosphinate with substitution of alkyl group for hydrogen of phosphorus-hydrogen bonds. C-P compounds have been found in many organisms, but only protists and bacteria, mostly Actinobacteria, have biosynthetic capacity. A common reaction in the biosynthetic pathway is C-P bond forming reaction from phosphoenolpyruvate (PEP) to phosphonopyruvate (PnPy) catalyzed by PEP phosphomutase. 2-Aminoethylphosphonate (AEP) is the most abundant C-P compound in the natural world. AEP derivatives include phosphonoprotein, phosphonoglycan, and phosphonolipid. Other known C-P compounds are bioactive substances used in medicine (antibiotics) and agriculture (herbicide) such as fosfomycin, FR-33289, rhizocticin, and bialaphos.', 'Metabolism; Metabolism of other amino acids', 'Phosphonate and phosphinate metabolism', 'Spondylometaphyseal dysplasia with cone-rod dystrophy', 'GO: 0019634', 'ko00440', 'list(REFERENCE = \"PMID:19489722\", AUTHORS = \"Metcalf WW, van der Donk WA\", TITLE = \"Biosynthesis of phosphonic and phosphinic acid natural products.\", JOURNAL = c(\"Annu Rev Biochem 78:65-94 (2009)\", \"DOI:10.1146/annurev.biochem.78.091707.100215\")), list(REFERENCE = \"PMID:19053721\", AUTHORS = \"Xiao Y, Lee K, Liu P\", TITLE = \"Syntheses of the P-methylase substrates of the bialaphos biosynthetic pathway.\", JOURNAL = c(\"Org Lett 10:5521-4 (2008)\", \"DOI:10.1021/ol802269x\")), list(REFERENCE = \"PMID:17168571\", AUTHORS = \"van der Donk WA\", TITLE = \"Rings, radicals, and regeneration: the e