INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (69, '00512', 'Mucin type O-glycan biosynthesis', 'O-glycans are a class of glycans that modify serine or threonine residues of proteins. Biosynthesis of O-glycans starts from the transfer of N-acetylgalactosamine (GalNAc) to serine or threonine. The first GalNAc may be extended with sugars including galactose, N-acetylglucosamine, fucose, or sialic acid, but not mannose, glucose, or xylose. Depending on the sugars added, there are four common O-glycan core structures, cores 1 through 4, and an additional four, cores 5 though 8. Mucins are highly O-glycosylated glycoproteins ubiquitous in mucous secretions on cell surfaces and in body fluids. Mucin O-glycans can be branched, and many sugars or groups of sugars are antigenic. Important modifications of mucin O-glycans include O-acetylation of sialic acid and O-sulfation of galactose and N-acetylglucosamine.', 'Metabolism; Glycan biosynthesis and metabolism', 'Mucin type O-glycan biosynthesis', 'O-glycan biosynthesis, mucin type core [PATH:map00512]', 'Colorectal cancer, Tn syndrome, Familial tumoral calcinosis', 'GO: 0006493', 'ko00512', 'list(REFERENCE = \"PMID:10580130\", AUTHORS = \"Brockhausen I.\", TITLE = \"Pathways of O-glycan biosynthesis in cancer cells.\", JOURNAL = c(\"Biochim Biophys Acta 1473:67-95 (1999)\", \"DOI:10.1016/S0304-4165(99)00170-1\")), list(REFERENCE = \"PMID:11821425\", AUTHORS = \"Iwai T, Inaba N, Naundorf A, Zhang Y, Gotoh M, Iwasaki H, Kudo T, Togayachi A, Ishizuka Y, Nakanishi H, Narimatsu H.\", TITLE = \"Molecular cloning and characterization of a novel UDP-GlcNAc:GalNAc-peptide beta1,3-N-acetylglucosaminyltransferase (beta 3Gn-T6), an enzyme synthesizing the core 3 structure of O-glycans.\", JOURNAL = c(\"J Biol Chem 277:12802-9 (2002)\", \"DOI:10.1074/jbc.M112457200\")), list(REFERENCE = \"PMID:12626393\", AUTHORS = \"Korekane H, Taguchi T, Sakamoto Y, Honke K, Dohmae N, Salminen H, Toivonen S, Helin J, Takio K, Renkonen O, Taniguchi N.\", TITLE = \"Purification and cDNA cloning of UDP-GlcNAc:GlcNAcbeta1-3Galbeta1-4Glc(NAc)-R [GlcNAc to Gal]beta1,6N-acetylglucosaminyltransferase from rat small intestine: a major carrier of dIGnT activity in rat small intestine.\", JOURNAL = c(\"Glycobiology 13:387-400 (2003)\", \"DOI:10.1093/glycob/cwg044\")), list(REFERENCE = \"PMID:10350056\", AUTHORS = \"van Die I, van Tetering A, Schiphorst WE, Sato T, Furukawa K, van den Eijnden DH.\", TITLE = \"The acceptor substrate specificity of human beta4-galactosyltransferase V indicates its potential function in O-glycosylation.\", JOURNAL = c(\"FEBS Lett 450:52-6 (1999)\", \"DOI:10.1016/S0014-5793(99)00462-7\"))', 248, 0, 248, 0, 0, 0, 0, 0);
