INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (71, '00514', 'Other types of O-glycan biosynthesis', 'O-linked glycosylation is the attachment of monosaccharides to the hydroxyl groups of amino acids, mostly serine and threonine, and is found in eukaryotes, archaea and bacteria. O-glycans exhibit diverse types of modifications where the innermost monosaccharide is N-acetylgalactosamine (map00512), mannose in mammals (map00515), and others (this map) including N-acetylglucosamine, fucose, glucose, galactose, mannose in yeast and arabinose in plants.', 'Metabolism; Glycan biosynthesis and metabolism', 'Other types of O-glycan biosynthesis', 'Limb-girdle muscular dystrophy, Dowling-Degos disease, Lysyl hydroxylase 3 deficiency', 'ko00514', 'list(REFERENCE = \"PMID:22669261\", AUTHORS = \"Kizuka Y, Oka S\", TITLE = \"Regulated expression and neural functions of human natural killer-1 (HNK-1) carbohydrate.\", JOURNAL = c(\"Cell Mol Life Sci 69:4135-47 (2012)\", \"DOI:10.1007/s00018-012-1036-z\")), list(REFERENCE = \"PMID:19429925\", AUTHORS = \"Lommel M, Strahl S\", TITLE = \"Protein O-mannosylation: conserved from bacteria to humans.\", JOURNAL = c(\"Glycobiology 19:816-28 (2009)\", \"DOI:10.1093/glycob/cwp066\"))', 'map00514', 'Other types of O-glycan biosynthesis', 492, 778, 248, 105, 127, 12, 774, 4);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (72, '00515', 'Mannose type O-glycan biosynthesis', 'Biosynthesis of mammalian O-mannosyl glycans is initiated by the transfer of mannose from mannose-P-Dol to serine or threonine residue, followed by extensions with N-acetylglucosamine (GlcNAc) and galactose (Gal) to generate core M1, M2 and M3 glycans. Core M1 and M2 glycans can then be further attached by fucose residues, sialic acid terminals and sulfatded glucuroinc acid terminals. Core M3 glycan is involved in the synthesis of alpha-dystroglycan, a heavily glycosylated protein found in muscle and brain tissues. Core M3 glycan contains a tandem repeat of ribitol 5-phosphate (Rbo5P) and -alpha3-GlcA-beta3-Xyl- repeating structures. Defects of genes encoding core glycans and modified core M3 glycans are associated with various congenital diseases, such as muscular dystrophies caused by reduced O-mannosylation of alpha-dystroglycan in skeletal muscles [DS:H00120].', 'Metabolism; Glycan biosynthesis and metabolism', 'Mannose type O-glycan biosynthesis', 'O-glycan biosynthesis, mannose type (core M3) [PATH:map00515]', 'Muscular dystrophy-dystroglycanopathy type A, Limb-girdle muscular dystrophy, Fukuyama congenital muscular dystrophy, Muscular dystrophy-dystroglycanopathy type C, Muscular dystrophy-dystroglycanopathy type B, Congenital muscular dystrophy type 1C, Congenital muscular dystrophy type 1D, Muscular dystrophy-dystroglycanopathy', 'GO: 0006493', 'ko00515', 'list(REFERENCE = \"PMID:24786756\", AUTHORS = \"Praissman JL, Wells L\", TITLE = \"Mammalian O-mannosylation pathway: glycan structures, enzymes, and protein substrates.\", JOURNAL = c(\"Biochemistry 53:3066-78 (2014)\", \"DOI:10.1021/bi500153y\")), list(REFERENCE = \"PMID:21945038\", AUTHORS = \"Stalnaker SH, Stuart R, Wells L\", TITLE = \"Mammalian O-mannosylation: unsolved questions of structure/function.\", JOURNAL = c(\"Curr Opin Struct Biol 21:603-9 (2011)\", \"DOI:10.1016/j.sbi.2011.09.001\")), list(REFERENCE = \"PMID:26923585\", AUTHORS = \"Kanagawa M, Kobayashi K, Tajiri M, Manya H, Kuga A, Yamaguchi Y, Akasaka-Manya K, Furukawa J, Mizuno M, Kawakami H, Shinohara Y, Wada Y, Endo T, Toda T\", TITLE = \"Identification of a Post-translational Modification with Ribitol-Phosphate and Its Defect in Muscular Dystrophy.\", JOURNAL = c(\"Cell Rep 14:2209-23 (2016)\", \"DOI:10.1016/j.celrep.2016.02.017\")), list(REFERENCE = \"PMID:27733679\", AUTHORS = \"Manya H, Yamaguchi Y, Kanagawa M, Kobayashi K, Tajiri M, Akasaka-Manya K, Kawakami H, Mizuno M, Wada Y, Toda T, Endo T\", TITLE = \"The Muscular Dystrophy Gene TMEM5 Encodes a Ribitol beta1,4-Xylosyltransferase Required for the Functional Glycosylation of Dystroglycan.\", JOURNAL = c(\"J Biol Chem 291:24618-24627 (2016)\", \"DOI:10.1074/jbc.M116.751917\"))', 443, 783, 248, 56, 127, 12, 779, 4);
