INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (74, '00521', 'Streptomycin biosynthesis', 'Streptomycin is an aminocyclitol-aminoglycoside antibiotic produced by Streptomyces griseus. Streptomycin consists of aminocyclitol (streptidine), 6-deoxyhexose (streptose), and N-methyl-L-glucosamine moieties, which are formed by independent biosynthetic pathways. All of the moieties are derived from D-glucose. The streptidine moiety is synthesized via myo-inositol, which is then oxidized at C-1 and transaminated to give scyllo-inosamine. After phosphorylation, the compound is transamidinated by arginine. The same procedure is repeated at the C-3 position. The streptose moiety is made from D-glucose via a dTDP-glucose pathway. The exact biosynthetic route for the N-methyl-L-glucosamine moiety is unknown, though the biosynthetic gene cluster have been proposed.', 'Metabolism; Biosynthesis of other secondary metabolites', 'Streptomycin biosynthesis', 'dTDP-L-rhamnose biosynthesis [PATH:map00521]', 'D08531, Streptomycin (INN)', 'GO: 0019872', 'ko00521', 'list(REFERENCE = \"PMID:19362651\", AUTHORS = \"Wehmeier UF, Piepersberg W\", TITLE = \"Enzymology of aminoglycoside biosynthesis-deduction from gene clusters.\", JOURNAL = c(\"Methods Enzymol 459:459-91 (2009)\", \"DOI:10.1016/S0076-6879(09)04619-9\")), list(REFERENCE = \"PMID:17390001\", AUTHORS = \"Flatt PM, Mahmud T\", TITLE = \"Biosynthesis of aminocyclitol-aminoglycoside antibiotics and related compounds.\", JOURNAL = c(\"Nat Prod Rep 24:358-92 (2007)\", \"DOI:10.1039/b603816f\")), list(REFERENCE = \"PMID:1698764\", AUTHORS = \"Walker JB\", TITLE = \"Possible evolutionary relationships between streptomycin and bluensomycin biosynthetic pathways: detect