INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (90, '00571', 'Lipoarabinomannan (LAM) biosynthesis', 'Lipoarabinomannan (LAM), as well as structurally related lipomannan (LM) and phosphatidylinositol mannosides (PIMs), are major glycolipids found on the mycobacterial cell wall. LAM is synthesized from phosphatidylinositol (PI) and in a biochemical pathway of PI => PIMs => LM =>LAM. Structually, LAM and LM are an extension of PIMs containing an alpha1,6-linked mannan core with alpha1,2-monomannose side chains. The mannan core of LAM is in turn arabinosylated by a linear alpha1,5-linked Araf backbone, punctutated by alpha1,3-linked branching. LAM can be substituted by several capping motifs, which determine the ability of LAM to modulate the immune response through the interaction with different receptors containing C-type lectins (map04625). It has been shown that the immunomodulatory properties of LAM and related glycolipids contribute to the survival of Mycobacteriaum tuberculosis, the causative agent of tuberculosis (map05152).', 'Metabolism; Glycan biosynthesis and metabolism', 'Lipoarabinomannan (LAM) biosynthesis', 'ko00571', 'list(REFERENCE = \"PMID:26104198\", AUTHORS = \"Jankute M, Grover S, Birch HL, Besra GS\", TITLE = \"Genetics of Mycobacterial Arabinogalactan and Lipoarabinomannan Assembly.\", JOURNAL = c(\"Microbiol Spectr 2:MGM2-0013-2013 (2014)\", \"DOI:10.1128/microbiolspec.MGM2-0013-2013\")), list(REFERENCE = \"PMID:14627436\", AUTHORS = \"Morita YS, Patterson JH, Billman-Jacobe H, McConville MJ\", TITLE = \"Biosynthesis of mycobacterial phosphatidylinositol mannosides.\", JOURNAL = c(\"Biochem J 378:589-97 (2004)\", \"DOI:10.1042/BJ20031372\")), list(REFERENCE = \"PMID:26488279\", AUTHORS = \"Jankute M, Cox JA, Harrison J, Besra GS\", TITLE = \"Assembly of the Mycobacterial Cell Wall.\", JOURNAL = c(\"Annu Rev Microbiol 69:405-23 (2015)\", \"DOI:10.1146/annurev-micro-091014-104121\")), list(REFERENCE = \"PMID:20801880\", AUTHORS = \"Guerin ME, Kordulakova J, Alzari PM, Brennan PJ, Jackson M\", TITLE = \"Molecular basis of phosphatidyl-myo-inositol mannoside biosynthesis and regulation in mycobacteria.\", JOURNAL = c(\"J Biol Chem 285:33577-83 (2010)\", \"DOI:10.1074/jbc.R110.168328\")), list(REFERENCE = \"PMID:15228522\", AUTHORS = \"Briken V, Porcelli SA, Besra GS, Kremer L\", TITLE = \"Mycobacterial lipoarabinomannan and related lipoglycans: from biogenesis to modulation of the immune response.\", JOURNAL = c(\"Mol Microbiol 53:391-403 (2004)\", \"DOI:10.1111/j.1365-2958.2004.04183.x\")), list(REFERENCE = \"PMID:17956343\", AUTHORS = \"Alderwick LJ, Birch HL, Mishra AK, Eggeling L, Besra GS\", TITLE = \"Structure, function and biosynthesis of the Mycobacterium tuberculosis cell wall: arabinogalactan and lipoarabinomannan assembly with a view to discovering new drug targets.\", JOURNAL = c(\"Biochem Soc Trans 35:1325-8 (2007)\", \"DOI:10.1042/BST0351325\")), list(REFERENCE = \"PMID:21521247\", AUTHORS = \"Mishra AK, Driessen NN, Appelmelk BJ, Besra GS\", TITLE = \"Lipoarabinomannan and related glycoconjugates: structure, biogenesis and role in Mycobacterium tuberculosis physiology and host-pathogen interaction.\", JOURNAL = c(\"FEMS Microbiol Rev 35:1126-57 (2011)\", \"DOI:10.1111/j.1574-6976.2011.00276.x\")), list(REFERENCE = \"PMID:16803893\", AUTHORS = \"Morita YS, Sena CB, Waller RF, Kurokawa K, Sernee MF, Nakatani F, Haites RE, Billman-Jacobe H, McConville MJ, Maeda Y, Kinoshita T\", TITLE = \"PimE is a polyprenol-phosphate-mannose-dependent mannosyltransferase that transfers the fifth mannose of phosphatidylinositol mannoside in mycobacteria.\", JOURNAL = c(\"J Biol Chem 281:25143-55 (2006)\", \"DOI:10.1074/jbc.M604214200\")), list(REFERENCE = \"PMID:20843801\", AUTHORS = \"Batt SM, Jabeen T, Mishra AK, Veerapen N, Krumbach K, Eggeling L, Besra GS, Futterer K\", TITLE = \"Acceptor substrate discrimination in phosphatidyl-myo-inositol mannoside synthesis: structural and mutational analysis of mannosyltransferase Corynebacterium glutamicum PimB\'.\", JOURNAL = c(\"J Biol Chem 285:37741-52 (2010)\", \"DOI:10.1074/jbc.M110.165407\")), list(REFERENCE = \"PMID:19049470\", AUTHORS = \"Boonyarattanakalin S, Liu X, Michieletti M, Lepenies B, Seeberger PH\", TITLE = \"Chemical synthesis of all phosphatidylinositol mannoside (PIM) glycans from Mycobacterium tuberculosis.\", JOURNAL = c(\"J Am Chem Soc 130:16791-9 (2008)\", \"DOI:10.1021/ja806283e\")), list(REFERENCE = \"PMID:12063260\", AUTHORS = \"Guerardel Y, Maes E, Elass E, Leroy Y, Timmerman P, Besra GS, Locht C, Strecker G, Kremer L\", TITLE = \"Structural study of lipomannan and lipoarabinomannan from Mycobacterium chelonae. Presence of unusual components with alpha 1,3-mannopyranose side chains.\", JOURNAL = c(\"J Biol Chem 277:30635-48 (2002)\", \"DOI:10.1074/jbc.M204398200\"))', 'map00571', 'Lipoarabinomannan (LAM) biosynthesis', 'Lipoarabinomannan (LAM), as well as structurally related lipomannan (LM) and phosphatidylinositol mannosides (PIMs), are major glycolipids found on the mycobacterial cell wall. LAM is synthesized from phosphatidylinositol (PI) and in a biochemical pathway of PI => PIMs => LM =>LAM. Structually, LAM and LM are an extension of PIMs containing an alpha1,6-linked mannan core with alpha1,2-monomannose side chains. The mannan core of LAM is in turn arabinosylated by a linear alpha1,5-linked Araf backbone, punctutated by alpha1,3-linked branching. LAM can be substituted by several capping motifs, which determine the ability of LAM to modulate the immune response through the interaction with different receptors containing C-type lectins (map04625). It has been shown that the immunomodulatory properties of LAM and related glycolipids contribute to the survival of Mycobacteriaum tuberculosis, the causative agent of tuberculosis (map05152).', 0, 70, 0, 0, 0, 0, 70, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (91, '00572', 'Arabinogalactan biosynthesis - Mycobacterium', 'Just like lipoarabinomannan (map00571), arabinogalactan (AG) is a major heteropolysaccharide found on the mycobacterium cell wall. AG consists of a linear galactan chain of galactofuranosyl (Galf) residues covalently connected to peptidoglycan (PG), with branches of three similar arabinan chains comprising arabinofuranosyl (Araf) residues. The non-reducing ends of arabinans are often covalently attached to the long fatty acids of mycolic acids. Thus, the covalently linked network of PG and mycolylated AG, termed the mycolyl-arabinogalactan-peptidoglycan (mAGP) complex, forms an exceptionally robust cell wall and provides a permeability barrier to antibiotics.', 'Metabolism; Glycan biosynthesis and metabolism', 'Arabinogalactan biosynthesis - Mycobacterium', 'ko00572', 'list(REFERENCE = \"PMID:26104198\", AUTHORS = \"Jankute M, Grover S, Birch HL, Besra GS\", TITLE = \"Genetics of Mycobacterial Arabinogalactan and Lipoarabinomannan Assembly.\", JOURNAL = c(\"Microbiol Spectr 2:MGM2-0013-2013 (2014)\", \"DOI:10.1128/microbiolspec.MGM2-0013-2013\")), list(REFERENCE = \"PMID:26488279\", AUTHORS = \"Jankute M, Cox JA, Harrison J, Besra GS\", TITLE = \"Assembly of the Mycobacterial Cell Wall.\", JOURNAL = c(\"Annu Rev Microbiol 69:405-23 (2015)\", \"DOI:10.1146/annurev-micro-091014-104121\")), list(REFERENCE = \"PMID:24915502\", AUTHORS = \"Angala SK, Belardinelli JM, Huc-Claustre E, Wheat WH, Jackson M\", TITLE = \"The cell envelope glycoconjugates of Mycobacterium tuberculosis.\", JOURNAL = c(\"Crit Rev Biochem Mol Biol 49:361-99 (2014)\", \"DOI:10.3109/10409238.2014.925420\")), list(REFEREN