INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (136, '00908', 'Zeatin biosynthesis', 'Zeatin is a member of the cytokinin family, a class of phytohormones involved in various processes of growth and development in plants. Most abundant cytokinins are adenine-type, where the N6 position of adenine is substituted with an isoprenoid, such as in zeatin, or an aromatic side chain, such as in kinetin. Zeatin can be synthesized in two different pathways: the tRNA pathway and the AMP pathway. In the tRNA pathway zeatin is a recycled product of isopentenylated tRNAs. In the AMP pathway zeatin is synthesized from an isopentenyl donor, dimethylallyl diphosphate (DMAPP), and AMP, ADP, or ATP by isopentenyltransferases. After synthesis cytokinins can be glucosylated.', 'Metabolism; Metabolism of terpenoids and polyketides', 'Zeatin biosynthesis', 'GO: 0009691', 'ko00908', 'list(REFERENCE = \"PMID:14726522\", AUTHORS = \"Kasahara H, Takei K, Ueda N, Hishiyama S, Yamaya T, Kamiya Y, Yamaguchi S, Sakakibara H.\", TITLE = \"Distinct isoprenoid origins of cis- and trans-zeatin biosyntheses in Arabidopsis.\", JOURNAL = c(\"J Biol Chem 279:14049-54 (2004)\", \"DOI:10.1074/jbc.M314195200\")), list(REFERENCE = \"PMID:11313355\", AUTHORS = \"Takei K, Sakakibara H, Sugiyama T.\", TITLE = \"Identification of genes encoding adenylate isopentenyltransferase, a cytokinin biosynthesis enzyme, in Arabidopsis thaliana.\", JOURNAL = c(\"J Biol Chem 276:26405-10 (2001)\", \"DOI:10.1074/jbc.M102130200\")), list(REFERENCE = \"PMID:11479373\", AUTHORS = \"Kakimoto T.\", TITLE = \"Identification of plant cytokinin biosynthetic enzymes as dimethylallyl diphosphate:ATP/ADP isopentenyltransferases.\", JOURNAL = c(\"Plant Cell Physiol 