INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (137, '00909', 'Sesquiterpenoid and triterpenoid biosynthesis', 'Sesquiterpenoids (C15 terpenoids) are a group of terpenoids consisting of three isoprene units. They are derive from farnesyl diphosphate (FPP) and can be cyclized to produce various skeletal structures. Sesquiterpenoid biosynthesis begins with the loss of diphosphate from FPP under the action of sesquiterpene synthesis enzymes, generating an allylic cation that is highly susceptible to intramolecular attacks. Cyclization of the farnesyl cation may take place onto either of the remaining double bonds with the result that 6-, 10-, or 11-membered rings may be formed. Many sesquiterpenoids have been isolated from plants, fungi, marine organisms, and Streptomyces species. This map shows a few examples of acyclic and cyclic sesquiterpenoids.', 'Metabolism; Metabolism of terpenoids and polyketides', 'Sesquiterpenoid and triterpenoid biosynthesis', 'GO: 0016106 0016104', 'ko00909', 'list(REFERENCE = \"PMID:9371761\", AUTHORS = \"Crock J, Wildung M, Croteau R\", TITLE = \"Isolation and bacterial expression of a sesquiterpene synthase cDNA clone from peppermint (Mentha x piperita, L.) that produces the aphid alarm pheromone (E)-beta-farnesene.\", JOURNAL = c(\"Proc Natl Acad Sci U S A 94:12833-8 (1997)\", \"DOI:10.1073/pnas.94.24.12833\")), list(REFERENCE = \"PMID:18363779\", AUTHORS = \"Nagegowda DA, Gutensohn M, Wilkerson CG, Dudareva N\", TITLE = \"Two nearly identical terpene synthases catalyze the formation of nerolidol and linalool in snapdragon flowers.\", JOURNAL = c(\"Plant J 55:224-39 (2008)\", \"DOI:10.1111/j.1365-313X.2008.03496.x\")), list(REFERENCE = \"PMID:16579958\", AUTHORS = \