INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (141, '00940', 'Phenylpropanoid biosynthesis', 'Phenylpropanoids are a group of plant secondary metabolites derived from phenylalanine and having a wide variety of functions both as structural and signaling molecules. Phenylalanine is first converted to cinnamic acid by deamination. It is followed by hydroxylation and frequent methylation to generate coumaric acid and other acids with a phenylpropane (C6-C3) unit. Reduction of the CoA-activated carboxyl groups of these acids results in the corresponding aldehydes and alcohols. The alcohols are called monolignols, the starting compounds for biosynthesis of lignin.', 'Metabolism; Biosynthesis of other secondary metabolites', 'Phenylpropanoid biosynthesis', 'Monolignol biosynthesis, phenylalanine/tyrosine => monolignol [PATH:map00940], Flavanone biosynthesis, phenylalanine => naringenin [PATH:map00940]', 'GO: 0009699', 'ko00940', 'list(REFERENCE = \"PMID:14729911\", AUTHORS = \"Nair RB, Bastress KL, Ruegger MO, Denault JW, Chapple C.\", TITLE = \"The Arabidopsis thaliana REDUCED EPIDERMAL FLUORESCENCE1 gene encodes an aldehyde dehydrogenase involved in ferulic acid and sinapic acid biosynthesis.\", JOURNAL = c(\"Plant Cell 16:544-54 (2004)\", \"DOI:10.1105/tpc.017509\")), list(REFERENCE = \"PMID:14551235\", AUTHORS = \"Baucher M, Halpin C, Petit-Conil M, Boerjan W.\", TITLE = \"Lignin: genetic engineering and impact on pulping.\", JOURNAL = c(\"Crit Rev Biochem Mol Biol 38:305-50 (2003)\", \"DOI:10.1080/10409230391036757\")), list(REFERENCE = \"PMID:15878986\", AUTHORS = \"Rogers LA, Dubos C, Cullis IF, Surman C, Poole M, Willment J, Mansfield SD, Campbell MM.\", TITLE = \"Light, the circadian clock, a