INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (142, '00941', 'Flavonoid biosynthesis', 'Flavonoids are a major class of plant secondary metabolites that serves a multitude of functions including pigments and antioxidant activity. Flavonoids are synthesized from phenylpropanoid derivatives by condensation with malonyl-CoA. For example, condensation of p-coumaroyl-CoA (C6-C3) with three malonyl-CoA (C3) molecules results in naringenin chalcone with a diphenylpropane (C6-C3-C6) unit, which is converted to naringenin with the flavone (2-phenylchromen-4-one) backbone by conjugate ring closure. These and further modifications yield a variety of structural forms including chalcones, flavanones, dihyroflavonols, and flavans, anthocyanins, flavones and flavonols, and isoflavonoids.', 'Metabolism; Biosynthesis of other secondary metabolites', 'Flavonoid biosynthesis', 'Flavanone biosynthesis, phenylalanine => naringenin [PATH:map00941], Flavonoid biosynthesis, naringenin => pelargonidin [PATH:map00941]', 'GO: 0009813', 'ko00941', 'list(REFERENCE = \"PMID:16945088\", AUTHORS = \"Tsai CJ, Harding SA, Tschaplinski TJ, Lindroth RL, Yuan Y.\", TITLE = \"Genome-wide analysis of the structural genes regulating defense phenylpropanoid metabolism in Populus.\", JOURNAL = c(\"New Phytol 172:47-62 (2006)\", \"DOI:10.1111/j.1469-8137.2006.01798.x\")), list(REFERENCE = \"PMID:16367960\", AUTHORS = \"Ono E, Hatayama M, Isono Y, Sato T, Watanabe R, Yonekura-Sakakibara K, Fukuchi-Mizutani M, Tanaka Y, Kusumi T, Nishino T, Nakayama T.\", TITLE = \"Localization of a flavonoid biosynthetic polyphenol oxidase in vacuoles.\", JOURNAL = c(\"Plant J 45:133-43 (2006)\", \"DOI:10.1111/j.1365-313X.2005.02625.x\")), list(REFERENCE