INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (146, '00945', 'Stilbenoid, diarylheptanoid and gingerol biosynthesis', 'Stilbenoids are a group of phenolic compounds, biosynthetically interrelated through their common origin from a C6-C2-C6 intermediate, such as resveratol found in grapes. Stilbenoids can also exist as glycosides (e.g., piceid). Combretastatins are potentially useful stilbenoid natural products with known antitumor activity. Diarylheptanoid is a compound group having phenyl rings at 1,7 positions of n-heptane (C6-C7-C6), such as curcumin found in the ginger family. [6]-Gingerol is a major active component of ginger and has diverse pharmacologic effects.', 'Metabolism; Biosynthesis of other secondary metabolites', 'Stilbenoid, diarylheptanoid and gingerol biosynthesis', 'ko00945', 'list(REFERENCE = \"PMID:19258320\", AUTHORS = \"Katsuyama Y, Kita T, Funa N, Horinouchi S\", TITLE = \"Curcuminoid biosynthesis by two type III polyketide synthases in the herb Curcuma longa.\", JOURNAL = c(\"J Biol Chem 284:11160-70 (2009)\", \"DOI:10.1074/jbc.M900070200\")), list(REFERENCE = \"PMID:16890967\", AUTHORS = \"Ramirez-Ahumada Mdel C, Timmermann BN, Gang DR\", TITLE = \"Biosynthesis of curcuminoids and gingerols in turmeric (Curcuma longa) and ginger (Zingiber officinale): identification of curcuminoid synthase and hydroxycinnamoyl-CoA thioesterases.\", JOURNAL = c(\"Phytochemistry 67:2017-29 (2006)\", \"DOI:10.1016/j.phytochem.2006.06.028\")), list(REFERENCE = \"PMID:17509574\", AUTHORS = \"Masada S, Terasaka K, Mizukami H\", TITLE = \"A single amino acid in the PSPG-box plays an important role in the catalytic function of CaUGT2 (Curcumin glucosyltransferase), a Group D Family 1 glucosyltransferase from Catharanthus roseus.\", JOURNAL = c(\"FEBS Lett 581:2605-10 (2007)\", \"DOI:10.1016/j.febslet.2007.05.002\"))', 'map00945', 'Stilbenoid, diarylheptanoid and gingerol biosynthesis', 'Stilbenoids are a group of phenolic compounds, biosynthetically interrelated through their common origin from a C6-C2-C6 intermediate, such as resveratol found in grapes. Stilbenoids can also exist as glycosides (e.g., piceid). Combretastatins are potentially useful stilbenoid natural products with known antitumor activity. Diarylheptanoid is a compound group having phenyl rings at 1,7 positions of n-heptane (C6-C7-C6), such as curcumin found in the ginger family. [6]-Gingerol is a major active component of ginger and has diverse pharmacologic effects.', 91, 0, 0, 91, 0, 0, 0, 0);
