INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (149, '00965', 'Betalain biosynthesis', 'Betalains are water-soluble nitrogen-containing pigments that are present in plants belonging to the order Caryophyllales (such as cactus and amaranth families) and in higher fungi. They contain betalamic acid as the chromophore and are classified into two types: betacyanins and betaxanthins. Betacyanins contain a cyclo-DOPA residue and exhibit red/violet coloration, while betaxanthins contain different amino acids or amino side chains and exhibit a yellow/orange coloration. The condensation of betalamic acid with amino acids (including cyclo-DOPA or amines) in plants is a spontaneous reaction, not an enzyme-catalyzed reaction.', 'Metabolism; Biosynthesis of other secondary metabolites', 'Betalain biosynthesis', 'ko00965', 'list(REFERENCE = \"PMID:15805475\", AUTHORS = \"Gandia-Herrero F, Escribano J, Garcia-Carmona F\", TITLE = \"Betaxanthins as substrates for tyrosinase. An approach to the role of tyrosinase in the biosynthetic pathway of betalains.\", JOURNAL = c(\"Plant Physiol 138:421-32 (2005)\", \"DOI:10.1104/pp.104.057992\")), list(REFERENCE = \"PMID:16247652\", AUTHORS = \"Sasaki N, Abe Y, Wada K, Koda T, Goda Y, Adachi T, Ozeki Y\", TITLE = \"Amaranthin in feather cockscombs is synthesized via glucuronylation at the cyclo-DOPA glucoside step in the betacyanin biosynthetic pathway.\", JOURNAL = c(\"J Plant Res 118:439-42 (2005)\", \"DOI:10.1007/s10265-005-0237-z\")), list(REFERENCE = \"PMID:12620337\", AUTHORS = \"Strack D, Vogt T, Schliemann W\", TITLE = \"Recent advances in betalain research.\", JOURNAL = c(\"Phytochemistry 62:247-69 (2003)\", \"DOI:10.1016/S0031-9422(02)00564-2\")), list(REFERENCE = \"PMID:11855654\", AUTHORS = \"Vogt T\", TITLE = \"Substrate specificity and sequence analysis define a polyphyletic origin of betanidin 5- and 6-O-glucosyltransferase from Dorotheanthus bellidiformis.\", JOURNAL = c(\"Planta 214:492-5 (2002)\", \"DOI:10.1007/s00425-001-0685-1\"))', 'map00965', 'Betalain biosynthesis', 'Betalains are water-soluble nitrogen-containing pigments that are present in plants belonging to the order Caryophyllales (such as cactus and amaranth families) and in higher fungi. They contain betalamic acid as the chromophore and are classified into two types: betacyanins and betaxanthins. Betacyanins contain a cyclo-DOPA residue and exhibit red/violet coloration, while betaxanthins contain different amino acids or amino side chains and exhibit a yellow/orange coloration. The condensation of betalamic acid with amino acids (including cyclo-DOPA or amines) in plants is a spontaneous reaction, not an enzyme-catalyzed reaction.', 92, 0, 0, 92, 0, 0, 0, 0);
