INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (192, '01523', 'Antifolate resistance', 'Since the 1940s, antifolates have played a pivotal role in drug treatment of malignant, microbial, parasitic and chronic inflammatory diseases. The molecular basis of the anti-proliferative activity of antifolates relies on inhibition of key enzymes in folate metabolism, which results in disruption of purine and thymidylate biosynthesis, inhibition of DNA replication and cell death. The anti-inflammatory properties of antifolate have been most strongly related to its ability to block the release of pro-inflammatory cytokines such as tumour necrosis factor (TNF)-alpha or interleukin (IL)-1beta. Cells may develop resistance to an antifolate drug by virtue of impaired drug transport into cells, augmented drug export, impaired activation of antifolates through polyglutamylation, augmented hydrolysis of antifolate polyglutamates, increased expression and mutation of target enzymes, and the augmentation of cellular tetrahydrofolate-cofactor pools in cells.', 'Human Diseases; Drug resistance: antineoplastic', 'Antifolate resistance', 'ko01523', 'list(REFERENCE = \"PMID:22921318\", AUTHORS = \"Gonen N, Assaraf YG\", TITLE = \"Antifolates in cancer therapy: structure, activity and mechanisms of drug resistance.\", JOURNAL = c(\"Drug Resist Updat 15:183-210 (2012)\", \"DOI:10.1016/j.drup.2012.07.002\")), list(REFERENCE = \"PMID:17333344\", AUTHORS = \"Assaraf YG\", TITLE = \"Molecular basis of antifolate resistance.\", JOURNAL = c(\"Cancer Metastasis Rev 26:153-81 (2007)\", \"DOI:10.1007/s10555-007-9049-z\")), list(REFERENCE = \"PMID:26920997\", AUTHORS = \"Municio C, Soler Palacios B, Estrada-Capetillo L, Benguria A, 