INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (196, '02024', 'Quorum sensing', 'Quorum sensing (QS) is a regulatory system that allows bacteria to share information about cell density and adjust gene expression accordingly. All QS bacteria produce and release chemical signal molecules called autoinducers (AIs) that increase in concentration as a function of cell density. The most commonly studied AIs belong to one of the following three categories: acylated homoserine lactones, also referred to as AI-1, used by Gram-negative bacteria; peptide signals, used by Gram-positive bacteria; and AI-2, used by both Gram-negative and Gram-positive bacteria. QS can be divided into at least 4 steps: production of AIs by the bacterial cell; release of AIs, either actively or passively, into the surrounding environment; recognition of AIs by specific receptors; and leading to changes in gene regulation once they exceed a threshold concentration. The processes controlled by QS include virulence, competence, conjugation, antibiotic production, motility, sporulation, and biofilm formation.', 'Cellular Processes; Cellular community - prokaryotes', 'Quorum sensing', 'GO: 0009372', 'ko02024', 'list(REFERENCE = \"PMID:22712853\", AUTHORS = \"Pereira CS, Thompson JA, Xavier KB\", TITLE = \"AI-2-mediated signalling in bacteria.\", JOURNAL = c(\"FEMS Microbiol Rev 37:156-81 (2013)\", \"DOI:10.1111/j.1574-6976.2012.00345.x\")), list(REFERENCE = \"PMID:23965960\", AUTHORS = \"Verma SC, Miyashiro T\", TITLE = \"Quorum sensing in the squid-Vibrio symbiosis.\", JOURNAL = c(\"Int J Mol Sci 14:16386-401 (2013)\", \"DOI:10.3390/ijms140816386\")), list(REFERENCE = \"PMID:24348467\", AUTHORS = \"Norsworthy AN, Visick KL\", TITLE