INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (215, '03410', 'Base excision repair', 'Base excision repair (BER) is the predominant DNA damage repair pathway for the processing of small base lesions, derived from oxidation and alkylation damages. BER is normally defined as DNA repair initiated by lesion-specific DNA glycosylases and completed by either of the two sub-pathways: short-patch BER where only one nucleotide is replaced and long-patch BER where 2-13 nucleotides are replaced. Each sub-pathway of BER relies on the formation of protein complexes that assemble at the site of the DNA lesion and facilitate repair in a coordinated fashion. This process of complex formation appears to provide an increase in specificity and efficiency to the BER pathway, thereby facilitating the maintenance of genome integrity by preventing the accumulation of highly toxic repair intermediates.', 'Genetic Information Processing; Replication and repair', 'Base excision repair', 'Familial adenomatous polyposis, Ataxia-telangiectasia-like syndrome', 'GO: 0006284 0006285 0006286 0006287 0006288', 'ko03410', 'list(REFERENCE = \"PMID:17893748\", AUTHORS = \"Krwawicz J, Arczewska KD, Speina E, Maciejewska A, Grzesiuk E.\", TITLE = \"Bacterial DNA repair genes and their eukaryotic homologues: 1. Mutations in genes involved in base excision repair (BER) and DNA-end processors and their implication in mutagenesis and human disease.\", JOURNAL = \"Acta Biochim Pol 54:413-34 (2007)\"), list(REFERENCE = \"PMID:17337257\", AUTHORS = \"Almeida KH, Sobol RW.\", TITLE = \"A unified view of base excision repair: lesion-dependent protein complexes regulated by post-translational modification.\", JOURNAL = c(\"DNA Repair (Amst) 6:695-711 (2007)\", \"DOI:10.1016/j.dnarep.2007.01.009\")), list(REFERENCE = \"PMID:21665970\", AUTHORS = \"Moen MN, Knaevelsrud I, Haugland GT, Grosvik K, Birkeland NK, Klungland A, Bjelland S\", TITLE = \"Uracil-DNA glycosylase of Thermoplasma acidophilum directs long-patch base excision repair, which is promoted by deoxynucleoside triphosphates and ATP/ADP, into short-patch repair.\", JOURNAL = c(\"J Bacteriol 193:4495-508 (2011)\", \"DOI:10.1128/JB.00233-11\")), list(REFERENCE = \"PMID:11436317\", AUTHORS = \"Ikeda S, Seki S.\", TITLE = \"[Base excision repair: DNA glycosylase and AP endonuclease] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 46:916-23 (2001)\")', 'map03410', 531, 5536, 248, 105, 128, 50, 5237, 299);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (216, '03420', 'Nucleotide excision repair', 'Nucleotide excision repair (NER) is a mechanism to recognize and repair bulky DNA damage caused by compounds, environmental carcinogens, and exposure to UV-light. In humans hereditary defects in the NER pathway are linked to at least three diseases: xeroderma pigmentosum (XP), Cockayne syndrome (CS), and trichothiodystrophy (TTD). The repair of damaged DNA involves at least 30 polypeptides within two different sub-pathways of NER known as transcription-coupled repair (TCR-NER) and global genome repair (GGR-NER). TCR refers to the expedited repair of lesions located in the actively transcribed strand of genes by RNA polymerase II (RNAP II). In GGR-NER the first step of damage recognition involves XPC-hHR23B complex together with XPE complex (in prokaryotes, uvrAB complex). The following steps of GGR-NER and TCR-NER are similar.', 'Genetic Information Processing; Replication and repair', 'Nucleotide excision repair', 'Cockayne syndrome, Disorders of nucleotide excision repair, Xeroderma pigmentosum, Ataxia-telangiectasia-like syndrome, UV-sensitive syndrome', 'GO: 0006289', 'ko03420', 'list(REFERENCE = \"PMID:11436314\", AUTHORS = \"Sugasawa K.\", TITLE = \"[Molecular mechanism of mammalian nucleotide excision repair] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 46:893-901 (2001)\"), list(REFERENCE = \"PMID:11436316\", AUTHORS = \"Nakatsu Y.\", TITLE = \"[Transcription-coupled repair and Cockayne syndrome] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 46:908-15 (2001)\"), list(REFERENCE = \"PMID:11436323\", AUTHORS = \"Nakagawa N, Masui R, Kuramitsu S.\", TITLE = \"[Structure and function of DNA repair enzyme UvrB from Thermus thermophilus HB8] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 46:968-75 (2001)\"), list(REFERENCE = \"PMID:17276014\", AUTHORS = \"Kraemer KH, Patronas NJ, Schiffmann R, Brooks BP, Tamura D, DiGiovanna JJ.\", TITLE = \"Xeroderma pigmentosum, trichothiodystrophy and Cockayne syndrome: a complex genotype-phenotype relationship.\", JOURNAL = c(\"Neuroscience 145:1388-96 (2007)\", \"DOI:10.1016/j.neuroscience.2006.12.020\")), list(REFERENCE = \"PMID:17884153\", AUTHORS = \"Saldivar JS, Wu X, Follen M, Gershenson D.\", TITLE = \"Nucleotide excision repair pathway review I: implications in ovarian cancer and platinum sensitivity.\", JOURNAL = c(\"Gynecol Oncol 107:S56-71 (2007)\", \"DOI:10.1016/j.ygyno.2007.07.043\")), list(REFERENCE = \"PMID:16922398\", AUTHORS = \"Sugasawa K.\", TITLE = \"[DNA repair pathways involving Cul4A ubiquitin ligases] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 51:1339-44 (2006)\"), list(REFERENCE = \"PMID:18018633\", AUTHORS = \"Ito S, Ando D, Tanaka K.\", TITLE = \"[Cross-talk between DNA repair and transcription: molecular mechanism and disorders] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 52:1823-31 (2007)\")', 'map03420', 531, 5532, 248, 105, 128, 50, 5233, 299);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (217, '03430', 'Mismatch repair', 'c(\"DNA mismatch repair (MMR) is a highly conserved biological pathway that plays a key role in maintaining genomic stability. MMR corrects DNA mismatches generated during DNA replication, thereby preventing mutations from becoming permanent in dividing cells. MMR also suppresses homologous recombination and was recently shown to play a role in DNA damage signaling. Defects in MMR are associated with genome-wide instability, predisposition to certain types of cancer including HNPCC, resistance to certain chemotherapeutic agents, and abnormalities in meiosis and sterility in mammalian systems.\", \n\"The Escherichia coli MMR pathway has been extensively studied and is well characterized. In E. coli, the mismatch-activated MutS-MutL-ATP complex licenses MutH to incise the nearest unmethylated GATC sequence. UvrD and an exonuclease generate a gap. This gap is filled by pol III and DNA ligase. The GATC sites are then methylated by Dam. Several human MMR proteins have been identified based on their homology to E. coli MMR proteins. These include human homologs of MutS and MutL. Although E. coli MutS and MutL proteins are homodimers, human MutS and MutL homologs are heterodimers. The role of hemimethylated dGATC sites as a signal for strand discrimination is not conserved from E. coli to human. Human MMR is presumed to be nick-directed in vivo, and is thought to discriminate daughter and template strands using a strand-specific nick.\"\n)', 'Genetic Information Processing; Replication and repair', 'Mismatch repair', 'Mismatch repair deficiency', 'GO: 0006298', 'ko03430', 'list(REFERENCE = \"PMID:16612326\", AUTHORS = \"Jiricny J.\", TITLE = \"The multifaceted mismatch-repair system.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 7:335-46 (2006)\", \"DOI:10.1038/nrm1907\")), list(REFERENCE = \"PMID:18157157\", AUTHORS = \"Li GM.\", TITLE = \"Mechanisms and functions of DNA mismatch repair.\", JOURNAL = c(\"Cell Res 18:85-98 (2008)\", \"DOI:10.1038/cr.2007.115\")), list(REFERENCE = \"PMID:11920679\", AUTHORS = \"Marti TM, Kunz C, Fleck O.\", TITLE = \"DNA mismatch repair and mutation avoidance pathways.\", JOURNAL = c(\"J Cell Physiol 191:28-41 (2002)\", \"DOI:10.1002/jcp.10077\")), list(REFERENCE = \"PMID:11436301\", AUTHORS = \"Ikejima M, Shimada T.\", TITLE = \"[Molecular mechanism of mismatch repair] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 46:1124-9 (2001)\")', 'map03430', 531, 5559, 248, 105, 128, 50, 5260, 299);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (218, '03440', 'Homologous recombination', 'Homologous recombination (HR) is essential for the accurate repair of DNA double-strand breaks (DSBs), potentially lethal lesions. HR takes place in the late S-G2 phase of the cell cycle and involves the generation of a single-stranded region of DNA, followed by strand invasion, formation of a Holliday junction, DNA synthesis using the intact strand as a template, branch migration and resolution. It is investigated that RecA/Rad51 family proteins play a central role. The breast cancer susceptibility protein Brca2 and the RecQ helicase BLM (Bloom syndrome mutated) are tumor suppressors that maintain genome integrity, at least in part, through HR.', 'Genetic Information Processing; Replication and repair', 'Homologous recombination', 'Ataxia telangiectasia, Immunodeficiency associated with DNA repair defects, Defects in RecQ helicases, Congenital mirror movements, Nijmegen syndrome, Bloom syndrome, Ataxia-telangiectasia-like syndrome', 'GO: 0000724', 'ko03440', 'list(REFERENCE = \"PMID:18086882\", AUTHORS = \"Maloisel L, Fabre F, Gangloff S.\", TITLE = \"DNA polymerase delta is preferentially recruited during homologous recombination to promote heteroduplex DNA extension.\", JOURNAL = c(\"Mol Cell Biol 28:1373-82 (2008)\", \"DOI:10.1128/MCB.01651-07\")), list(REFERENCE = \"PMID:17571595\", AUTHORS = \"Haruta-Takahashi N, Iwasaki H.\", TITLE = \"[Recombination mediators] Japanese\", JOURNAL = \"Seikagaku 79:449-53 (2007)\"), list(REFERENCE = \"PMID:18430459\", AUTHORS = \"Ouyang KJ, Woo LL, Ellis NA.\", TITLE = \"Homologous recombination and maintenance of genome integrity: Cancer and aging through the prism of human RecQ helicases.\", JOURNAL = c(\"Mech Ageing Dev 129:425-40 (2008)\", \"DOI:10.1016/j.mad.2008.03.003\")), list(REFERENCE = \"PMID:15902993\", AUTHORS = \"Kawabata M, Kawabata T, Nishibori M.\", TITLE = \"Role of recA/RAD51 family proteins in mammals.\", JOURNAL = c(\"Acta Med Okayama 59:1-9 (2005)\", \"DOI:10.18926/AMO/31987\")), list(REFERENCE = \"PMID:12589470\", AUTHORS = \"Prado F, Cortes-Ledesma F, Huertas P, Aguilera A.\", TITLE = \"Mitotic recombination in Saccharomyces cerevisiae.\", JOURNAL = c(\"Curr Genet 42:185-98 (2003)\", \"DOI:10.1007/s00294-002-0346-3\")), list(REFERENCE = \"PMID:16935872\", AUTHORS = \"Heyer WD, Li X, Rolfsmeier M, Zhang XP.\", TITLE = \"Rad54: the Swiss Army knife of homologous recombination?\", JOURNAL = c(\"Nucleic Acids Res 34:4115-25 (2006)\", \"DOI:10.1093/nar/gkl481\")), list(REFERENCE = \"PMID:17395553\", AUTHORS = \"Michel B, Boubakri H, Baharoglu Z, LeMasson M, Lestini R.\", TITLE = \"Recombination proteins and rescue of arrested replication forks.\", JOURNAL = c(\"DNA Repair (Amst) 6:967-80 (2007)\", \"DOI:10.1016/j.dnarep.2007.02.016\"))', 'map03440', 531, 5495, 248, 105, 128, 50, 5251, 244);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (219, '03450', 'Non-homologous end-joining', 'Nonhomologous end joining (NHEJ) eliminates DNA double-strand breaks (DSBs) by direct ligation. NHEJ involves binding of the KU heterodimer to double-stranded DNA ends, recruitment of DNA-PKcs (MRX complex in yeast), processing of ends, and recruitment of the DNA ligase IV (LIG4)-XRCC4 complex, which brings about ligation. A recent study shows that bacteria accomplish NHEJ using just two proteins (Ku and DNA ligase), whereas eukaryotes require many factors. NHEJ repairs DSBs at all stages of the cell cycle, bringing about the ligation of two DNA DSBs without the need for sequence homology, and so is error-prone.', 'Genetic Information Processing; Replication and repair', 'Non-homologous end-joining', 'Severe combined immunodeficiency with microcephaly, growth retardation, and sensitivity to ionizing radiation, Nijmegen syndrome, LIG4 syndrome', 'GO: 0006303', 'ko03450', 'list(REFERENCE = \"PMID:16971555\", AUTHORS = \"Gennery AR.\", TITLE = \"Primary immunodeficiency syndromes associated with defective DNA double-strand break repair.\", JOURNAL = c(\"Br Med Bull 77-78:71-85 (2006)\", \"DOI:10.1093/bmb/ldl006\")), list(REFERENCE = \"PMID:17589524\", AUTHORS = \"Zhang Y, Hefferin ML, Chen L, Shim EY, Tseng HM, Kwon Y, Sung P, Lee SE, Tomkinson AE.\", TITLE = \"Role of Dnl4-Lif1 in nonhomologous end-joining repair complex assembly and suppression of homologous recombination.\", JOURNAL = c(\"Nat Struct Mol Biol 14:639-46 (2007)\", \"DOI:10.1038/nsmb1261\")), list(REFERENCE = \"PMID:16895466\", AUTHORS = \"Wyman C, Kanaar R.\", TITLE = \"DNA double-strand break repair: all\'s well that ends well.\", JOURNAL = c(\"Annu Rev Genet 40:363-83 (2006)\", \"DOI:10.1146/annurev.genet.40.110405.090451\")), list(REFERENCE = \"PMID:15578053\", AUTHORS = \"Weller GR, Brandt VL, Roth DB.\", TITLE = \"Doing more with less in bacterial DNA repair.\", JOURNAL = c(\"Nat Struct Mol Biol 11:1158-9 (2004)\", \"DOI:10.1038/nsmb1204-1158\")), list(REFERENCE = \"PMID:12215643\", AUTHORS = \"Weller GR, Kysela B, Roy R, Tonkin LM, Scanlan E, Della M, Devine SK, Day JP, Wilkinson A, d\'Adda di Fagagna F, Devine KM, Bowater RP, Jeggo PA, Jackson SP, Doherty AJ.\", TITLE = \"Identification of a DNA nonhomologous end-joining complex in bacteria.\", JOURNAL = c(\"Science 297:1686-9 (2002)\", \"DOI:10.1126/science.1074584\")), list(REFERENCE = \"PMID:17506672\", AUTHORS = \"Pitcher RS, Brissett NC, Doherty AJ.\", TITLE = \"Nonhomologous end-joining in bacteria: a microbial perspective.\", JOURNAL = c(\"Annu Rev Microbiol 61:259-82 (2007)\", \"DOI:10.1146/annurev.micro.61.080706.093354\"))', 'map03450', 531, 1315, 248, 105, 128, 50, 1297, 18);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (220, '03460', 'Fanconi anemia pathway', 'The Fanconi anemia pathway is required for the efficient repair of damaged DNA, especially interstrand cross-links (ICLs). DNA ICL is directly recognized by FANCM and associated proteins, that recruit the FA core complex. The FA core complex monoubiquitinates FANCD2 and FANCI. The monoubiquitinated FANCD2/FANCI becomes an active form and interacts with a series of DNA repair proteins and facilitates downstream repair pathways. Fanconi anemia is caused by mutations in one of at least 13 FA genes and is characterized by congenital growth abnormalities, bone marrow failure and cancer predisposition.', 'Genetic Information Processing; Replication and repair', 'Fanconi anemia pathway', 'Fanconi anemia, Seckel syndrome, Bloom syndrome', 'GO: 0043240', 'ko03460', 'list(REFERENCE = \"PMID:18047734\", AUTHORS = \"Jacquemont C, Taniguchi T\", TITLE = \"The Fanconi anemia pathway and ubiquitin.\", JOURNAL = c(\"BMC Biochem 8 Suppl 1:S10 (2007)\", \"DOI:10.1186/1471-2091-8-S1-S10\")), list(REFERENCE = \"PMID:19805513\", AUTHORS = \"Bhagwat N, Olsen AL, Wang AT, Hanada K, Stuckert P, Kanaar R, D\'Andrea A, Niedernhofer LJ, McHugh PJ\", TITLE = \"XPF-ERCC1 participates in the Fanconi anemia pathway of cross-link repair.\", JOURNAL = c(\"Mol Cell Biol 29:6427-37 (2009)\", \"DOI:10.1128/MCB.00086-09\")), list(REFERENCE = \"PMID:21701511\", AUTHORS = \"Deans AJ, West SC\", TITLE = \"DNA interstrand crosslink repair and cancer.\", JOURNAL = c(\"Nat Rev Cancer 11:467-80 (2011)\", \"DOI:10.1038/nrc3088\")), list(REFERENCE = \"PMID:20064461\", AUTHORS = \"Deans AJ, West SC\", TITLE = \"FANCM connects the genome instability disorders Bloom\'s Syndrome and Fanconi Anemia.\", JOURNAL = c(\"Mol Cell 36:943-53 (2009)\", \"DOI:10.1016/j.molcel.2009.12.006\")), list(REFERENCE = \"PMID:20039786\", AUTHORS = \"Muniandy PA, Liu J, Majumdar A, Liu ST, Seidman MM\", TITLE = \"DNA interstrand crosslink repair in mammalian cells: step by step.\", JOURNAL = c(\"Crit Rev Biochem Mol Biol 45:23-49 (2010)\", \"DOI:10.3109/10409230903501819\")), list(REFERENCE = \"PMID:18518821\", AUTHORS = \"Ciccia A, McDonald N, West SC\", TITLE = \"Structural and functional relationships of the XPF/MUS81 family of proteins.\", JOURNAL = c(\"Annu Rev Biochem 77:259-87 (2008)\", \"DOI:10.1146/annurev.biochem.77.070306.102408\"))', 'map03460', 248, 0, 248, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (221, '04010', 'MAPK signaling pathway', 'The mitogen-activated protein kinase (MAPK) cascade is a highly conserved module that is involved in various cellular functions, including cell proliferation, differentiation and migration. Mammals express at least four distinctly regulated groups of MAPKs, extracellular signal-related kinases (ERK)-1/2, Jun amino-terminal kinases (JNK1/2/3), p38 proteins (p38alpha/beta/gamma/delta) and ERK5, that are activated by specific MAPKKs: MEK1/2 for ERK1/2, MKK3/6 for the p38, MKK4/7 (JNKK1/2) for the JNKs, and MEK5 for ERK5. Each MAPKK, however, can be activated by more than one MAPKKK, increasing the complexity and diversity of MAPK signalling. Presumably each MAPKKK confers responsiveness to distinct stimuli. For example, activation of ERK1/2 by growth factors depends on the MAPKKK c-Raf, but other MAPKKKs may activate ERK1/2 in response to pro-inflammatory stimuli.', 'Environmental Information Processing; Signal transduction', 'MAPK signaling pathway', 'Frontotemporal lobar degeneration, Hereditary sensory and autonomic neuropathy, Multiple synostosis syndrome, FGFR3-related short limb skeletal dysplasia, Noonan syndrome and related disorders, Parkes Weber syndrome, Hereditary lymphedema, Osteoporosis, lymphedema, anhydrotic ectodermal dysplasia with immunodeficiency (OLEDAID), Coffin-Lowry syndrome, Myofibrillar myopathies, Lacrimo-auriculo-dento-digital syndrome, Incontinentia pigmenti, Aplasia of lacrimal and salivary glands, Brugada syndrome, Hypokalemic periodic paralysis (HypoPP), Episodic ataxias, Hemiplegic migraine, Congenital stationary night blindness, Loeys-Dietz syndrome, Familial thoracic aortic an