INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (222, '04011', 'MAPK signaling pathway - yeast', 'The S. cerevisiae genome encodes multiple MAP kinase orthologs. One (Fus3) mediates cellular response to peptide pheromones. Another (Kss1) permits adjustment to nutrient limiting conditions. A third (Hog1) is necessary for survival under hyperosmotic conditions. A fourth (Slt2/Mpk1) is required for repair of injuries to the cell wall. As in mammalian cells, these pathways consist of a conserved module in which three kinases phosphorylate each other in sequence. The MAPK is phosphorylated by the MAPK/ERK kinase (MAPKK/MEK), which is itself phosphorylated by a MEK kinase (MAPKKK/MEKK).', 'Environmental Information Processing; Signal transduction', 'MAPK signaling pathway - yeast', 'GO: 0000165', 'ko04011', 'list(REFERENCE = \"PMID:15690603\", AUTHORS = \"Bardwell L\", TITLE = \"A walk-through of the yeast mating pheromone response pathway.\", JOURNAL = c(\"Peptides 26:339-50 (2005)\", \"DOI:10.1016/j.peptides.2004.10.002\")), list(REFERENCE = \"PMID:15944456\", AUTHORS = \"Levin DE\", TITLE = \"Cell wall integrity signaling in Saccharomyces cerevisiae.\", JOURNAL = c(\"Microbiol Mol Biol Rev 69:262-91 (2005)\", \"DOI:10.1128/MMBR.69.2.262-291.2005\")), list(REFERENCE = \"PMID:22174182\", AUTHORS = \"Levin DE\", TITLE = \"Regulation of cell wall biogenesis in Saccharomyces cerevisiae: the cell wall integrity signaling pathway.\", JOURNAL = c(\"Genetics 189:1145-75 (2011)\", \"DOI:10.1534/genetics.111.128264\")), list(REFERENCE = \"PMID:17875410\", AUTHORS = \"Hohmann S, Krantz M, Nordlander B\", TITLE = \"Yeast osmoregulation.\", JOURNAL = c(\"Methods Enzymol 428:29-45 (2007)\", \"DOI:10.1016/S0076-687