INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (239, '04072', 'Phospholipase D signaling pathway', 'Phospholipase D (PLD) is an essential enzyme responsible for the production of the lipid second messenger phosphatidic acid (PA), which is involved in fundamental cellular processes, including membrane trafficking, actin cytoskeleton remodeling, cell proliferation and cell survival. PLD activity can be stimulated by a large number of cell surface receptors and is elaborately regulated by intracellular factors, including protein kinase C isoforms, small GTPases of the ARF, Rho and Ras families and the phosphoinositide, phosphatidylinositol 4,5-bisphosphate (PIP2). The PLD-produced PA activates signaling proteins and acts as a node within the membrane to which signaling proteins translocate. Several signaling proteins, including Raf-1 and mTOR, directly bind PA to mediate translocation or activation, respectively.', 'Environmental Information Processing; Signal transduction', 'Phospholipase D signaling pathway', 'ko04072', 'list(REFERENCE = \"PMID:17245604\", AUTHORS = \"Oude Weernink PA, Lopez de Jesus M, Schmidt M\", TITLE = \"Phospholipase D signaling: orchestration by PIP2 and small GTPases.\", JOURNAL = c(\"Naunyn Schmiedebergs Arch Pharmacol 374:399-411 (2007)\", \"DOI:10.1007/s00210-007-0131-4\")), list(REFERENCE = \"PMID:21936578\", AUTHORS = \"Selvy PE, Lavieri RR, Lindsley CW, Brown HA\", TITLE = \"Phospholipase D: enzymology, functionality, and chemical modulation.\", JOURNAL = c(\"Chem Rev 111:6064-119 (2011)\", \"DOI:10.1021/cr200296t\")), list(REFERENCE = \"PMID:24995811\", AUTHORS = \"Brandenburg LO, Pufe T, Koch T\", TITLE = \"Role of phospholipase d in g-protein coupled receptor function.\", JOURNAL = c(\"Membranes (Basel) 4:302-18 (2014)\", \"DOI:10.3390/membranes4030302\")), list(REFERENCE = \"PMID:17054901\", AUTHORS = \"Oude Weernink PA, Han L, Jakobs KH, Schmidt M\", TITLE = \"Dynamic phospholipid signaling by G protein-coupled receptors.\", JOURNAL = c(\"Biochim Biophys Acta 1768:888-900 (2007)\", \"DOI:10.1016/j.bbamem.2006.09.012\")), list(REFERENCE = \"PMID:24990952\", AUTHORS = \"Foster DA, Salloum D, Menon D, Frias MA\", TITLE = \"Phospholipase D and the maintenance of phosphatidic acid levels for regulation of mammalian target of rapamycin (mTOR).\", JOURNAL = c(\"J Biol Chem 289:22583-8 (2014)\", \"DOI:10.1074/jbc.R114.566091\")), list(REFERENCE = \"PMID:16143829\", AUTHORS = \"Jenkins GM, Frohman MA\", TITLE = \"Phospholipase D: a lipid centric review.\", JOURNAL = c(\"Cell Mol Life Sci 62:2305-16 (2005)\", \"DOI:10.1007/s00018-005-5195-z\")), list(REFERENCE = \"PMID:19815110\", AUTHORS = \"Metcalfe DD, Peavy RD, Gilfillan AM\", TITLE = \"Mechanisms of mast cell signaling in anaphylaxis.\", JOURNAL = c(\"J Allergy Clin Immunol 124:639-46; quiz 647-8 (2009)\", \"DOI:10.1016/j.jaci.2009.08.035\")), list(REFERENCE = \"PMID:18936782\", AUTHORS = \"Kalesnikoff J, Galli SJ\", TITLE = \"New developments in mast cell biology.\", JOURNAL = c(\"Nat Immunol 9:1215-23 (2008)\", \"DOI:10.1038/ni.f.216\")), list(REFERENCE = \"PMID:12217395\", AUTHORS = \"Cockcroft S, Way G, O\'Luanaigh N, Pardo R, Sarri E, Fensome A\", TITLE = \"Signalling role for ARF and phospholipase D in mast cell exocytosis stimulated by crosslinking of the high affinity FcepsilonR1 receptor.\", JOURNAL = c(\"Mol Immunol 38:1277-82 (2002)\", \"DOI:10.1016/S0161-5890(02)00075-5\")), list(REFERENCE = \"PMID:19410013\", AUTHORS = \"Lee CS, Kim KL, Jang JH, Choi YS, Suh PG, Ryu SH\", TITLE = \"The roles of phospholipase D in EGFR signaling.\", JOURNAL = c(\"Biochim Biophys Acta 1791:862-8 (2009)\", \"DOI:10.1016/j.bbalip.2009.04.007\")), list(REFERENCE = \"PMID:16622417\", AU