INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (245, '04113', 'Meiosis - yeast', 'During meiosis, a single round of DNA replication is followed by two rounds of chromosome segregation, called meiosis I and meiosis II. At meiosis I, one pair of sister chromatids recombines with, then segregates from, its homologous pair, while the sister chromatids segregate from each other at meoisis II. In yeast, the switch to enter growth phase or meiosis phase is controlled by nutritional signals. At the onset of meiosis, nutritional signaling pathways converge on transcriptional regulation of Ime1. Ime1 is a transcriptional activator to promote expression of early meiosis-specific genes required for pre-meiotic DNA replication and meiotic recombination. Two of the early meiosis-specific genes, a transcriptional activator, Ndt80, and a CDK2 homolog, Ime2, are required for the transcription of middle meiosis-specific genes, which includes B-type cyclin genes and genes involved in spore wall formation. Polo-like kinase Cdc5, whose expression is activated by Ndt80, is required for the critical step in meiotic progression. Cdc5 regulates sister-kinetochore co-orientation by promoting the localization of Mam1 and Lrs4, components of the co-orientation complex, to kinetochores. Cdc5 also phosphorylates the meiotic cohesin subunit Rec8, which is essential for its cleavage and removal by separase. Moreover, Cdc5 promotes the release of protein phosphatase Cdc14 from the nuclezus, which is required for anaphase I spindle disassembly and exit from meiosis I. The centromeric protein Sgo1 is a key regulator of meiotic chromosome segregation. Sgo1 with PP2A may protect pericentromeric but not arm cohesion of sister chromatids during meiosis I. Sgo1 then disappears at meiosis II to allow sister chromatids to separate.', 'Cellular Processes; Cell growth and death', 'Meiosis - yeast', 'GO: 0051321', 'ko04113', 'list(REFERENCE = \"PMID:12730290\", AUTHORS = \"Honigberg SM, Purnapatre K\", TITLE = \"Signal pathway integration in the switch from the mitotic cell cycle to meiosis in yeast.\", JOURNAL = c(\"J Cell Sci 116:2137-47 (2003)\", \"DOI:10.1242/jcs.00460\")), list(REFERENCE = \"PMID:12722950\", AUTHORS = \"Kassir Y, Adir N, Boger-Nadjar E, Raviv NG, Rubin-Bejerano I, Sagee S, Shenhar G\", TITLE = \"Transcriptional regulation of meiosis in budding yeast.\", JOURNAL = c(\"Int Rev Cytol 224:111-71 (2003)\", \"DOI:10.1016/S0074-7696(05)24004-4\")), list(REFERENCE = \"PMID:15258924\", AUTHORS = \"Honigberg SM\", TITLE = \"Ime2p and Cdc28p: co-pilots driving meiotic development.\", JOURNAL = c(\"J Cell Biochem 92:1025-33 (2004)\", \"DOI:10.1002/jcb.20131\")), list(REFERENCE = \"PMID:16546077\", AUTHORS = \"Hochwagen A, Amon A\", TITLE = \"Checking your breaks: surveillance mechanisms of meiotic recombination.\", JOURNAL = c(\"Curr Biol 16:R217-28 (2006)\", \"DOI:10.1016/j.cub.2006.03.009\")), list(REFERENCE = \"PMID:18832063\", AUTHORS = \"Hollingsworth NM\", TITLE = \"Deconstructing meiosis one kinase at a time: polo pushes past pachytene.\", JOURNAL = c(\"Genes Dev 22:2596-600 (2008)\", \"DOI:10.1101/gad.1727508\")), list(REFERENCE = \"PMID:18832066\", AUTHORS = \"Sourirajan A, Lichten M\", TITLE = \"Polo-like kinase Cdc5 drives exit from pachytene during budding yeast meiosis.\", JOURNAL = c(\"Genes Dev 22:2627-32 (2008)\", \"DOI:10.1101/gad.1711408\")), list(REFERENCE = \"PMID:10973068\", AUTHORS = \"Roeder GS, Bailis JM\", TITLE = \"The pachytene checkpoint.\", JOURNAL = c(\"Trends Genet 16:395-403 (2000)\", \"DOI:10.1016/S0168-9525(00)02080-1\")), list(REFERENCE = \"PMID:15173822\", AUTHORS = \"Barr FA, Sillje HH, Nigg EA\", TITLE = \"Polo-like kinases and the orchestration of cell division.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 5:429-40 (2004)\", \"DOI:10.1038/nrm1401\")), list(REFERENCE = \"PMID:12963824\", AUTHORS = \"Lee BH, Amon A\", TITLE = \"Polo kinase--meiotic cell cycle coordinator.\", JOURNAL = c(\"Cell Cycle 2:400-2 (2003)\", \"DOI:10.4161/cc.2.5.475\")), list(REFERENCE = \"PMID:12737807\", AUTHORS = \"Buonomo SB, Rabitsch KP, Fuchs J, Gruber S, Sullivan M, Uhlmann F, Petronczki M, Toth A, Nasmyth K\", TITLE = \"Division of the nucleolus and its release of CDC14 during anaphase of meiosis I depends on separase, SPO12, and SLK19.\", JOURNAL = c(\"Dev Cell 4:727-39 (2003)\", \"DOI:10.1016/S1534-5807(03)00129-1\")), list(REFERENCE = \"PMID:15797379\", AUTHORS = \"Oelschlaegel T, Schwickart M, Matos J, Bogdanova A, Camasses A, Havlis J, Shevchenko A, Zachariae W\", TITLE = \"The yeast APC/C subunit Mnd2 prevents premature sister chromatid separation triggered by the meiosis-specific APC/C-Ama1.\", JOURNAL = c(\"Cell 120:773-88 (2005)\", \"DOI:10.1016/j.cell.2005.01.032\")), list(REFERENCE = \"PMID:16759372\", AUTHORS = \"Dai W, Wang X\", TITLE = \"The Yin and Yang of centromeric cohesion of sister chromatids: mitotic kinases meet protein phosphatase 2A.\", JOURNAL = c(\"Cell Div 1:9 (2006)\", \"DOI:10.1186/1747-1028-1-9\")), list(REFERENCE = \"PMID:19299562\", AUTHORS = \"Clift D, Bizzari F, Marston AL\", TITLE = \"Shugoshin prevents cohesin cleavage by PP2A(Cdc55)-dependent inhibition of separase.\", JOURNAL = c(\"Genes Dev 23:766-80 (2009)\", \"DOI:10.1101/gad.507509\")), list(REFERENCE = \"PMID:18598214\", AUTHORS = \"Pesin JA, Orr-Weaver TL\", TITLE = \"Regulation of APC/C activators in mitosis and meiosis.\", JOURNAL = c(\"Annu Rev Cell Dev Biol 24:475-99 (2008)\", \"DOI:10.1146/annurev.cellbio.041408.115949\")), list(REFERENCE = \"PMID:18946082\", AUTHORS = \"Diamond AE, Park JS, Inoue I, Tachikawa H, Neiman AM\", TITLE = \"The anaphase promoting complex targeting subunit Ama1 links meiotic exit to cytokinesis during sporulation in Saccharomyces cerevisiae.\", JOURNAL = c(\"Mol Biol Cell 20:134-45 (2009)\", \"DOI:10.1091/mbc.E08-06-0615\"))', 'map04113', 'Meiosis - yeast', 128, 0, 0, 0, 128, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (246, '04114', 'Oocyte meiosis', 'During meiosis, a single round of DNA replication is followed by two rounds of chromosome segregation, called meiosis I and meiosis II. At meiosis I, homologous chromosomes recombine and then segregate to opposite poles, while the sister chromatids segregate from each other at meoisis II. In vertebrates, immature oocytes are arrested at the PI (prophase of meiosis I). The resumption of meiosis is stimulated by progesterone, which carries the oocyte through two consecutive M-phases (MI and MII) to a second arrest at MII. The key activity driving meiotic progression is the MPF (maturation-promoting factor), a heterodimer of CDC2 (cell division cycle 2 kinase) and cyclin B. In PI-arrested oocytes, MPF is initially inactive and is activated by the dual-specificity CDC25C phosphatase as the result of new synthesis of Mos induced by progesterone. MPF activation mediates the transition from the PI arrest to MI. The subsequent decrease in MPF levels, required to exit from MI into interkinesis, is induced by a negative feedback loop, where CDC2 brings about the activation of the APC (anaphase-promoting complex), which mediates destruction of cyclin B. Re-activation of MPF for MII requires re-accumulation of high levels of cyclin B as well as the inactivation of the APC by newly synthesized Emi2 and other components of the CSF (cytostatic factor), such as cyclin E or high levels of Mos. CSF antagonizes the ubiquitin ligase activity of the APC, preventing cyclin B destruction and meiotic exit until fertilization occurs. Fertilization triggers a transient increase in cytosolic free Ca2+, which leads to CSF inactivation and cyclin B destruction through the APC. Then eggs are released from MII into the first embryonic cell cycle.', 'Cellular Processes; Cell growth and death', 'Oocyte meiosis', 'Coffin-Lowry syndrome, Cornelia de Lange syndrome, Growth delay due to insulin-like growth factor I resistance, Chronic atrial and intestinal dysrhythmia', 'GO: 0051321', 'ko04114', 'list(REFERENCE = \"PMID:16760654\", AUTHORS = \"Haccard O, Jessus C\", TITLE = \"Oocyte maturation, Mos and cyclins--a matter of synthesis: two functionally redundant ways to induce meiotic maturation.\", JOURNAL = c(\"Cell Cycle 5:1152-9 (2006)\", \"DOI:10.4161/cc.5.11.2800\")), list(REFERENCE = \"PMID:15182704\", AUTHORS = \"Crane R, Gadea B, Littlepage L, Wu H, Ruderman JV\", TITLE = \"Aurora A, meiosis and mitosis.\", JOURNAL = c(\"Biol Cell 96:215-29 (2004)\", \"DOI:10.1016/j.biolcel.2003.09.008\")), list(REFERENCE = \"PMID:15136768\", AUTHORS = \"Perdiguero E, Nebreda AR\", TITLE = \"Regulation of Cdc25C activity during the meiotic G2/M transition.\", JOURNAL = c(\"Cell Cycle 3:733-7 (2004)\", \"DOI:10.4161/cc.3.6.906\")), list(REFERENCE = \"PMID:17480229\", AUTHORS = \"Perry JA, Kornbluth S\", TITLE = \"Cdc25 and Wee1: analogous opposites?\", JOURNAL = c(\"Cell Div 2:12 (2007)\", \"DOI:10.1186/1747-1028-2-12\")), list(REFERENCE = \"PMID:15182701\", AUTHORS = \"Jessus C, Ozon R\", TITLE = \"How does Xenopus oocyte acquire its competence to undergo meiotic maturation?\", JOURNAL = c(\"Biol Cell 96:187-92 (2004)\", \"DOI:10.1016/j.biolcel.2003.12.007\")), list(REFERENCE = \"PMID:16518630\", AUTHORS = \"Revenkova E, Jessberger R\", TITLE = \"Shaping meiotic prophase chromosomes: cohesins and synaptonemal complex proteins.\", JOURNAL = c(\"Chromosoma 115:235-40 (2006)\", \"DOI:10.1007/s00412-006-0060-x\")), list(REFERENCE = \"PMID:18631137\", AUTHORS = \"Belloc E, Pique M, Mendez R\", TITLE = \"Sequential waves of polyadenylation and deadenylation define a translation circuit that drives meiotic progression.\", JOURNAL = c(\"Biochem Soc Trans 36:665-70 (2008)\", \"DOI:10.1042/BST0360665\")), list(REFERENCE = \"PMID:18598214\", AUTHORS = \"Pesin JA, Orr-Weaver TL\", TITLE = \"Regulation of APC/C activators in mitosis and meiosis.\", JOURNAL = c(\"Annu Rev Cell Dev Biol 24:475-99 (2008)\", \"DOI:10.1146/annurev.cellbio.041408.115949\")), list(REFERENCE = \"PMID:18550795\", AUTHORS = \"Tang W, Wu JQ, Guo Y, Hansen DV, Perry JA, Freel CD, Nutt L, Jackson PK, Kornbluth S\", TITLE = \"Cdc2 and Mos regulate Emi2 stability to promote the meiosis I-meiosis II transition.\", JOURNAL = c(\"Mol Biol Cell 19:3536-43 (2008)\", \"DOI:10.1091/mbc.E08-04-0417\")), list(REFERENCE = \"PMID:18946022\", AUTHORS = \"Wu JQ, Kornbluth S\", TITLE = \"Across the meiotic divide - CSF activity in the post-Emi2/XErp1 era.\", JOURNAL = c(\"J Cell Sci 121:3509-14 (2008)\", \"DOI:10.1242/jcs.036855\")), list(REFERENCE = \"PMID:16554437\", AUTHORS = \"Schmidt A, Rauh NR, Nigg EA, Mayer TU\", TITLE = \"Cytostatic factor: an activity that puts the cell cycle on hold.\", JOURNAL = c(\"J Cell Sci 119:1213-8 (2006)\", \"DOI:10.1242/jcs.02919\")), list(REFERENCE = \"PMID:18096427\", AUTHORS = \"Vogt E, Kirsch-Volders M, Parry J, Eichenlaub-Ritter U\", TITLE = \"Spindle formation, chromosome segregation and the spindle checkpoint in mammalian oocytes and susceptibility to meiotic error.\", JOURNAL = c(\"Mutat Res 651:14-29 (2008)\", \"DOI:10.1016/j.mrgentox.2007.10.015\")), list(REFERENCE = \"PMID:18311174\", AUTHORS = \"Perry AC, Verlhac MH\", TITLE = \"Second meiotic arrest and exit in frogs and mice.\", JOURNAL = c(\"EMBO Rep 9:246-51 (2008)\", \"DOI:10.1038/embor.2008.22\")), list(REFERENCE = \"PMID:14691134\", AUTHORS = \"Tunquist BJ, Eyers PA, Chen LG, Lewellyn AL, Maller JL\", TITLE = \"Spindle checkpoint proteins Mad1 and Mad2 are required for cytostatic factor-mediated metaphase arrest.\", JOURNAL = c(\"J Cell Biol 163:1231-42 (2003)\", \"DOI:10.1083/jcb.200306153\")), list(REFERENCE = \"PMID:14517314\", AUTHO