INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (247, '04115', 'p53 signaling pathway', 'p53 activation is induced by a number of stress signals, including DNA damage, oxidative stress and activated oncogenes. The p53 protein is employed as a transcriptional activator of p53-regulated genes. This results in three major outputs; cell cycle arrest, cellular senescence or apoptosis. Other p53-regulated gene functions communicate with adjacent cells, repair the damaged DNA or set up positive and negative feedback loops that enhance or attenuate the functions of the p53 protein and integrate these stress responses with other signal transduction pathways.', 'Cellular Processes; Cell growth and death', 'p53 signaling pathway', 'Type 2 diabetes mellitus, Li-Fraumeni syndrome, Lymphangioleiomyomatosis, Tuberous sclerosis complex, Seckel syndrome, Choroid plexus papilloma, Plasminogen activator inhibitor type 1 deficiency, VACTERL/VATER association', 'ko04115', 'list(REFERENCE = \"PMID:16557269\", AUTHORS = \"Levine AJ, Hu W, Feng Z.\", TITLE = \"The P53 pathway: what questions remain to be explored?\", JOURNAL = c(\"Cell Death Differ 13:1027-36 (2006)\", \"DOI:10.1038/sj.cdd.4401910\")), list(REFERENCE = \"PMID:15838523\", AUTHORS = \"Harris SL, Levine AJ.\", TITLE = \"The p53 pathway: positive and negative feedback loops.\", JOURNAL = c(\"Oncogene 24:2899-908 (2005)\", \"DOI:10.1038/sj.onc.1208615\")), list(REFERENCE = \"PMID:11747320\", AUTHORS = \"Balint E E, Vousden KH.\", TITLE = \"Activation and activities of the p53 tumour suppressor protein.\", JOURNAL = c(\"Br J Cancer 85:1813-23 (2001)\", \"DOI:10.1054/bjoc.2001.2128\")), list(REFERENCE = \"PMID:11313928\", AUTHORS = \"Taylor WR, Stark GR.\", TI