INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (248, '04120', 'Ubiquitin mediated proteolysis', 'Protein ubiquitination plays an important role in eukaryotic cellular processes. It mainly functions as a signal for 26S proteasome dependent protein degradation. The addition of ubiquitin to proteins being degraded is performed by a reaction cascade consisting of three enzymes, named E1 (ubiquitin activating enzyme), E2 (ubiquitin conjugating enzyme), and E3 (ubiquitin ligase). Each E3 has specificity to its substrate, or proteins to be targeted by ubiquitination. Many E3s are discovered in eukaryotes and they are classified into four types: HECT type, U-box type, single RING-finger type, and multi-subunit RING-finger type. Multi-subunit RING-finger E3s are exemplified by cullin-Rbx E3s and APC/C. They consist of a RING-finger-containing subunit (RBX1 or RBX2) that functions to bind E2s, a scaffold-like cullin molecule, adaptor proteins, and a target recognizing subunit that binds substrates.', 'Genetic Information Processing; Folding, sorting and degradation', 'Ubiquitin mediated proteolysis', '3M syndrome, von Hippel-Lindau syndrome, Opitz-GBBB syndrome, Limb-girdle muscular dystrophy, Syndromic X-linked mental retardation, Syndromic multisystem autoimmune disease, Mulibrey nanism, Angelman syndrome, UV-sensitive syndrome, Macrocephaly, dysmorphic facies, and psychomotor retardation', 'GO: 0016567', 'ko04120', 'list(REFERENCE = \"PMID:16922370 (Japanese)\", AUTHORS = \"Kamura T.\", TITLE = \"[Cullin-based E3 family] Japanese\", JOURNAL = \"Tanpakushitsu Kakusan Koso 51:1167-72 (2006)\"), list(REFERENCE = \"PMID:16922408 (Japanese)\", AUTHORS = \"Matsuda N.\", TITLE = \"[Overview: hereditary disorders caused by dysfunction of ubiquitylation]\", JOURNAL = \"Tanpakushitsu Kakusan Koso 51:1402-7 (2006)\"), list(REFERENCE = \"PMID:16922367 (Japanese)\", AUTHORS = \"Tokunaga F.\", TITLE = \"[E2 ubiquitin-conjugating enzymes: structures and functions]\", JOURNAL = \"Tanpakushitsu Kakusan Koso 51:1150-6 (2006)\"), list(REFERENCE = \"PMID:16816840\", AUTHORS = \"Nalepa G, Rolfe M, Harper JW.\", TITLE = \"Drug discovery in the ubiquitin-proteasome system.\", JOURNAL = c(\"Nat Rev Drug Discov 5:596-613 (2006)\", \"DOI:10.1038/nrd2056\")), list(REFERENCE = \"PMID:16405856\", AUTHORS = \"Hsu YJ, Goodman SR.\", TITLE = \"Spectrin and ubiquitination: a review.\", JOURNAL = \"Cell Mol Biol (Noisy-le-grand) Suppl 51:OL801-7 (2005)\"), list(REFERENCE = \"PMID:12944364\", AUTHORS = \"Hatakeyama S, Nakayama KI.\", TITLE = \"Ubiquitylation as a quality control system for intracellular proteins.\", JOURNAL = c(\"J Biochem 134:1-8 (2003)\", \"DOI:10.1093/jb/mvg106\")), list(REFERENCE = \"PMID:17588513\", AUTHORS = \"Lee J, Zhou P.\", TITLE = \"DCAFs, the missing link of the CUL4-DDB1 ubiquitin ligase.\", JOURNAL = c(\"Mol Cell 26:775-80 (2007)\", \"DOI:10.1016/j.molcel.2007.06.001\"))', 'map04120', 531, 0, 248, 105, 128, 50, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (249, '04122', 'Sulfur relay system', 'Ubiquitin and ubiquitin-like proteins (Ubls) are signalling messengers that control many cellular functions, such as cell proliferation, apoptosis, and DNA repair. It is suggested that Ub-protein modification evolved from prokaryotic sulfurtransfer systems. Molybdenum cofactor (Moco) and thiamin are sulfur-containing cofactors whose biosynthesis includes a key sulfur transfer step that uses unique sulfur carrier proteins, MoaD and ThiS. Ubiquitin, MoaD, and ThiS are all structurally related proteins whose C-termini are activated through adenylation by homologous E1-like enzymes. s2T biosynthesis may share similar chemistry with Moco and thiamin synthesis. In Saccharomyces cerevisiae, Urm1 and Uba4 function as part of a ubl protein conjugation system, though they have sequence homology to bacterial sulfur-transfer enzymes and the ability to function in sulfur transfer.', 'Genetic Information Processing; Folding, sorting and degradation', 'Sulfur relay system', 'Xanthinuria, Infantile liver failure', 'ko04122', 'list(REFERENCE = \"PMID:19151091\", AUTHORS = \"Noma A, Sakaguchi Y, Suzuki T\", TITLE = \"Mechanistic characterization of the sulfur-relay system for eukaryotic 2-thiouridine biogenesis at tRNA wobble positions.\", JOURNAL = c(\"Nucleic Acids Res 37:1335-52 (2009)\", \"DOI:10.1093/nar/gkn1023\")), list(REFERENCE = \"PMID:16859499\", AUTHORS = \"Iyer LM, Burroughs AM, Aravind L\", TITLE = \"The prokaryotic antecedents of the ubiquitin-signaling system and the early evolution of ubiquitin-like beta-grasp domains.\", JOURNAL = c(\"Genome Biol 7:R60 (2006)\", \"DOI:10.1186/gb-2006-7-7-r60\")), list(REFERENCE = \"PMID:19675644\", AUTHORS = \"Schwarz G, Mendel RR, Ribbe MW\", TITLE = \"Molybdenum cofactors, enzymes and pathways.\", JOURNAL = c(\"Nature 460:839-47 (2009)\", \"DOI:10.1038/nature08302\"))', 'map04122', 'Sulfur relay system', 530, 5404, 248, 105, 128, 49, 5107, 297);
