INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (252, '04137', 'Mitophagy - animal', 'Mitochondria act as the energy powerhouse of the cell, and are essential for eukaryotic cells to grow and function normally. However, deleterious byproducts of oxidative phosphorylation process called reactive oxidative species (ROS) lead to mitochondrial dysfunction. If the damage is too excessive to be repaired, such mitochondria are selectively recognized and targeted for degradation by a specific mode of autophagy, termed mitophagy. The loss of the mitochondrial membrane potential can induce mitophagy, involving the kinase PINK1 and the E3 ligase Parkin. PINK1 serves as the sensor for the mitochondrial depolarization and recruits Parkin, followed by ubiquitin-dependent recruitment of mitophagy receptors. There are also several PINK1/Parkin-independent mitophagy pathways, in which a group of LIR-containing receptors are required in response to different stimuli. Mitophagy contributes to the maintenance of a healthy mitochondrial network and the prevention of programmed cell death.', 'Cellular Processes; Transport and catabolism', 'Mitophagy - animal', 'GO: 0000422', 'ko04137', 'list(REFERENCE = \"PMID:25611507\", AUTHORS = \"Pickrell AM, Youle RJ\", TITLE = \"The roles of PINK1, parkin, and mitochondrial fidelity in Parkinson\'s disease.\", JOURNAL = c(\"Neuron 85:257-73 (2015)\", \"DOI:10.1016/j.neuron.2014.12.007\")), list(REFERENCE = \"PMID:25995186\", AUTHORS = \"Durcan TM, Fon EA\", TITLE = \"The three \'P\'s of mitophagy: PARKIN, PINK1, and post-translational modifications.\", JOURNAL = c(\"Genes Dev 29:989-99 (2015)\", \"DOI:10.1101/gad.262758.115\")), list(REFERENCE = \"PMID:25999423\", AUTHORS = \"Shirihai OS, Song M, Dorn GW 2nd\", TITLE = \"How mitochondrial dynamism orchestrates mitophagy.\", JOURNAL = c(\"Circ Res 116:1835-49 (2015)\", \"DOI:10.1161/CIRCRESAHA.116.306374\")), list(REFERENCE = \"PMID:26611876\", AUTHORS = \"Hamacher-Brady A, Brady NR\", TITLE = \"Mitophagy programs: mechanisms and physiological implications of mitochondrial targeting by autophagy.\", JOURNAL = c(\"Cell Mol Life Sci 73:775-95 (2016)\", \"DOI:10.1007/s00018-015-2087-8\")), list(REFERENCE = \"PMID:26882551\", AUTHORS = \"Yamano K, Matsuda N, Tanaka K\", TITLE = \"The ubiquitin signal and autophagy: an orchestrated dance leading to mitochondrial degradation.\", JOURNAL = c(\"EMBO Rep 17:300-16 (2016)\", \"DOI:10.15252/embr.201541486\")), list(REFERENCE = \"PMID:27021519\", AUTHORS = \"Yamaguchi O, Murakawa T, Nishida K, Otsu K\", TITLE = \"Receptor-mediated mitophagy.\", JOURNAL = c(\"J Mol Cell Cardiol 95:50-6 (2016)\", \"DOI:10.1016/j.yjmcc.2016.03.010\")), list(REFERENCE = \"PMID:27443527\", AUTHORS = \"Roberts RF, Tang MY, Fon EA, Durcan TM\", TITLE = \"Defending the mitochondria: The pathways of mitophagy and mitochondrial-derived vesicles.\", JOURNAL = c(\"Int J Biochem Cell Biol 79:427-436 (2016)\", \"DOI:10.1016/j.biocel.2016.07.020\")), list(REFERENCE = \"PMID:27701735\", AUTHORS = \"Arena G, Valente EM\", TITLE = \"PINK1 in the limelight: multiple functions of an eclectic protein in human health and disease.\", JOURNAL = c(\"J Pathol 241:251-263 (2017)\", \"DOI:10.1002/path.4815\"))', 'map04137', 'Mitophagy - animal', 248, 0, 248, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (253, '04138', 'Autophagy - yeast', 'Autophagy is a non-selective and bulk intracellular degradation system of eukaryotic cells and is highly conserved from yeast to human. In this process, the double-membrane vesicle, known as autophagosome, is formed and sequesters organelles or portions of cytosol. The autophagosome is subsequently fused with the vacuole for breakdown by resident hydrolases, and the resulting metabolites are delivered for reuse. In yeast, nutrient withdrawal is the primary stimulus that induces autophagy. Autophagy plays a central role in normal development and cell homeostasis of yeast.', 'Cellular Processes; Transport and catabolism', 'Autophagy - yeast', 'GO: 0006914', 'ko04138', 'list(REFERENCE = \"PMID:11099404\", AUTHORS = \"Klionsky DJ, Emr SD.\", TITLE = \"Autophagy as a regulated pathway of cellular degradation.\", JOURNAL = c(\"Science 290:1717-21 (2000)\", \"DOI:10.1126/science.290.5497.1717\")), list(REFERENCE = \"PMID:15752997\", AUTHORS = \"Thompson AR, Vierstra RD.\", TITLE = \"Autophagic recycling: lessons from yeast help define the process in plants.\", JOURNAL = c(\"Curr Opin Plant Biol 8:165-73 (2005)\", \"DOI:10.1016/j.pbi.2005.01.013\")), list(REFERENCE = \"PMID:15928708\", AUTHORS = \"Lum JJ, DeBerardinis RJ, Thompson CB.\", TITLE = \"Autophagy in metazoans: cell survival in the land of plenty.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 6:439-48 (2005)\", \"DOI:10.1038/nrm1660\")), list(REFERENCE = \"PMID:15978794\", AUTHORS = \"Reggiori F, Klionsky DJ.\", TITLE = \"Autophagosomes: biogenesis from scratch?\", JOURNAL = c(\"Curr Opin Cell Biol 17:415-22 (2005)\", \"DOI:10.1016/j.ceb.2005.06.007\")), list(REFERENC