INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (255, '04140', 'Autophagy - animal', 'Autophagy (or macroautophagy) is a cellular catabolic pathway involving in protein degradation, organelle turnover, and non-selective breakdown of cytoplasmic components, which is evolutionarily conserved among eukaryotes and exquisitely regulated. This progress initiates with production of the autophagosome, a double-membrane intracellular structure of reticular origin that engulfs cytoplasmic contents and ultimately fuses with lysosomes for cargo degradation. Autophagy is regulated in response to extra- or intracellular stress and signals such as starvation, growth factor deprivation and ER stress. Constitutive level of autophagy plays an important role in cellular homeostasis and maintains quality control of essential cellular components.', 'Cellular Processes; Transport and catabolism', 'Autophagy - animal', 'Crohn disease, Inflammatory bowel disease (IBD), Cathepsin D deficiency', 'GO: 0006914', 'ko04140', 'list(REFERENCE = \"PMID:15325584\", AUTHORS = \"Meijer AJ, Codogno P.\", TITLE = \"Regulation and role of autophagy in mammalian cells.\", JOURNAL = c(\"Int J Biochem Cell Biol 36:2445-62 (2004)\", \"DOI:10.1016/j.biocel.2004.02.002\")), list(REFERENCE = \"PMID:16099218\", AUTHORS = \"Deretic V.\", TITLE = \"Autophagy in innate and adaptive immunity.\", JOURNAL = c(\"Trends Immunol 26:523-8 (2005)\", \"DOI:10.1016/j.it.2005.08.003\")), list(REFERENCE = \"PMID:16200202\", AUTHORS = \"Levine B, Yuan J.\", TITLE = \"Autophagy in cell death: an innocent convict?\", JOURNAL = c(\"J Clin Invest 115:2679-88 (2005)\", \"DOI:10.1172/JCI26390\")), list(REFERENCE = \"PMID:15325588\", AUTHORS = \"Tanida I, Ueno T, K