INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (256, '04141', 'Protein processing in endoplasmic reticulum', 'The endoplasmic reticulum (ER) is a subcellular organelle where proteins are folded with the help of lumenal chaperones. Newly synthesized peptides enter the ER via the sec61 pore and are glycosylated. Correctly folded proteins are packaged into transport vesicles that shuttle them to the Golgi complex. Misfolded proteins are retained within the ER lumen in complex with molecular chaperones. Proteins that are terminally misfolded bind to BiP and are directed toward degradation through the proteasome in a process called ER-associated degradation (ERAD). Accumulation of misfolded proteins in the ER causes ER stress and activates a signaling pathway called the unfolded protein response (UPR). In certain severe situations, however, the protective mechanisms activated by the UPR are not sufficient to restore normal ER function and cells die by apoptosis.', 'Genetic Information Processing; Folding, sorting and degradation', 'Protein processing in endoplasmic reticulum', 'Frontotemporal lobar degeneration, Congenital disorders of glycosylation type I, Combined deficiency of factors V and VIII, Leprosy, Type 2 diabetes mellitus, Polycystic liver disease, Distal myopathy, Myofibrillar myopathies, Ichthyosis follicularis, alopecia, and photophobia syndrome, Wolcott-Rallison syndrome, Wolfram syndrome, Congenital dyserythropoietic anemias (CDAs), Chylomicron retention disease, Cranio-lenticulo-sutural dysplasia, Cataract, Marinesco-Sjogren syndrome, Machado-Joseph disease, Cole-Carpenter syndrome, Kufs disease, Deafness, dystonia, and cerebral hypomyelination', 'GO: 0030433 0034976', 'ko04141', 'list(REFERENCE = \"PMID:19491040\", AUTHORS = \"Naidoo N\", TITLE = \"ER and aging-Protein folding and the ER stress response.\", JOURNAL = c(\"Ageing Res Rev 8:150-9 (2009)\", \"DOI:10.1016/j.arr.2009.03.001\")), list(REFERENCE = \"PMID:18023214\", AUTHORS = \"Malhotra JD, Kaufman RJ\", TITLE = \"The endoplasmic reticulum and the unfolded protein response.\", JOURNAL = c(\"Semin Cell Dev Biol 18:716-31 (2007)\", \"DOI:10.1016/j.semcdb.2007.09.003\")), list(REFERENCE = \"PMID:20347046\", AUTHORS = \"Maattanen P, Gehring K, Bergeron JJ, Thomas DY\", TITLE = \"Protein quality control in the ER: the recognition of misfolded proteins.\", JOURNAL = c(\"Semin Cell Dev Biol 21:500-11 (2010)\", \"DOI:10.1016/j.semcdb.2010.03.006\")), list(REFERENCE = \"PMID:20219571\", AUTHORS = \"Stolz A, Wolf DH\", TITLE = \"Endoplasmic reticulum associated protein degradation: a chaperone assisted journey to hell.\", JOURNAL = c(\"Biochim Biophys Acta 1803:694-705 (2010)\", \"DOI:10.1016/j.bbamcr.2010.02.005\")), list(REFERENCE = \"PMID:20112977\", AUTHORS = \"Dejgaard K, Theberge JF, Heath-Engel H, Chevet E, Tremblay ML, Thomas DY\", TITLE = \"Organization of the Sec61 translocon, studied by high resolution native electrophoresis.\", JOURNAL = c(\"J Proteome Res 9:1763-71 (2010)\", \"DOI:10.1021/pr900900x\"))', 'map04141', 531, 0, 248, 105, 128, 50, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (257, '04142', 'Lysosome', 'Lysosomes are membrane-delimited organelles in animal cells serving as the cell\'s main digestive compartment to which all sorts of macromolecules are delivered for degradation. They contain more than 40 hydrolases in an acidic environment (pH of about 5). After synthesis in the ER, lysosomal enzymes are decorated with mannose-6-phosphate residues, which are recognized by mannose-6-phosphate receptors in the trans-Golgi network. They are packaged into clathrin-coated vesicles and are transported to late endosomes. Substances for digestion are acquired by the lysosomes via a series of processes including endocytosis, phagocytosis, and autophagy.', 'Cellular Processes; Transport and catabolism', 'Lysosome', 'Mucopolysaccharidosis type IV, GM2 gangliosidoses, Fabry disease, Gaucher disease, Metachromatic leukodystrophy, Mucopolysaccharidosis type I, Mucopolysaccharidosis type II, Mucopolysaccharidosis type III, Mucopolysaccharidosis type VI, Mucopolysaccharidosis type VII, Mucopolysaccharidosis type IX, Krabbe disease, Niemann-Pick disease type C, Niemann-Pick disease type A/B, Farber lipogranulomatosis, alpha-Mannosidosis, beta-Mannosidosis, Fucosidosis, Sialidosis, Mucolipidosis II, Mucolipidosis IV, Aspartylglucosaminuria, Alpha-N-acetylgalactosaminidase deficiency, Sialuria, Lysosomal acid lipase deficiency, Neuronal ceroid lipofuscinosis, Danon disease, Hermansky-Pudlak syndrome, Hereditary spastic paraplegia, Multiple sulfatase deficiency, Pycnodysostosis, Papillon-Lefevre syndrome, Cystinosis, Galactosialidosis, GM1 gangliosidosis, Mucopolysaccharidosis, Glycoproteinoses, Sphingolipidosis, Defects in the degradation of sphingomyelin, Lysosomal cysteine protease deficiencies, Gangliosidosis, Haim-Munk syndrome, Tropical calcific pancreatitis, Acid phosphatase deficiency, Hypochromic microcytic anemia, Combined SAP deficiency, Lysosomal storage disease, Muscle glycogen storage disease, Glycogen storage disease type II, Prepubertal periodontitis, Mucolipidosis III, MEDNIK syndrome, Batten disease, Kufs disease, Santavuori-Haltia disease, Cathepsin D deficiency', 'GO: 0005764', 'ko04142', 'list(REFERENCE = \"\", AUTHORS = \"Alberts B,  Johnson A, Lewis J, Raff M, Roberts K, Walter P\", TITLE = \"Molecular Biology of the Cell 4th ed.\", JOURNAL = \"Garland Science (2002)\"), list(REFERENCE = \"PMID:12628346\", AUTHORS = \"Eskelinen EL, Tanaka Y, Saftig P\", TITLE = \"At the acidic edge: emerging functions for lysosomal membrane proteins.\", JOURNAL = c(\"Trends Cell Biol 13:137-45 (2003)\", \"DOI:10.1016/S0962-8924(03)00005-9\")), list(REFERENCE = \"PMID:1883197\", AUTHORS = \"Neufeld EF\", TITLE = \"Lysosomal storage diseases.\", JOURNAL = c(\"Annu Rev Biochem 60:257-80 (1991)\", \"DOI:10.1146/annurev.bi.60.070191.001353\")), list(REFERENCE = \"PMID:2943218\", AUTHORS = \"von Figura K, Hasilik A\", TITLE = \"Lysosomal enzymes and their receptors.\", JOURNAL = c(\"Annu Rev Biochem 55:167-93 (1986)\", \"DOI:10.1146/annurev.bi.55.070186.001123\")), list(REFERENCE = \"PMID:15987739\", AUTHORS = \"Janvier K, Bonifacino JS\", TITLE = \"Role of the endocytic machinery in the sorting of lysosome-associated membrane proteins.\", JOURNAL = c(\"Mol Biol Cell 16:4231-42 (2005)\", \"DOI:10.1091/mbc.E05-03-0213\")), list(REFERENCE = \"PMID:18635948\", AUTHORS = \"Vergarajauregui S, Puertollano R\", TITLE = \"Mucolipidosis type IV: the importance of functional lysosomes for efficient autophagy.\", JOURNAL = c(\"Autophagy 4:832-4 (2008)\", \"DOI:10.4161/auto.6567\"))', 'map04142', 248, 0, 248, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (258, '04144', 'Endocytosis', 'Endocytosis is a mechanism for cells to remove ligands, nutrients, and plasma membrane (PM) proteins, and lipids from the cell surface, bringing them into the cell interior. Transmembrane proteins entering through clathrin-dependent endocytosis (CDE) have sequences in their cytoplasmic domains that bind to the APs (adaptor-related protein complexes) and enable their rapid removal from the PM. In addition to APs and clathrin, there are numerous accessory proteins including dynamin. Depending on the various proteins that enter the endosome membrane, these cargoes are sorted to distinct destinations. Some cargoes, such as nutrient receptors, are recycled back to the PM. Ubiquitylated membrane proteins, such as activated growth-factor receptors, are sorted into intraluminal vesicles and eventually end up in the lysosome lumen via multivesicular endosomes (MVEs). There are distinct mechanisms of clathrin-independent endocytosis (CIE) depending upon the cargo and the cell type.', 'Cellular Processes; Transport and catabolism', 'Endocytosis', 'Frontotemporal lobar degeneration, Familial hypercholesterolemia, Hereditary sensory and autonomic neuropathy, Hereditary spastic paraplegia, Limb-girdle muscular dystrophy 1C, Limb-girdle muscular dystrophy, Lacrimo-auriculo-dento-digital syndrome, Centronuclear myopathy, Loeys-Dietz syndrome, Syndromic multisystem autoimmune disease, Neurodegeneration due to cerebral folate transport deficiency, Hyperlipoproteinemia type IIa, Wiskott-Aldrich syndrome, Familial autosomal recessive hypercholesterolemia, Rippling muscle disease, Idiopathic hyperCKemia, Distal myopathy, Tateyama type, CBL syndrome', 'GO: 0006897', 'ko04144', 'list(REFERENCE = \"PMID:19007773\", AUTHORS = \"Acconcia F, Sigismund S, Polo S\", TITLE = \"Ubiquitin in trafficking: the network at work.\", JOURNAL = c(\"Exp Cell Res 315:1610-8 (2009)\", \"DOI:10.1016/j.yexcr.2008.10.014\")), list(REFERENCE = \"PMID:18647649\", AUTHORS = \"Donaldson JG, Porat-Shliom N, Cohen LA\", TITLE = \"Clathrin-independent endocytosis: a unique platform for cell signaling and PM remodeling.\", JOURNAL = c(\"Cell Signal 21:1-6 (2009)\", \"DOI:10.1016/j.cellsig.2008.06.020\")), list(REFERENCE = \"PMID:15261671\", AUTHORS = \"Sorkin A\", TITLE = \"Cargo recognition during clathrin-mediated endocytosis: a team effort.\", JOURNAL = c(\"Curr Opin Cell Biol 16:392-9 (2004)\", \"DOI:10.1016/j.ceb.2004.06.001\")), list(REFERENCE = \"PMID:19325624\", AUTHORS = \"Raiborg C, Stenmark H\", TITLE = \"The ESCRT machinery in endosomal sorting of ubiquitylated membrane proteins.\", JOURNAL = c(\"Nature 458:445-52 (2009)\", \"DOI:10.1038/nature07961\")), list(REFERENCE = \"PMID:20110990\", AUTHORS = \"Scita G, Di Fiore PP\", TITLE = \"The endocytic matrix.\", JOURNAL = c(\"Nature 463:464-73 (2010)\", \"DOI:10.1038/nature08910\")), list(REFERENCE = \"PMID:15687999\", AUTHORS = \"Le Roy C, Wrana JL\", TITLE = \"Clathrin- and non-clathrin-mediated endocytic regulation of cell signalling.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 6:112-26 (2005)\", \"DOI:10.1038/nrm1571\")), list(REFERENCE = \"PMID:19924646\", AUTHORS = \"Jovic M, Sharma M, Rahajeng J, Caplan S\", TITLE = \"The early endosome: a busy sorting station for proteins at the crossroads.\", JOURNAL = c(\"Histol Histopathol 25:99-112 (2010)\", \"DOI:10.14670/HH-25.99\")), list(REFERENCE = \"PMID:16203612\", AUTHORS = \"Pietiainen VM, Marjomaki V, Heino J, Hyypia T\", TITLE = \"Viral entry, lipid rafts and caveosomes.\", JOURNAL = c(\"Ann Med 37:394-403 (2005)\", \"DOI:10.1080/07853890510011976\")), list(REFERENCE = \"PMID:19696797\", AUTHORS = \"Grant BD, Donaldson JG