INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (267, '04213', 'Longevity regulating pathway - multiple species', 'Aging is a complex process of accumulation of molecular, cellular, and organ damage, leading to loss of function and increased vulnerability to disease and death. Despite the complexity of aging, recent work has shown that dietary restriction (DR) and genetic down-regulation of nutrient-sensing pathways, namely IIS (insulin/insulin-like growth factor signalling) and TOR (target-of- rapamycin) can substantially increase healthy life span of laboratory model organisms. These nutrient signaling pathways are conserved in various organisms. In worms, flies, and mammals, DR reduces signalling through IIS/TOR pathways, deactivating the PI3K/Akt/TOR intracellular signalling cascade and consequently activating the antiaging FOXO family transcription factor(s). In yeast, the effects of DR on life- span extension are associated with reduced activities of the TOR/Sch9 and Ras/PKA pathways and require the serine-threonine kinase Rim15 and transcription factors Gis1 and Msn2/4. These transcription factors (FOXO, DAF-16, Gis1, and Msn2/4) transactivate genes involved in resistance to oxidative stress, energy metabolism, DNA damage repair, glucose metabolism, autophagy and protection of proteins by chaperones.', 'Organismal Systems; Aging', 'Longevity regulating pathway - multiple species', 'ko04213', 'list(REFERENCE = \"PMID:20395504\", AUTHORS = \"Fontana L, Partridge L, Longo VD\", TITLE = \"Extending healthy life span--from yeast to humans.\", JOURNAL = c(\"Science 328:321-6 (2010)\", \"DOI:10.1126/science.1172539\")), list(REFERENCE = \"PMID:20098959\", AUTHORS = \"Parrella E, Longo VD\", TITLE = \"Insulin/IGF-I and related signaling pathways regulate aging in nondividing cells: from yeast to the mammalian brain.\", JOURNAL = c(\"ScientificWorldJournal 10:161-77 (2010)\", \"DOI:10.1100/tsw.2010.8\")), list(REFERENCE = \"PMID:26682004\", AUTHORS = \"Santos J, Leitao-Correia F, Sousa MJ, Leao C\", TITLE = \"Dietary Restriction and Nutrient Balance in Aging.\", JOURNAL = c(\"Oxid Med Cell Longev 2016:4010357 (2016)\", \"DOI:10.1155/2016/4010357\")), list(REFERENCE = \"PMID:18946478\", AUTHORS = \"Longo VD, Lieber MR, Vijg J\", TITLE = \"Turning anti-ageing genes against cancer.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 9:903-10 (2008)\", \"DOI:10.1038/nrm2526\")), list(REFERENCE = \"PMID:16304601\", AUTHORS = \"Longo VD, Mitteldorf J, Skulachev VP\", TITLE = \"Programmed and altruistic ageing.\", JOURNAL = c(\"Nat Rev Genet 6:866-72 (2005)\", \"DOI:10.1038/nrg1706\")), list(REFERENCE = \"PMID:12610293\", AUTHORS = \"Longo VD, Finch CE\", TITLE = \"Evolutionary medicine: from dwarf model systems to healthy centenarians?\", JOURNAL = c(\"Science 299:1342-6 (2003)\", \"DOI:10.1126/science.1077991\")), list(REFERENCE = \"PMID:25153840\", AUTHORS = \"Mirzaei H, Suarez JA, Longo VD\", TITLE = \"Protein and amino acid restriction, aging and disease: from yeast to humans.\", JOURNAL = c(\"Trends Endocrinol Metab 25:558-66 (2014)\", \"DOI:10.1016/j.tem.2014.07.002\")), list(REFERENCE = \"PMID:20336132\", AUTHORS = \"Kenyon CJ\", TITLE = \"The genetics of ageing.\", JOURNAL = c(\"Nature 464:504-12 (2010)\", \"DOI:10.1038/nature08980\")), list(REFERENCE = \"PMID:20097433\", AUTHORS = \"Longo VD, Fontana L\", TITLE = \"Calorie restriction and cancer prevention: metabolic and molecular mechanisms.\", JOURNAL = c(\"Trends Pharmacol Sci 31:89-98 (2010)\", \"DOI:10.1016/j.tips.2009.11.004\")), list(REFERENCE = \"PMID:22094419\", AUTHORS = \"Longo VD, Fabrizio P\", TITLE = \"Chronological aging in Saccharomyces cerevisiae.\", JOURNAL = c(\"Subcell Biochem 57:101-21 (2012)\", \"