INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (272, '04218', 'Cellular senescence', 'Cellular senescence is a state of irreversible cellular arrest and can be triggered by a number of factors, such as telomere shortening, oncogene activation, irradiation, DNA damage and oxidative stress. It is characterized by enlarged flattened morphology, senescence-associated beta-galactosidase (SA-b-gal) activity, secretion of inflammatory cytokines, growth factors and matrix metalloproteinases, as part of the senescence-associated secretory phenotype (SASP). Cellular senescence is functionally associated with many biological processes including aging, tumor suppression, placental biology, embryonic development, and wound healing.', 'Cellular Processes; Cell growth and death', 'Cellular senescence', 'Ataxia telangiectasia', 'GO: 0090398', 'ko04218', 'list(REFERENCE = \"PMID:24954210\", AUTHORS = \"Munoz-Espin D, Serrano M\", TITLE = \"Cellular senescence: from physiology to pathology.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 15:482-96 (2014)\", \"DOI:10.1038/nrm3823\")), list(REFERENCE = \"PMID:26646499\", AUTHORS = \"Childs BG, Durik M, Baker DJ, van Deursen JM\", TITLE = \"Cellular senescence in aging and age-related disease: from mechanisms to therapy.\", JOURNAL = c(\"Nat Med 21:1424-35 (2015)\", \"DOI:10.1038/nm.4000\")), list(REFERENCE = \"PMID:26105537\", AUTHORS = \"Sharpless NE, Sherr CJ\", TITLE = \"Forging a signature of in vivo senescence.\", JOURNAL = c(\"Nat Rev Cancer 15:397-408 (2015)\", \"DOI:10.1038/nrc3960\")), list(REFERENCE = \"PMID:26435346\", AUTHORS = \"Fridlyanskaya I, Alekseenko L, Nikolsky N\", TITLE = \"Senescence as a general cellular response to stress: A mini-review.\", JOURNAL 