INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (273, '04260', 'Cardiac muscle contraction', 'Contraction of the heart is a complex process initiated by the electrical excitation of cardiac myocytes (excitation-contraction coupling, ECC). In cardiac myocytes, Ca2+ influx induced by activation of voltage-dependent L-type Ca channels (DHP receptors) upon membrane depolarization triggers the release of Ca2+ via Ca2+ release channels (ryanodine receptors) of sarcoplasmic reticulum (SR) through a Ca2+ -induced Ca release (CICR) mechanism. Ca2+ ions released via the CICR mechanism diffuse through the cytosolic space to contractile proteins to bind to troponinC resulting in the release of inhibition induced by troponinI. The Ca2+ binding to troponinC thereby triggers the sliding of thin and thick filaments, that is, the activation of a crossbridge and subsequent cardiac force development and/or cell shortening. Recovery occurs as Ca2+ is pumped out of the cell by the Na+/Ca2+ exchanger (NCX) or is returned to the sarcoplasmic reticulum (SR) by sarco(endo)plasmic Ca2+ -ATPase (SERCA) pumps on the non-junctional region of the SR.', 'Organismal Systems; Circulatory system', 'Cardiac muscle contraction', 'Distal myopathy, Scapuloperoneal myopathy, Nemaline myopathy, Congenital fiber type disproportion, Cap myopathy, Myosin storage myopathy, Brugada syndrome, Hypokalemic periodic paralysis (HypoPP), Hemiplegic migraine, Distal arthrogryposis, Primary dystonia, Catecholaminergic polymorphic ventricular tachycardia, Left ventricular noncompaction, Restrictive cardiomyopathy, Kearns-Sayre syndrome, Cytochrome c oxidase (COX) deficiency, Mitochondrial complex III deficiency', 'GO: 0060048', 'ko04260', 'list(REFERENCE = \"PMID:17988210\", AUTHORS = \"Bers DM\", TITLE = \"Calcium cycling and signaling in cardiac myocytes.\", JOURNAL = c(\"Annu Rev Physiol 70:23-49 (2008)\", \"DOI:10.1146/annurev.physiol.70.113006.100455\")), list(REFERENCE = \"PMID:11805843\", AUTHORS = \"Bers DM\", TITLE = \"Cardiac excitation-contraction coupling.\", JOURNAL = c(\"Nature 415:198-205 (2002)\", \"DOI:10.1038/415198a\")), list(REFERENCE = \"PMID:16799263\", AUTHORS = \"Endoh M\", TITLE = \"Signal transduction and Ca2+ signaling in intact myocardium.\", JOURNAL = c(\"J Pharmacol Sci 100:525-37 (2006)\", \"DOI:10.1254/jphs.CPJ06009X\")), list(REFERENCE = \"PMID:18981594\", AUTHORS = \"Endoh M\", TITLE = \"Cardiac Ca2+ signaling and Ca2+ sensitizers.\", JOURNAL = c(\"Circ J 72:1915-25 (2008)\", \"DOI:10.1253/circj.CJ-08-0838\")), list(REFERENCE = \"PMID:16552170\", AUTHORS = \"Bers DM, Despa S\", TITLE = \"Cardiac myocytes Ca2+ and Na+ regulation in normal and failing hearts.\", JOURNAL = c(\"J Pharmacol Sci 100:315-22 (2006)\", \"DOI:10.1254/jphs.CPJ06001X\")), list(REFERENCE = \"PMID:10747208\", AUTHORS = \"Gordon AM, Homsher E, Regnier M\", TITLE = \"Regulation of contraction in striated muscle.\", JOURNAL = c(\"Physiol Rev 80:853-924 (2000)\", \"DOI:10.1152/physrev.2000.80.2.853\")), list(REFERENCE = \"PMID:16701909\", AUTHORS = \"Phrommintikul A, Chattipakorn N\", TITLE = \"Roles of cardiac ryanodine receptor in heart failure and sudden cardiac death.\", JOURNAL = c(\"Int J Cardiol 112:142-52 (2006)\", \"DOI:10.1016/j.ijcard.2005.11.106\")), list(REFERENCE = \"PMID:16096350\", AUTHORS = \"Lacinova L\", TITLE = \"Voltage-dependent calcium channels.\", JOURNAL = \"Gen Physiol Biophys 24 Suppl 1:1-78 (2005)\"), list(REFERENCE = \"PMID:12763850\", AUTHORS = \"Lingrel J, Moseley A, Dostanic I, Cougnon M, He S, James P, Woo A, O\'Connor K, Neumann J\", TITLE = \"Functional roles of the alpha isoforms of the Na,K-ATPase.\", JOURNAL = c(\"Ann N Y Acad Sci 986:354-9 (2003)\", \"DOI:10.1111/j.1749-6632.2003.tb07214.x\")), list(REFERENCE = \"PMID:11294886\", AUTHORS = \"Berg JS, Powell BC, Cheney RE\", TITLE = \"A millennial myosin census.\", JOURNAL = c(\"Mol Biol Cell 12:780-94 (2001)\", \"DOI:10.1091/mbc.12.4.780\")), list(REFERENCE = \"PMID:15736582\", AUTHORS = \"Parmacek MS, Solaro RJ\", TITLE = \"Biology of the troponin complex in cardiac myocytes.\", JOURNAL = c(\"Prog Cardiovasc Dis 47:159-76 (2004)\", \"DOI:10.1016/j.pcad.2004.07.003\")), list(REFERENCE = \"PMID:14659803\", AUTHORS = \"Yamashita H, Sugiura S, Fujita H, Yasuda S, Nagai R, Saeki Y, Sunagawa K, Sugi H\", TITLE = \"Myosin light chain isoforms modify force-generating ability of cardiac myosin by changing the kinetics of actin-myosin interaction.\", JOURNAL = c(\"Cardiovasc Res 60:580-8 (2003)\", \"DOI:10.1016/j.cardiores.2003.09.011\")), list(REFERENCE = \"PMID:19403607\", AUTHORS = \"Pritchard TJ, Kranias EG\", TITLE = \"Junctin and the histidine-rich Ca2+ binding protein: potential roles in heart failure and arrhythmogenesis.\", JOURNAL = c(\"J Physiol 587:3125-33 (2009)\", \"DOI:10.1113/jphysiol.2009.172171\")), list(REFERENCE = \"PMID:28596175\", AUTHORS = \"Landstrom AP, Dobrev D, Wehrens XHT\", TITLE = \"Calcium Signaling and Cardiac Arrhythmias.\", JOURNAL = c(\"Circ Res 120:1969-1993 (2017)\", \"DOI:10.1161/CIRCRESAHA.117.310083\"))', 'map04260', 145, 0, 145, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (274, '04261', 'Adrenergic signaling in cardiomyocytes', 'Cardiac myocytes express at least six subtypes of adrenergic receptor (AR) which include three subtypes of beta-AR (beta-1, beta-2, beta-3) and three subtypes of the alpha-1-AR (alpha-1A, alpha-1B, and alpha-1C). In the human heart the beta-1-AR is the pre- dominate receptor. Acute sympathetic stimulation of cardiac beta-1-ARs induces positive inotropic and chronotropic effects, the most effective mechanism to acutely increase output of the heart, by coupling to Gs, formation of cAMP by adenylyl cyclase (AC), and PKA- dependent phosphorylation of various target proteins (e.g., ryanodine receptor [RyR]; phospholamban [PLB], troponin I [TnI], and the L-type Ca2+ channel [LTCC]). Chronic beta-1-AR stimulation is detrimental and induces cardiomyocyte hypertrophy and apoptosis. beta-2-AR coupled to Gs exerts a proapoptotic action as well as beta-1-AR, while beta-2-AR coupled to Gi exerts an antiapoptotic action.', 'Organismal Systems; Circulatory system', 'Adrenergic signaling in cardiomyocytes', 'Long QT syndrome, Atrial fibrillation, Autosomal dominant mental retardation, Catecholaminergic polymorphic ventricular tachycardia, Jervell and Lange-Nielsen syndrome', 'GO: 0001997', 'ko04261', 'list(REFERENCE = \"PMID:19883653\", AUTHORS = \"Grimm M, Brown JH\", TITLE = \"Beta-adrenergic receptor signaling in the heart: role of CaMKII.\", JOURNAL = c(\"J Mol Cell Cardiol 48:322-30 (2010)\", \"DOI:10.1016/j.yjmcc.2009.10.016\")), list(REFERENCE = \"PMID:21212391\", AUTHORS = \"Yang JH, Saucerman JJ\", TITLE = \"Computational models reduce complexity and accelerate insight into cardiac signaling network