FERENCE = \"PMID:12967557\", AUTHORS = \"Veeman MT, Axelrod JD, Moon RT.\", TITLE = \"A second canon. Functions and mechanisms of beta-catenin-independent Wnt signaling.\", JOURNAL = c(\"Dev Cell 5:367-77 (2003)\", \"DOI:10.1016/S1534-5807(03)00266-1\")), list(REFERENCE = \"PMID:11433294\", AUTHORS = \"Sun TQ, Lu B, Feng JJ, Reinhard C, Jan YN, Fantl WJ, Williams LT.\", TITLE = \"PAR-1 is a Dishevelled-associated kinase and a positive regulator of Wnt signalling.\", JOURNAL = c(\"Nat Cell Biol 3:628-36 (2001)\", \"DOI:10.1038/35083016\")), list(REFERENCE = \"PMID:23256519\", AUTHORS = \"Saito-Diaz K, Chen TW, Wang X, Thorne CA, Wallace HA, Page-McCaw A, Lee E\", TITLE = \"The way Wnt works: Components and mechanism.\", JOURNAL = c(\"Growth Factors 31:1-31 (2013)\", \"DOI:10.3109/08977194.2012.752737\")), list(REFERENCE = \"PMID:19482104\", AUTHORS = \"Carethers JM\", TITLE = \"Intersection of transforming growth factor-beta and Wnt signaling pathways in colorectal cancer and metastasis.\", JOURNAL = c(\"Gastroenterology 137:33-6 (2009)\", \"DOI:10.1053/j.gastro.2009.05.011\")), list(REFERENCE = \"PMID:18838381\", AUTHORS = \"Lin Z, Gao C, Ning Y, He X, Wu W, Chen YG\", TITLE = \"The pseudoreceptor BMP and activin membrane-bound inhibitor positively modulates Wnt/beta-catenin signaling.\", JOURNAL = c(\"J Biol Chem 283:33053-8 (2008)\", \"DOI:10.1074/jbc.M804039200\")), list(REFERENCE = \"PMID:23151663\", AUTHORS = \"Niehrs C\", TITLE = \"The complex world of WNT receptor signalling.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 13:767-79 (2012)\", \"DOI:10.1038/nrm3470\")), list(REFERENCE = \"PMID:28522145\", AUTHORS = \"Abou Ziki MD, Mani A\", TITLE = \"Wnt signaling, a novel pathway regulating blood pressure? State of the art review.\", JOURNAL = c(\"Atherosclerosis 262:171-178 (2017)\", \"DOI:10.1016/j.atherosclerosis.2017.05.001\")), list(REFERENCE = \"PMID:25808920\", AUTHORS = \"He Y, Ki H, Kim H, Kim K\", TITLE = \"delta-Catenin interacts with LEF-1 and negatively r