INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (280, '04341', 'Hedgehog signaling pathway - fly', 'Hedgehog (Hh) signaling plays essential roles in embryonic development and adult tissue homeostasis. It is first elucidated by genetic studies in Drosophila and the core of the Hh signaling pathway is conserved in evolution. The Hh signaling components include an important complex composed of Cos2, Fu, SuFu and Ci. In the absence of Hh, Ptc inhibits Smo and the complex promotes the formation of the Ci\'s repressor form CiR. In the presentation of Hh, binding Hh to Ptc relieves Smo repression, which results in Ci\'s activation and then the increased transcription of Hh target genes in the nucleus.', 'Environmental Information Processing; Signal transduction', 'Hedgehog signaling pathway - fly', 'ko04341', 'list(REFERENCE = \"PMID:20530542\", AUTHORS = \"Wilson CW, Chuang PT\", TITLE = \"Mechanism and evolution of cytosolic Hedgehog signal transduction.\", JOURNAL = c(\"Development 137:2079-94 (2010)\", \"DOI:10.1242/dev.045021\")), list(REFERENCE = \"PMID:23719536\", AUTHORS = \"Briscoe J, Therond PP\", TITLE = \"The mechanisms of Hedgehog signalling and its roles in development and disease.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 14:416-29 (2013)\", \"DOI:10.1038/nrm3598\")), list(REFERENCE = \"PMID:18379584\", AUTHORS = \"Aikin RA, Ayers KL, Therond PP\", TITLE = \"The role of kinases in the Hedgehog signalling pathway.\", JOURNAL = c(\"EMBO Rep 9:330-6 (2008)\", \"DOI:10.1038/embor.2008.38\")), list(REFERENCE = \"PMID:21502959\", AUTHORS = \"Ingham PW, Nakano Y, Seger C\", TITLE = \"Mechanisms and functions of Hedgehog signalling across the metazoa.\", JOURNAL = c(\"Nat Rev Genet 12:393-406 (2011)