INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (295, '04540', 'Gap junction', 'Gap junctions contain intercellular channels that allow direct communication between the cytosolic compartments of adjacent cells. Each gap junction channel is formed by docking of two \'hemichannels\', each containing six connexins, contributed by each neighboring cell. These channels permit the direct transfer of small molecules including ions, amino acids, nucleotides, second messengers and other metabolites between adjacent cells. Gap junctional communication is essential for many physiological events, including embryonic development, electrical coupling, metabolic transport, apoptosis, and tissue homeostasis. Communication through Gap Junction is sensitive to a variety of stimuli, including changes in the level of intracellular Ca2+, pH, transjunctional applied voltage and phosphorylation/dephosphorylation processes. This figure represents the possible activation routes of different protein kinases involved in Cx43 and Cx36 phosphorylation.', 'Cellular Processes; Cellular community - eukaryotes', 'Gap junction', 'Congenital fibrosis of the extraocular muscles, Syndactyly, Hypomagnesemia, Hypoplastic left heart syndrome', 'GO: 0005921', 'ko04540', 'list(REFERENCE = \"PMID:14506308\", AUTHORS = \"Saez JC, Berthoud VM, Branes MC, Martinez AD, Beyer EC.\", TITLE = \"Plasma membrane channels formed by connexins: their regulation and functions.\", JOURNAL = c(\"Physiol Rev 83:1359-400 (2003)\", \"DOI:10.1152/physrev.00007.2003\")), list(REFERENCE = \"PMID:15033580\", AUTHORS = \"Warn-Cramer BJ, Lau AF.\", TITLE = \"Regulation of gap junctions by tyrosine protein kinases.\", JOURNAL = c(\"Biochim Biophys Acta 1662:81-95