INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (303, '04622', 'RIG-I-like receptor signaling pathway', 'Specific families of pattern recognition receptors are responsible for detecting viral pathogens and generating innate immune responses. Non-self RNA appearing in a cell as a result of intracellular viral replication is recognized by a family of cytosolic RNA helicases termed RIG-I-like receptors (RLRs). The RLR proteins include RIG-I, MDA5, and LGP2 and are expressed in both immune and nonimmune cells. Upon recognition of viral nucleic acids, RLRs recruit specific intracellular adaptor proteins to initiate signaling pathways that lead to the synthesis of type I interferon and other inflammatory cytokines, which are important for eliminating viruses.', 'Organismal Systems; Immune system', 'RIG-I-like receptor signaling pathway', 'Aicardi-Goutieres syndrome, Brooke-Spiegler syndrome, Cocoon syndrome, STING-associated vasculopathy with onset in infancy', 'ko04622', 'list(REFERENCE = \"PMID:15208624\", AUTHORS = \"Yoneyama M, Kikuchi M, Natsukawa T, Shinobu N, Imaizumi T, Miyagishi M, Taira K, Akira S, Fujita T\", TITLE = \"The RNA helicase RIG-I has an essential function in double-stranded RNA-induced innate antiviral responses.\", JOURNAL = c(\"Nat Immunol 5:730-7 (2004)\", \"DOI:10.1038/ni1087\")), list(REFERENCE = \"PMID:18272355\", AUTHORS = \"Takeuchi O, Akira S\", TITLE = \"MDA5/RIG-I and virus recognition.\", JOURNAL = c(\"Curr Opin Immunol 20:17-22 (2008)\", \"DOI:10.1016/j.coi.2008.01.002\")), list(REFERENCE = \"PMID:19120475\", AUTHORS = \"Yoneyama M, Fujita T\", TITLE = \"RNA recognition and signal transduction by RIG-I-like receptors.\", JOURNAL = c(\"Immunol Rev 227:54-65 (2009)\", 