INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (305, '04624', 'Toll and Imd signaling pathway', 'Insects, such as Drosophila, have evolved complex mechanisms to combat microbial infection. TOLL and IMD pathways are responsible for the immune response that functions through the production of antimicrobial peptides (AMPs). The TOLL pathway mainly detects fungi, Gram-positive bacteria and virulence factors (proteases et al.). Activation of the TOLL pathway results in degradation of Cactus and localization of Dorsal and Dif to the nucleus. On the other hand, the IMD pathway responses to Gram-negative bacteria and leads to the activation of Relish. Dorsal, Dif and Relish induce the transcriptional up-regulation of AMPs in the nucleus.', 'Organismal Systems; Immune system', 'Toll and Imd signaling pathway', 'ko04624', 'list(REFERENCE = \"PMID:22824741\", AUTHORS = \"Merkling SH, van Rij RP\", TITLE = \"Beyond RNAi: antiviral defense strategies in Drosophila and mosquito.\", JOURNAL = c(\"J Insect Physiol 59:159-70 (2013)\", \"DOI:10.1016/j.jinsphys.2012.07.004\")), list(REFERENCE = \"PMID:21209287\", AUTHORS = \"Valanne S, Wang JH, Ramet M\", TITLE = \"The Drosophila Toll signaling pathway.\", JOURNAL = c(\"J Immunol 186:649-56 (2011)\", \"DOI:10.4049/jimmunol.1002302\")), list(REFERENCE = \"PMID:21625362\", AUTHORS = \"Silverman N, Paquette N, Aggarwal K\", TITLE = \"Specificity and signaling in the Drosophila immune response.\", JOURNAL = \"Invertebrate Surviv J 6:163-174 (2009)\"), list(REFERENCE = \"PMID:22076558\", AUTHORS = \"Royet J, Gupta D, Dziarski R\", TITLE = \"Peptidoglycan recognition proteins: modulators of the microbiome and inflammation.\", JOURNAL = c(\"Nat Rev Immunol 11:837-51 (201