INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (306, '04625', 'C-type lectin receptor signaling pathway', 'C-type lectin receptors (CLRs) are a large superfamily of proteins characterized by the presence of one or more C-type lectin-like domains (CTLDs). CLRs function as pattern-recognition receptors (PRRs) for pathogen-derived ligands in dendric cells, macrophages, neutrophils, etc., such as Dectin-1 and Dectin-2 for recognition of fungi-derived B-glucan and high mannose-type carbohydrates. Upon ligand binding, CLRs stimulate intracellular signaling cascades that induce the production of inflammatory cytokines and chemokines, consequently triggering innate and adaptive immunity to pathogens.', 'Organismal Systems; Immune system', 'C-type lectin receptor signaling pathway', 'ko04625', 'list(REFERENCE = \"PMID:27291962\", AUTHORS = \"Geijtenbeek TB, Gringhuis SI\", TITLE = \"C-type lectin receptors in the control of T helper cell differentiation.\", JOURNAL = c(\"Nat Rev Immunol 16:433-48 (2016)\", \"DOI:10.1038/nri.2016.55\")), list(REFERENCE = \"PMID:21350579\", AUTHORS = \"Hajishengallis G, Lambris JD\", TITLE = \"Microbial manipulation of receptor crosstalk in innate immunity.\", JOURNAL = c(\"Nat Rev Immunol 11:187-200 (2011)\", \"DOI:10.1038/nri2918\")), list(REFERENCE = \"PMID:28604735\", AUTHORS = \"Iliev ID, Leonardi I\", TITLE = \"Fungal dysbiosis: immunity and interactions at mucosal barriers.\", JOURNAL = c(\"Nat Rev Immunol 17:635-646 (2017)\", \"DOI:10.1038/nri.2017.55\")), list(REFERENCE = \"PMID:25002863\", AUTHORS = \"Richardson MB, Williams SJ\", TITLE = \"MCL and Mincle: C-Type Lectin Receptors That Sense Damaged Self and Pathogen-Associated Molecular Patterns.\", JOURNAL = c(\