INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (309, '04640', 'Hematopoietic cell lineage', 'Blood-cell development progresses from a hematopoietic stem cell (HSC), which can undergo either self-renewal or differentiation into a multilineage committed progenitor cell: a common lymphoid progenitor (CLP) or a common myeloid progenitor (CMP). A CLP gives rise to the lymphoid lineage of white blood cells or leukocytes-the natural killer (NK) cells and the T and B lymphocytes. A CMP gives rise to the myeloid lineage, which comprises the rest of the leukocytes, the erythrocytes (red blood cells), and the megakaryocytes that produce platelets important in blood clotting. Cells undergoing these differentiation process express a stage- and lineage-specific set of surface markers. Therefore cellular stages are identified by the specific expression patterns of these genes.', 'Organismal Systems; Immune system', 'Hematopoietic cell lineage', 'Hemophilia, Bernard-Soulier syndrome, Castleman disease', 'ko04640', 'list(REFERENCE = \"\", AUTHORS = \"Janeway CA Jr, Travers P, Walport M, Shlomchik MJ\", TITLE = \"Immunobiology - International editon: The immune system in health and disease\", JOURNAL = \"Harcourt Publishers Ltd, a subsidiary of Harcourt International Ltd (2001)\"), list(REFERENCE = \"(Japanese)\", AUTHORS = \"Abe T\", TITLE = \"[Atlas of malignancies in hematopoietic organ - Morphology, immunology, chromosome and gene] (Japanese)\", JOURNAL = \"Nihonijishinhousya (2000)\"), list(REFERENCE = \"PMID:15771582\", AUTHORS = \"Rothenberg EV, Taghon T.\", TITLE = \"Molecular genetics of T cell development.\", JOURNAL = c(\"Annu Rev Immunol 23:601-49 (2005)\", \"DOI:10.1146/annurev.immunol.23.021704.1157