INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (318, '04668', 'TNF signaling pathway', 'Tumor necrosis factor (TNF), as a critical cytokine, can induce a wide range of intracellular signal pathways including apoptosis and cell survival as well as inflammation and immunity. Activated TNF is assembled to a homotrimer and binds to its receptors (TNFR1, TNFR2) resulting in the trimerization of TNFR1 or TNFR2. TNFR1 is expressed by nearly all cells and is the major receptor for TNF (also called TNF-alpha). In contrast, TNFR2 is expressed in limited cells such as CD4 and CD8 T lymphocytes, endothelial cells, microglia, oligodendrocytes, neuron subtypes, cardiac myocytes, thymocytes and human mesenchymal stem cells. It is the receptor for both TNF and LTA (also called TNF-beta). Upon binding of the ligand, TNFR mediates the association of some adaptor proteins such as TRADD or TRAF2, which in turn initiate recruitment of signal transducers. TNFR1 signaling induces activation of many genes, primarily controlled by two distinct pathways, NF-kappa B pathway and the MAPK cascade, or apoptosis and necroptosis. TNFR2 signaling activates NF-kappa B pathway including PI3K-dependent NF-kappa B pathway and JNK pathway leading to survival.', 'Environmental Information Processing; Signal transduction', 'TNF signaling pathway', 'Encephalopathy due to defective mitochondrial and peroxisomal fission', 'GO: 0033209', 'ko04668', 'list(REFERENCE = \"PMID:23085193\", AUTHORS = \"Chu WM\", TITLE = \"Tumor necrosis factor.\", JOURNAL = c(\"Cancer Lett 328:222-5 (2013)\", \"DOI:10.1016/j.canlet.2012.10.014\")), list(REFERENCE = \"PMID:23531747\", AUTHORS = \"Shuh M, Bohorquez H, Loss GE Jr, Cohen AJ\", TITLE = \"Tumor Necrosis Factor-alpha: Life and Death of Hepatocytes During Liver Ischemia/Reperfusion Injury.\", JOURNAL = \"Ochsner J 13:119-30 (2013)\"), list(REFERENCE = \"PMID:11897488\", AUTHORS = \"MacEwan DJ\", TITLE = \"TNF receptor subtype signalling: differences and cellular consequences.\", JOURNAL = c(\"Cell Signal 14:477-92 (2002)\", \"DOI:10.1016/S0898-6568(01)00262-5\")), list(REFERENCE = \"PMID:22296764\", AUTHORS = \"Newton K, Dixit VM\", TITLE = \"Signaling in innate immunity and inflammation.\", JOURNAL = c(\"Cold Spring Harb Perspect Biol 4:a006049 (2012)\", \"DOI:10.1101/cshperspect.a006049\")), list(REFERENCE = \"PMID:10581243\", AUTHORS = \"Pomerantz JL, Baltimore D\", TITLE = \"NF-kappaB activation by a signaling complex containing TRAF2, TANK and TBK1, a novel IKK-related kinase.\", JOURNAL = c(\"EMBO J 18:6694-704 (1999)\", \"DOI:10.1093/emboj/18.23.6694\")), list(REFERENCE = \"PMID:21232019\", AUTHORS = \"Naude PJ, den Boer JA, Luiten PG, Eisel UL\", TITLE = \"Tumor necrosis factor receptor cross-talk.\", JOURNAL = c(\"FEBS J 278:888-98 (2011)\", \"DOI:10.1111/j.1742-4658.2011.08017.x\")), list(REFERENCE = \"PMID:20489699\", AUTHORS = \"Faustman D, Davis M\", TITLE = \"TNF receptor 2 pathway: drug target for autoimmune diseases.\", JOURNAL = c(\"Nat Rev Drug Discov 9:482-93 (2010)\", \"DOI:10.1038/nrd3030\")), list(REFERENCE = \"PMID:14532286\", AUTHORS = \"Chan FK, Shisler J, Bixby JG, Felices M, Zheng L, Appel M, Orenstein J, Moss B, Lenardo MJ\", TITLE = \"A role for tumor necrosis factor receptor-2 and receptor-interacting protein in programmed necrosis and antiviral responses.\", JOURNAL = c(\"J Biol Chem 278:51613-21 (2003)\", \"DOI:10.1074/jbc.M305633200\"))', 'map04668', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (319, '04670', 'Leukocyte transendothelial migration', 'Leukocyte migaration from the blood into tissues is vital for immune surveillance and inflammation. During this diapedesis of leukocytes, the leukocytes bind to endothelial cell adhesion molecules (CAM) and then migrate across the vascular endothelium. A leukocyte adherent to CAMs on the endothelial cells moves forward by leading-edge protrusion and retraction of its tail. In this process, alphaL /beta2 integrin activates through Vav1, RhoA, which subsequently activates the kinase p160ROCK. ROCK activation leads to MLC phosphorylation, resulting in retraction of the actin cytoskeleton. Moreover, Leukocytes activate endothelial cell signals that stimulate endothelial cell retraction during localized dissociation of the endothelial cell junctions. ICAM-1-mediated signals activate an endothelial cell calcium flux and PKC, which are required for ICAM-1 dependent leukocyte migration. VCAM-1 is involved in the opening of the \"endothelial passage\" through which leukocytes can extravasate. In this regard, VCAM-1 ligation induces NADPH oxidase activation and the production of reactive oxygen species (ROS) in a Rac-mediated manner, with subsequent activation of matrix metallopoteinases and loss of VE-cadherin-mediated adhesion.', 'Organismal Systems; Immune system', 'Leukocyte transendothelial migration', 'Leukocyte adhesion deficiency, Neonatal ichthyosis-sclerosing cholangitis (NISCH) syndrome, Band-like calcification with simplified gyration and polymicrogyria, Pilomatricoma, Metachondromatosis, Juvenile-onset dystonia, Activated PI3K-delta syndrome', 'GO: 0050900', 'ko04670', 'list(REFERENCE = \"PM