INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (320, '04672', 'Intestinal immune network for IgA production', 'The intestine is the largest lymphoid tissue in the body. One striking feature of intestinal immunity is its ability to generate great amounts of noninflammatory immunoglobulin A (IgA) antibodies that serve as the first line of defense against microorganisms. The basic map of IgA production includes induction of mucosal B cells in the Peyer\'s patches, circulation through the bloodstream and homing to intestinal mucosa of IgA-commited plasma cells, and local antibody production for export across the intestinal membranes. Multiple cytokines, including TGF-{beta}, IL-10, IL-4, IL-5, and IL-6, are required to promote IgA class switching and terminal differentiation process of the B cells. Secreted IgA promotes immune exclusion by entrapping dietary antigens and microorganisms in the mucus and functions for neutralization of toxins and pathogenic microbes.', 'Organismal Systems; Immune system', 'Intestinal immune network for IgA production', 'GO: 0002387', 'ko04672', 'list(REFERENCE = \"PMID:12511876\", AUTHORS = \"Fagarasan S, Honjo T\", TITLE = \"Intestinal IgA synthesis: regulation of front-line body defences.\", JOURNAL = c(\"Nat Rev Immunol 3:63-72 (2003)\", \"DOI:10.1038/nri982\")), list(REFERENCE = \"PMID:19079156\", AUTHORS = \"Macpherson AJ, McCoy KD, Johansen FE, Brandtzaeg P\", TITLE = \"The immune geography of IgA induction and function.\", JOURNAL = c(\"Mucosal Immunol 1:11-22 (2008)\", \"DOI:10.1038/mi.2007.6\")), list(REFERENCE = \"PMID:15971106\", AUTHORS = \"Kunisawa J, Kiyono H\", TITLE = \"A marvel of mucosal T cells and secretory antibodies for the creation of first lines of defense.\", JOURNAL = c(\"Cell Mol Life Sci 62:1308-21 (2005)\", \"DOI:10.1007/s00018-005-5035-1\")), list(REFERENCE = \"PMID:18549797\", AUTHORS = \"Cerutti A, Rescigno M\", TITLE = \"The biology of intestinal immunoglobulin A responses.\", JOURNAL = c(\"Immunity 28:740-50 (2008)\", \"DOI:10.1016/j.immuni.2008.05.001\")), list(REFERENCE = \"PMID:18243016\", AUTHORS = \"Tsuji M, Suzuki K, Kinoshita K, Fagarasan S\", TITLE = \"Dynamic interactions between bacteria and immune cells leading to intestinal IgA synthesis.\", JOURNAL = c(\"Semin Immunol 20:59-66 (2008)\", \"DOI:10.1016/j.smim.2007.12.003\")), list(REFERENCE = \"PMID:17182536\", AUTHORS = \"Corthesy B\", TITLE = \"Roundtrip ticket for secretory IgA: role in mucosal homeostasis?\", JOURNAL = c(\"J Immunol 178:27-32 (2007)\", \"DOI:10.4049/jimmunol.178.1.27\")), list(REFERENCE = \"PMID:18838301\", AUTHORS = \"Suzuki K, Fagarasan S\", TITLE = \"How host-bacterial interactions lead to IgA synthesis in the gut.\", JOURNAL = c(\"Trends Immunol 29:523-31 (2008)\", \"DOI:10.1016/j.it.2008.08.001\")), list(REFERENCE = \"PMID:19079167\", AUTHORS = \"Mora JR, von Andrian UH\", TITLE = \"Differentiation and homing of IgA-secreting cells.\", JOURNAL = c(\"Mucosal Immunol 1:96-109 (2008)\", \"DOI:10.1038/mi.2007.14\")), list(REFERENCE = \"PMID:17161619\", AUTHORS = \"Suzuki K, Ha SA, Tsuji M, Fagarasan S\", TITLE = \"Intestinal IgA synthesis: a primitive form of adaptive immunity that regulates microbial communities in the gut.\", JOURNAL = c(\"Semin Immunol 19:127-35 (2007)\", \"DOI:10.1016/j.smim.2006.10.001\")), list(REFERENCE = \"PMID:18483500\", AUTHORS = \"Cerutti A\", TITLE = \"The regulation of IgA class switching.\", JOURNAL = c(\"Nat Rev Immunol 8:421-34 (2008)\", \"DOI:10.1038/nri2322\"))', 'map04672', 'Intestinal immune network for IgA production', 145, 0, 145, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (321, '04710', 'Circadian rhythm', 'Circadian rhythm is an internal biological clock, which enables to sustain an approximately 24-hour rhythm in the absence of environmental cues. In mammals, the circadian clock mechanism consists of cell-autonomous transcription-translation feedback loops that drive rhythmic, 24-hour expression patterns of core clock components. The first negative feedback loop is a rhythmic transcription of period genes (PER1, PER2, and PER3) and chryptochrome genes (CRY1 and CRY2). PER and CRY proteins form a heterodimer, which acts on the CLOCK/BMAL1 heterodimer to repress its own transcription. PER and CRY proteins are phosphorylated by casein kinase epsilon (CKIepsilon), which leads to degradation and restarting of the cycle. The second loop is a positive feedback loop driven by the CLOCK/BMAL1 heterodimer, which initiates transcription of target genes containing E-box cis-regulatory enhancer sequences.', 'Organismal Systems; Environmental adaptation', 'Circadian rhythm', 'Familial advanced sleep phase syndrome, Delayed sleep phase syndrome', 'GO: 0007623', 'ko04710', 'list(REFERENCE = \"PMID:11165242\", AUTHORS = \"Camacho F, Cilio M, Guo Y, Virshup DM, Patel K, Khorkova O, Styren S, Morse B, Yao Z, Keesler GA.\", TITLE = \"Human casein kinase Idelta phosphorylation of human circadian clock proteins period 1 and 2.\", JOURNAL = c(\"FEBS Lett 489:159-65 (2001)\", \"DOI:10.1016/S0014-5793(00)02434-0\")), list(REFERENCE = \"PMID:12015613\", AUTHORS = \"Panda S, Hogenesch JB, Kay SA.\", TITLE = \"Circadian rhythms from flies to human.\", JOURNAL = c(\"Nature 417:329-35 (2002)\", \"DOI:10.1038/417329a\")), list(REFERENCE = \"PMID