INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (323, '04712', 'Circadian rhythm - plant', 'The circadian system is an endogenous rhythm with the period of about 24 hours to provide temporal organization of biological activity. In Arabidopsis the circadian oscillator at the core of this system is composed of the interlocked feedback loop formed by the major transcriptional factors CCA1 (circadian clock associated), LHY (late elongated hypocotyl) and TOC1 (timing of cab), a pseudo response regulator (PRR). The morning-expressed CCA1/LHY Myb transcription factors suppress TOC1 expression by binding to its promoter. Moreover, CCA1/LHY activates the expression of PRR7/9 in the morning and then PRR7/9 repress the transcription of CCA1/LHY during the rest of the day. By contrast, the evening-expressed TOC1 activates the expression of CCA1/LHY.', 'Organismal Systems; Environmental adaptation', 'Circadian rhythm - plant', 'GO: 0007623', 'ko04712', 'list(REFERENCE = \"PMID:11710980\", AUTHORS = \"McWatters HG, Roden LC, Staiger D.\", TITLE = \"Picking out parallels: plant circadian clocks in context.\", JOURNAL = c(\"Philos Trans R Soc Lond B Biol Sci 356:1735-43 (2001)\", \"DOI:10.1098/rstb.2001.0936\")), list(REFERENCE = \"PMID:11597501\", AUTHORS = \"Yanovsky MJ, Kay SA.\", TITLE = \"Signaling networks in the plant circadian system.\", JOURNAL = c(\"Curr Opin Plant Biol 4:429-35 (2001)\", \"DOI:10.1016/S1369-5266(00)00196-5\")), list(REFERENCE = \"PMID:10792828\", AUTHORS = \"Kim L, Kircher S, Toth R, Adam E, Schafer E, Nagy F.\", TITLE = \"Light-induced nuclear import of phytochrome-A:GFP fusion proteins is differentially regulated in transgenic tobacco and Arabidopsis.\", JOURNAL = c(\"Plant J 22:125