INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (327, '04721', 'Synaptic vesicle cycle', 'Communication between neurons is mediated by the release of neurotransmitter from synaptic vesicles (SVs). At the nerve terminal, SVs cycle through repetitive episodes of exocytosis and endocytosis. SVs are filled with neurotransmitters by active transport. The loaded SVs are then docked at a specialized region of the presynaptic plasma membrane known as the active zone, where they undergo a priming reaction. Upon arrival of an action potential, Ca2+ enters through voltage-gated channels and neurotransmitter is released by exocytosis, usually in less than a millisecond. After fusion, the vesicle is retrieved by endocytosis and reloaded for another round of exocytosis.', 'Organismal Systems; Nervous system', 'Synaptic vesicle cycle', 'Centronuclear myopathy, Episodic ataxias, Hemiplegic migraine, DDOD syndrome', 'GO: 0016079 0048488', 'ko04721', 'list(REFERENCE = \"PMID:12827282\", AUTHORS = \"Li L, Chin LS\", TITLE = \"The molecular machinery of synaptic vesicle exocytosis.\", JOURNAL = c(\"Cell Mol Life Sci 60:942-60 (2003)\", \"DOI:10.1007/s00018-003-2240-7\")), list(REFERENCE = \"\", AUTHORS = \"Nestler EJ, Hyman SE, Malenka RC.\", TITLE = \"Molecular neuropharmacology: A foundation for clinical neuroscience\", JOURNAL = \"The McGraw-Hill Companies, Inc. (2001)\"), list(REFERENCE = \"PMID:18275379\", AUTHORS = \"Chapman ER\", TITLE = \"How does synaptotagmin trigger neurotransmitter release?\", JOURNAL = c(\"Annu Rev Biochem 77:615-41 (2008)\", \"DOI:10.1146/annurev.biochem.77.062005.101135\")), list(REFERENCE = \"PMID:10212473\", AUTHORS = \"Benfenati F, Onofri F, Giovedi S\", TITLE = \"Protein-protein i