INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (338, '04744', 'Phototransduction', 'Phototransduction is a biochemical process by which the photoreceptor cells generate electrical signals in response to captured photons. The vertebrate cascade starts with the absorption of photons by the photoreceptive pigments, the rhodopsins, which consist of a membrane embedded chromophore, 11-cis-retinal, and a G-protein-coupled receptor, opsin. The photon isomerizes 11-cis-retinal to all-trans-retinal which induces a structural change that activates the opsin. This triggers hydrolysis of cGMP by activating a transducinphosphodiesterase 6 (PDE6) cascade, which results in closure of the cGMP-gated cation channels (CNG) in the plasma membrane and membrane hyperpolarization. The hyperpolarization of the membrane potential of the photoreceptor cell modulates the release of neurotransmitters to downstream cells. Recovery from light involves the deactivation of the light- activated intermediates: photolyzed rhodopsin is phosphorylated by rhodopsin kinase (RK) and subsequently capped off by arrestin; GTP-binding transducin alpha subunit deactivates through a process that is stimulated by RGS9.', 'Organismal Systems; Sensory system', 'Phototransduction', 'Cone-rod dystrophy and cone dystrophy, Retinitis pigmentosa, Congenital stationary night blindness, Familial flecked retina syndrome, Leber congenital amaurosis, Achromatopsia, Bradyopsia', 'GO: 0009586', 'ko04744', 'list(REFERENCE = \"PMID:19837030\", AUTHORS = \"Yau KW, Hardie RC\", TITLE = \"Phototransduction motifs and variations.\", JOURNAL = c(\"Cell 139:246-64 (2009)\", \"DOI:10.1016/j.cell.2009.09.029\")), list(REFERENCE = \"PMID:11952100\", AUTHORS = \"Koch KW, Duda T, Sharma RK\", TITLE = \"Photoreceptor specific guanylate cyclases in vertebrate phototransduction.\", JOURNAL = \"Mol Cell Biochem 230:97-106 (2002)\"), list(REFERENCE = \"PMID:9803480\", AUTHORS = \"Jindrova H\", TITLE = \"Vertebrate phototransduction: activation, recovery, and adaptation.\", JOURNAL = \"Physiol Res 47:155-68 (1998)\"), list(REFERENCE = \"PMID:11070368\", AUTHORS = \"Ebrey T, Koutalos Y\", TITLE = \"Vertebrate photoreceptors.\", JOURNAL = c(\"Prog Retin Eye Res 20:49-94 (2001)\", \"DOI:10.1016/S1350-9462(00)00014-8\")), list(REFERENCE = \"PMID:11587916\", AUTHORS = \"Pepe IM\", TITLE = \"Recent advances in our understanding of rhodopsin and phototransduction.\", JOURNAL = c(\"Prog Retin Eye Res 20:733-59 (2001)\", \"DOI:10.1016/S1350-9462(01)00013-1\")), list(REFERENCE = \"PMID:11551982\", AUTHORS = \"Kramer RH, Molokanova E\", TITLE = \"Modulation of cyclic-nucleotide-gated channels and regulation of vertebrate phototransduction.\", JOURNAL = \"J Exp Biol 204:2921-31 (2001)\"), list(REFERENCE = \"PMID:19720650\", AUTHORS = \"Larhammar D, Nordstrom K, Larsson TA\", TITLE = \"Evolution of vertebrate rod and cone phototransduction genes.\", JOURNAL = c(\"Philos Trans R Soc Lond B Biol Sci 364:2867-80 (2009)\", \"DOI:10.1098/rstb.2009.0077\")), list(REFERENCE = \"PMID:19953307\", AUTHORS = \"Dizhoor AM, Olshevskaya EV, Peshenko IV\", TITLE = \"Mg2+/Ca2+ cation binding cycle of guanylyl cyclase activating proteins (GCAPs): role in regulation of photoreceptor guanylyl cyclase.\", JOURNAL = c(\"Mol Cell Biochem 334:117-24 (2010)\", \"DOI:10.1007/s11010-009-0328-6\")), list(REFERENCE = \"PMID:12161344\", AUTHORS = \"Krizaj D, Copenhagen DR\", TITLE = \"Calcium regulation in photoreceptors.\", JOURNAL = \"Front Biosci 7:d2023-44 (2002)\"), list(REFERENCE = \"PMID:9893707\", AUTHORS = \"Shichida Y, Imai H\", TITLE = \"Visual pigment: G-protein-coupled receptor for light signals.\", JOURNAL = c(\"Cell Mol Life Sci 54:1299-315 (1998)\", \"DOI:10.