INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (344, '04912', 'GnRH signaling pathway', 'Gonadotropin-releasing hormone (GnRH) secretion from the hypothalamus acts upon its receptor in the anterior pituitary to regulate the production and release of the gonadotropins, LH and FSH. The GnRHR is coupled to Gq/11 proteins to activate phospholipase C which transmits its signal to diacylglycerol (DAG) and inositol 1,4,5-trisphosphate (IP3). DAG activates the intracellular protein kinase C (PKC) pathway and IP3 stimulates release of intracellular calcium. In addition to the classical Gq/11, coupling of Gs is occasionally observed in a cell-specific fashion. Signaling downstream of protein kinase C (PKC) leads to transactivation of the epidermal growth factor (EGF) receptor and activation of mitogen-activated protein kinases (MAPKs), including extracellular-signal-regulated kinase (ERK), Jun N-terminal kinase (JNK) and p38 MAPK. Active MAPKs translocate to the nucleus, resulting in activation of transcription factors and rapid induction of early genes.', 'Organismal Systems; Endocrine system', 'GnRH signaling pathway', 'Hypogonadotropic hypogonadism, Hypokalemic periodic paralysis (HypoPP), Precocious puberty, Isolated follicle-stimulating hormone deficiency, Abnormal pituitary gonadotropin secretion', 'ko04912', 'list(REFERENCE = \"PMID:15350605\", AUTHORS = \"Ruf F, Sealfon SC.\", TITLE = \"Genomics view of gonadotrope signaling circuits.\", JOURNAL = c(\"Trends Endocrinol Metab 15:331-8 (2004)\", \"DOI:10.1016/j.tem.2004.07.008\")), list(REFERENCE = \"PMID:15613448\", AUTHORS = \"Harrison GS, Wierman ME, Nett TM, Glode LM.\", TITLE = \"Gonadotropin-releasing hormone and its receptor in normal and malignant cells.\", JOURNAL = c(\"Endocr Relat Cancer 11:725-48 (2004)\", \"DOI:10.1677/erc.1.00777\")), list(REFERENCE = \"PMID:15982836\", AUTHORS = \"Pawson AJ, McNeilly AS.\", TITLE = \"The pituitary effects of GnRH.\", JOURNAL = c(\"Anim Reprod Sci 88:75-94 (2005)\", \"DOI:10.1016/j.anireprosci.2005.05.010\")), list(REFERENCE = \"\", AUTHORS = \"DeGroot LJ, Jameson JL (ed).\", TITLE = \"Endocrinology (Fifth edition)\", JOURNAL = \"Elsevier Saunders (2006)\"), list(REFERENCE = \"PMID:10707049\", AUTHORS = \"Naor Z, Benard O, Seger R.\", TITLE = \"Activation of MAPK cascades by G-protein-coupled receptors: the case of gonadotropin-releasing hormone receptor.\", JOURNAL = c(\"Trends Endocrinol Metab 11:91-9 (2000)\", \"DOI:10.1016/S1043-2760(99)00232-5\")), list(REFERENCE = \"PMID:14596811\", AUTHORS = \"Ruf F, Fink MY, Sealfon SC.\", TITLE = \"Structure of the GnRH receptor-stimulated signaling network: insights from genomics.\", JOURNAL = c(\"Front Neuroendocrinol 24:181-99 (2003)\", \"DOI:10.1016/S0091-3022(03)00027-X\")), list(REFERENCE = \"PMID:12943720\", AUTHORS = \"Farshori PQ, Shah BH, Arora KK, Martinez-Fuentes A, Catt KJ.\", TITLE = \"Activation and nuclear translocation of PKCdelta, Pyk2 and ERK1/2 by gonadotropin releasing hormone in HEK293 cells.\", JOURNAL = c(\"J Steroid Biochem Mol Biol 85:337-47 (2003)\", \"DOI:10.1016/S0960-0760(03)00226-7\")), list(REFERENCE = \"PMID:15080147\", AUTHORS = \"Shah BH, Catt KJ.\", TITLE = \"Matrix metalloproteinases in reproductive endocrinology.\", JOURNAL = c(\"Trends Endocrinol Metab 15:47-9 (2004)\", \"DOI:10.1016/j.tem.2004.01.004\")), list(REFERENCE = \"PMID:12963732\", AUTHORS = \"Roelle S, Grosse R, Aigner A, Krell HW, Czubayko F, Gudermann T.\", TITLE = \"Matrix metalloproteinases 2 and 9 mediate epidermal growth factor receptor transactivation by gonadotropin-releasing hormone.\", JOURNAL = c(\"J Biol Chem 278:47307-18 (2003)\", \"DOI:10.1074/jbc.M304377200\")), list(REFERENCE = \"PMID:18708085\", AUTHORS =