INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (345, '04913', 'Ovarian steroidogenesis', 'The ovarian steroids, 17-beta estradiol (E2) and progesterone (P4), are critical for normal uterine function, establishment and maintenance of pregnancy, and mammary gland development. Furthermore, the local effects that are essential for normal ovarian physiology are dependent on the endocrine, paracrine, and autocrine actions of E2, P4, and androgens. In most mammals (including humans and mice), ovarian steroidogenesis occurs according to the two-cell/two-gonadotropin theory. This theory describes how granulosa and theca cells work together to make the ovarian steroids. Theca cells respond to LH signaling by increasing the expression of enzymes necessary for the conversion of cholesterol to androgens, such as androstenedione (A) and testosterone (T). Granulosa cells respond to FSH signaling by increasing the expression of enzymes necessary for the conversion of theca-derived androgens into estrogens (E2 and estrone).', 'Organismal Systems; Endocrine system', 'Ovarian steroidogenesis', '46,XX gonadal dysgenesis, Premature ovarian failure, Hyperlipoproteinemia type IIa, Fertile eunuch syndrome, Aromatase deficiency', 'GO: 0050810', 'ko04913', 'list(REFERENCE = \"PMID:15541573\", AUTHORS = \"Havelock JC, Rainey WE, Carr BR\", TITLE = \"Ovarian granulosa cell lines.\", JOURNAL = c(\"Mol Cell Endocrinol 228:67-78 (2004)\", \"DOI:10.1016/j.mce.2004.04.018\")), list(REFERENCE = \"PMID:15831519\", AUTHORS = \"Stocco DM, Wang X, Jo Y, Manna PR\", TITLE = \"Multiple signaling pathways regulating steroidogenesis and steroidogenic acute regulatory protein expression: more complicated than we thought.\", JOURNAL = c(\