INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (346, '04914', 'Progesterone-mediated oocyte maturation', 'Xenopus oocytes are naturally arrested at G2 of meiosis I. Exposure to either insulin/IGF-1 or the steroid hormone progesterone breaks this arrest and induces resumption of the two meiotic division cycles and maturation of the oocyte into a mature, fertilizable egg. This process is termed oocyte maturation. The transition is accompanied by an increase in maturation promoting factor (MPF or Cdc2/cyclin B) which precedes germinal vesicle breakdown (GVBD). Most reports point towards the Mos-MEK1-ERK2 pathway [where ERK is an extracellular signal-related protein kinase, MEK is a MAPK/ERK kinase and Mos is a p42(MAPK) activator] and the polo-like kinase/CDC25 pathway as responsible for the activation of MPF in meiosis, most likely triggered by a decrease in cAMP.', 'Organismal Systems; Endocrine system', 'Progesterone-mediated oocyte maturation', 'Growth delay due to insulin-like growth factor I resistance', 'GO: 0001556', 'ko04914', 'list(REFERENCE = \"PMID:12045215\", AUTHORS = \"Schmitt A, Nebreda AR.\", TITLE = \"Signalling pathways in oocyte meiotic maturation.\", JOURNAL = \"J Cell Sci 115:2457-9 (2002)\"), list(REFERENCE = \"PMID:12084906\", AUTHORS = \"Cato AC, Nestl A, Mink S.\", TITLE = \"Rapid actions of steroid receptors in cellular signaling pathways.\", JOURNAL = c(\"Sci STKE 2002:RE9 (2002)\", \"DOI:10.1126/stke.2002.138.re9\")), list(REFERENCE = \"PMID:11730319\", AUTHORS = \"Maller JL, Schwab MS, Roberts BT, Gross SD, Taieb FE, Tunquist BJ.\", TITLE = \"The pathway of MAP kinase mediation of CSF arrest in Xenopus oocytes.\", JOURNAL = c(\"Biol Cell 93:27-33 (2001)\", \"DOI:10.1016/S0248-4900(01)01127-3\")), list(REFERENCE = \"PMID:11063930\", AUTHORS = \"Nebreda AR, Ferby I.\", TITLE = \"Regulation of the meiotic cell cycle in oocytes.\", JOURNAL = c(\"Curr Opin Cell Biol 12:666-75 (2000)\", \"DOI:10.1016/S0955-0674(00)00150-2\")), list(REFERENCE = \"PMID:10497333\", AUTHORS = \"Ferrell JE Jr.\", TITLE = \"Xenopus oocyte maturation: new lessons from a good egg.\", JOURNAL = c(\"Bioessays 21:833-42 (1999)\", \"DOI:10.1002/(SICI)1521-1878(199910)21:10<833::AID-BIES5>3.0.CO;2-P\")), list(REFERENCE = \"PMID:12374568\", AUTHORS = \"Andersen CB, Sakaue H, Nedachi T, Kovacina KS, Clayberger C, Conti M, Roth RA.\", TITLE = \"Protein kinase B/Akt is essential for the insulin- but not progesterone-stimulated resumption of meiosis in Xenopus oocytes.\", JOURNAL = c(\"Biochem J 369:227-38 (2003)\", \"DOI:10.1042/BJ20021243\")), list(REFERENCE = \"PMID:12477927\", AUTHORS = \"Duckworth BC, Weaver JS, Ruderman JV.\", TITLE = \"G2 arrest in Xenopus oocytes depends on phosphorylation of cdc25 by protein kinase A.\", JOURNAL = c(\"Proc Natl Acad Sci U S A 99:16794-9 (2002)\", \"DOI:10.1073/pnas.222661299\")), list(REFERENCE = \"PMID:11148128\", AUTHORS = \"Abrieu A, Doree M, Fisher D.\", TITLE = \"The interplay between cyclin-B-Cdc2 kinase (MPF) and MAP kinase during maturation of oocytes.\", JOURNAL = \"J Cell Sci 114:257-67 (2001)\")', 'map04914', 145, 0, 145, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (347, '04915', 'Estrogen signaling pathway', 'Estrogens are steroid hormones that regulate a plethora of physiological processes in mammals, including reproduction, cardiovascular protection, bone integrity, cellular homeostasis, and behavior. Estrogen mediates its cellular actions through two signaling pathways classified as \"nuclear-initiated steroid signaling\" and \"membrane-initiated steroid signaling\". In the \"nuclear\" pathway, estrogen binds either ERalpha or ERbeta, which in turn translocates to the nucleus, binds DNA at ERE elements and activates the expression of ERE-dependent genes. In \"membrane\" pathway, Estrogen can exert its actions through a subpopulation of ER at the plasma membrane (mER) or novel G-protein coupled E2 receptors (GPER). Upon activation of these receptors various signaling pathways (i.e. Ca2+, cAMP, protein kinase cascades) are rapidly activated and ultimately influence downstream transcription factors.', 'Organismal Systems; Endocrine system', 'Estrogen signaling pathway', 'Pachyonychia congenita, Naegeli-Franceschetti-Jadassohn syndrome, Estrogen resistance syndrome', 'GO: 0030520', 'ko04915', 'list(REFERENCE = \"PMID:16516516\", AUTHORS = \"Zhang D, Trudeau VL\", TITLE = \"Integration of membrane and nuclear estrogen receptor signaling.\", JOURNAL = c(\"Comp Biochem Physiol A Mol Integr Physiol 144:306-15 (2006)\", \"DOI:10.1016/j.cbpa.2006.01.025\")), list(REFERENCE = \"PMID:19187465\", AUTHORS = \"Roepke TA, Qiu J, Bosch MA, Ronnekleiv OK, Kelly MJ\", TITLE = \"Cross-talk between membrane-initiated and nuclear-initiated oestrogen signalling in the hypothalamus.\", JOURNAL = c(\"J Neuroendocrinol 21:263-70 (2