INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (348, '04916', 'Melanogenesis', 'Cutaneous melanin pigment plays a critical role in camouflage, mimicry, social communication, and protection against harmful effects of solar radiation. Melanogenesis is under complex regulatory control by multiple agents. The most important positive regulator of melanogenesis is the MC1 receptor with its ligands melanocortic peptides. MC1R activates the cyclic AMP (cAMP) response-element binding protein (CREB). Increased expression of MITF and its activation by phosphorylation (P) stimulate the transcription of tyrosinase (TYR), tyrosinase-related protein 1 (TYRP1), and dopachrome tautomerase (DCT), which produce melanin. Melanin synthesis takes place within specialized intracellular organelles named melanosomes. Melanin-containing melanosomes then move from the perinuclear region to the dendrite tips and are transferred to keratinocytes by a still not well-characterized mechanism.', 'Organismal Systems; Endocrine system', 'Melanogenesis', 'Oculocutaneous albinism, Ocular albinism, Piebaldism, Odontoonychodermal dysplasia, Waardenburg syndrome, Waardenburg syndrome, ABCD syndrome, Familial progressive hyperpigmentation, Pilomatricoma, Tietz syndrome', 'ko04916', 'list(REFERENCE = \"PMID:15383650\", AUTHORS = \"Slominski A, Tobin DJ, Shibahara S, Wortsman J.\", TITLE = \"Melanin pigmentation in mammalian skin and its hormonal regulation.\", JOURNAL = c(\"Physiol Rev 84:1155-228 (2004)\", \"DOI:10.1152/physrev.00044.2003\")), list(REFERENCE = \"PMID:10841026\", AUTHORS = \"Busca R, Ballotti R.\", TITLE = \"Cyclic AMP a key messenger in the regulation of skin pigmentation.\", JOURNAL = c(\"Pigment Cell Res 13:60-9 (2000)\", \"DOI:10.1034/j.1600-0749.2000.130203.x\")), list(REFERENCE = \"PMID:15016298\", AUTHORS = \"Imokawa G.\", TITLE = \"Autocrine and paracrine regulation of melanocytes in human skin and in pigmentary disorders.\", JOURNAL = c(\"Pigment Cell Res 17:96-110 (2004)\", \"DOI:10.1111/j.1600-0749.2003.00126.x\")), list(REFERENCE = \"PMID:11023702\", AUTHORS = \"Wikberg JE, Muceniece R, Mandrika I, Prusis P, Lindblom J, Post C, Skottner A.\", TITLE = \"New aspects on the melanocortins and their receptors.\", JOURNAL = c(\"Pharmacol Res 42:393-420 (2000)\", \"DOI:10.1006/phrs.2000.0725\")), list(REFERENCE = \"PMID:12894244\", AUTHORS = \"Chin L.\", TITLE = \"The genetics of malignant melanoma: lessons from mouse and man.\", JOURNAL = c(\"Nat Rev Cancer 3:559-70 (2003)\", \"DOI:10.1038/nrc1145\")), list(REFERENCE = \"PMID:16822996\", AUTHORS = \"Miller AJ, Mihm MC Jr.\", TITLE = \"Melanoma.\", JOURNAL = c(\"N Engl J Med 355:51-65 (2006)\", \"DOI:10.1056/NEJMra052166\")), list(REFERENCE = \"PMID:12753399\", AUTHORS = \"Saito H, Yasumoto K, Takeda K, Takahashi K, Yamamoto H, Shibahara S.\", TITLE = \"Microphthalmia-associated transcription factor in the Wnt signaling pathway.\", JOURNAL = c(\"Pigment Cell Res 16:261-5 (2003)\", \"DOI:10.1034/j.1600-0749.2003.00039.x\")), list(REFERENCE = \"PMID:9263329\", AUTHORS = \"Imokawa G, Kobayashi T, Miyagishi M, Higashi K, Yada Y.\", TITLE = \"The role of endothelin-1 in epidermal hyperpigmentation and signaling mechanisms of mitogenesis and melanogenesis.\", JOURNAL = c(\"Pigment Cell Res 10:218-28 (1997)\", \"DOI:10.1111/j.1600-0749.1997.tb00488.x\")), list(REFERENCE = \"PMID:10643996\", AUTHORS = \"Park HY, Gilchrest BA.\", TITLE = \"Signaling pathways mediating melanogenesis.\", JOURNAL = \"Cell Mol Biol (Noisy-le-grand) 45:919-30 (1999)\"), list(REFERENCE = \"PMID:10347209\", AUTHORS = \"Park HY, Perez JM, Laursen R, Hara M, Gilchrest BA.\", TITLE = \"Protein kinase C-beta activates tyrosinase by phosphorylating serine residues in its cytoplasmic domain.\", JOURNAL = c(\"J Biol Chem 274:16470-8 (1999)\", \"DOI:10.1074/jbc.274.23.16470\")), list(REFERENCE = \"PMID:11602344\", AUTHORS = \"Sturm RA, Teasdale RD, Box NF.\", TITLE = \"Human pigmentation genes: identification, structure and consequences of polymorphic variation.\", JOURNAL = c(\"Gene 277:49-62 (2001)\", \"DOI:10.1016/S0378-1119(01)00694-1\")), list(REFERENCE = \"PMID:11041354\", AUTHORS = \"Hearing VJ.\", TITLE = \"The melanosome: the perfect model for cellular responses to the environment.\", JOURNAL = c(\"Pigment Cell Res 13 Suppl 8:23-34 (2000)\", \"DOI:10.1034/j.1600-0749.13.s8.7.x\")), list(REFERENCE = \"PMID:16246839\", AUTHORS = \"Molina DM, Grewal S, Bardwell L.\", TITLE = \"Characterization of an ERK-binding domain in microphthalmia-associated transcription factor and differential inhibition of ERK2-mediated substrate phosphorylation.\", JOURNAL = c(\"J Biol Chem 280:42051-60 (2005)\", \"DOI:10.1074/jbc.M510590200\"))', 'map04916', 'Melanogenesis', 145, 0, 145, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (349, '04917', 'Prolactin signaling pathway', 'Prolactin (PRL) is a polypeptide hormone known to be involved in a wide range of biological functions including osmoregulation, lactation, reproduction, growth and development, endocrinology and metabolism, brain and behavior, and immunomodulation. PRL mediates its action through PRLR, a transmembrane protein of the hematopoietin cytokine receptor superfamily. At the protein level, the long PRLR isoform (long-R) and several short PRLR isoforms (short-R) have been detected. Acting through the long-R, PRL activates many signaling cascades including Jak2/Stat, the major cascade, Src kinase, phosphatidylinositol-3-kinase (PI3K)/AKT, and mitogen-activated protein kinase (MAPK) pathways. PRL cannot activate Jak2/Stat5 through the short-R, but can activate pathways including MAPK and PI3K pathways.', 'Organismal Systems; Endocrine system', 'Prolactin signaling pathway', 'Hyperprolactinemia, Fertile eunuch syndrome, Familial male-limited precocious puberty, Estrogen resistance syndrome', 'GO: 0038161', 'ko04917', 'list(REFERENCE = \"PMID:20149678\", AUTHORS = \"Binart N, Bachelot A, Bouilly J\", TITLE = \"Impact of prolactin receptor isoforms on reproduction.\", JOURNAL = c(\"Trends Endocrinol Metab 21:362-8 (2010)\", \"DOI:10.1016/j.tem.2010.01.008\")), list(REFERENCE = \"PMID:17307904\", AUTHORS = \"Bachelot A, Binart N\", TITLE = \"Reproductive role of prolactin.\", JOURNAL = c(\"Reproduction 133:361-9 (2007)\", \"DOI:10.1530/REP-06-0299\")), list(REFERENCE = \"PMID:16231422\", AUTHORS = \"Hennighausen L, Robinson GW\", TITLE = \"Information networks in the mammary gland.\", JOURNAL = c(\"Nat Rev Mol Cell Bi