INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (363, '04931', 'Insulin resistance', 'Insulin resistance is a condition where cells become resistant to the effects of insulin. It is often found in people with health disorders, including obesity, type 2 diabetes mellitus, non-alcoholic fatty liver disease, and cardiovascular diseases. In this diagram multiple mechanisms underlying insulin resistance are shown: (a) increased phosphorylation of IRS (insulin receptor substrate) protein through serine/threonine kinases, such as JNK1 and IKKB, and protein kinase C, (b) increased IRS-1 proteasome degradation via mTOR signaling pathway, (c) decreased activation of signaling molecules including PI3K and AKT, (d) increase in activity of phosphatases including PTPs, PTEN, and PP2A. Regulatory actions such as oxidative stress, mitochondrial dysfunction, accumulation of intracellular lipid derivatives (diacylglycrol and ceramides), and inflammation (via IL-6 and TNFA) contribute to these mechanisms. Consequently, insulin resistance causes reduced GLUT4 translocation, resulting in glucose takeup and glycogen synthesis in skeletal muscle as well as increased hepatic gluconeogenesis and decreased glycogen synthesis in liver. At the bottom of the diagram, interplay between O-GlcNAcylation and serine/threonine phosphorylation is shown. Studies suggested that elevated O-GlcNAc level was correlated to high glucose-induced insulin resistance. Donor UDP-GlcNAc is induced through hexosamine biosynthesis pathway and added to proteins by O-GlcNAc transferase. Elevation of O-GlcNAc modification alters phosphorylation and function of key insulin signaling proteins including IRS-1, PI3K, PDK1, Akt and other transcription factor and cofactors, resulting in the attenuation of insulin signaling cascade.', 'Human Diseases; Endocrine and metabolic disease', 'Insulin resistance', 'ko04931', 'list(REFERENCE = \"PMID:22360800\", AUTHORS = \"Martins AR, Nachbar RT, Gorjao R, Vinolo MA, Festuccia WT, Lambertucci RH, Cury-Boaventura MF, Silveira LR, Curi R, Hirabara SM\", TITLE = \"Mechanisms underlying skeletal muscle insulin resistance induced by fatty acids: importance of the mitochondrial function.\", JOURNAL = c(\"Lipids Health Dis 11:30 (2012)\", \"DOI:10.1186/1476-511X-11-30\")), list(REFERENCE = \"PMID:12765939\", AUTHORS = \"Bouzakri K, Roques M, Gual P, Espinosa S, Guebre-Egziabher F, Riou JP, Laville M, Le Marchand-Brustel Y, Tanti JF, Vidal H\", TITLE = \"Reduced activation of phosphatidylinositol-3 kinase and increased serine 636 phosphorylation of insulin receptor substrate-1 in primary culture of skeletal muscle cells from patients with type 2 diabetes.\", JOURNAL = c(\"Diabetes 52:1319-25 (2003)\", \"DOI:10.2337/diabetes.52.6.1319\")), list(REFERENCE = \"PMID:20526383\", AUTHORS = \"Choi K, Kim YB\", TITLE = \"Molecular mechanism of insulin resistance in obesity and type 2 diabetes.\", JOURNAL = c(\"Korean J Intern Med 25:119-29 (2010)\", \"DOI:10.3904/kjim.2010.25.2.119\")), list(REFERENCE = \"PMID:7666792\", AUTHORS = \"Ahmad F, Goldstein BJ\", TITLE = \"Increased abundance of specific skeletal muscle protein-tyrosine phosphatases in a genetic model of insulin-resistant obesity and diabetes mellitus.\", JOURNAL = c(\"Metabolism 44:1175-84 (1995)\", \"DOI:10.1016/0026-0495(95)90012-8\")), list(REFERENCE = \"PMID:15166380\", AUTHORS = \"George S, Rochford JJ, Wolfrum C, Gray SL, Schinner S, Wilson JC, Soos MA, Murgatroyd PR, Williams RM, Acerini CL, Dunger DB, Barford D, Umpleby AM, Wareham NJ, Davies HA, Schafer AJ, Stoffel M, O\'Rahilly S, Barroso I\", TITLE = \"A family with severe insulin resistance and diabetes due to a mutation in AKT2.\", JOURNAL = c(\"Science 304:1325-8 (2004)\", \"DOI:10.1126/science.1096706\")), list(REFERENCE = \"PMID:22675355\", AUTHORS = \"Snel M, Jonker JT, Schoones J, Lamb H, de Roos A, Pijl H, Smit JW, Meinders AE, Jazet IM\", TITLE = \"Ectopic fat and insulin resistance: pathophysiology and effect of diet and lifestyle interventions.\", JOURNAL = c(\"Int J Endocrinol 2012:983814 (2012)\", \"DOI:10.1155/2012/983814\")), list(REFERENCE = \"PMID:17130651\", AUTHORS = \"Morino K, Petersen KF, Shulman GI\", TITLE = \"Molecular mechanisms of insulin resistance in humans and their potential links with mitochondrial dysfunction.\", JOURNAL = c(\"Diabetes 55 Suppl 2:S9-S15 (2006)\", \"DOI:10.2337/db06-S002\")), list(REFERENCE = \"PMID:16186396\", AUTHORS = \"Plomgaard P, Bouzakri K, Krogh-Madsen R, Mittendorfer B, Zierath JR, Pedersen BK\", TITLE = \"Tumor necrosis factor-alpha induces skeletal muscle insulin resistance in healthy human subjects via inhibition of Akt substrate 160 phosphorylation.\", JOURNAL = c(\"Diabetes 54:2939-45 (2005)\", \"DOI:10.2337/diabetes.54.10.2939\")), list(REFERENCE = \"PMID:18309108\", AUTHORS = \"Kim JA, Wei Y, Sowers JR\", TITLE = \"Role of mitochondrial dysfunction in insulin resistance.\", JOURNAL = c(\"Circ Res 102:401-14 (2008)\", \"DOI:10.1161/CIRCRESAHA.107.165472\")), list(REFERENCE = \"PMID:17141630\", AUTHORS = \"Steinberg GR, Michell BJ, van Denderen BJ, Watt MJ, Carey AL, Fam BC, Andrikopoulos S, Proietto J, Gorgun CZ, Carling D, Hotamisligil GS, Febbraio MA, Kay TW, Kemp BE\", TITLE = \"Tumor necrosis factor alpha-induced skeletal muscle insulin resistance involves suppression of AMP-kinase signaling.\", JOURNAL = c(\"Cell Metab 4:465-74 (2006)\", \"DOI:10.1016/j.cmet.2006.11.005\")), list(REFERENCE = \"PMID:9886945\", AUTHORS = \"Ruderman NB, Saha AK, Vavvas D, Witters LA\", TITLE = \"Malonyl-CoA, fuel sensing, and insulin resistance.\", JOURNAL = c(\"Am J Physiol 276:E1-E18 (1999)\", \"DOI:10.1152/ajpendo.1999.276.1.E1\")), list(REFERENCE = \"PMID:8048519\", AUTHORS = 