INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (367, '04935', 'Growth hormone synthesis, secretion and action', 'Growth hormone (GH) is a peptide hormone secreted from the anterior pituitary and plays a critical role in cell growth, development, and metabolism throughout the body. GH secretion occurs episodically and is primarily under the control of two hypothalamic neuroendocrine hormones: GH-releasing hormone (GHRH), which stimulates GH secretion, and somatostatin, which inhibits GH secretion. GHRH interacts with a G protein-coupled receptor (GHRHR) in somatotroph cells to activate the cAMP signaling pathway, which leads to increased GH mRNA transcription and release. Once released into the circulation, GH binds to cell-surface GHR in target tissues such as liver, muscle, bone, and adipose tissue. Binding leads to activation of JAK2 that in turn triggers an array of signaling cascade including Raf-MEK-ERK, PI3K-Akt, and STAT5. ERK, Akt, and STAT5 all promote growth via the synthesis and secretion of IGF-1.', 'Organismal Systems; Endocrine system', 'Growth hormone synthesis, secretion and action', 'ko04935', 'list(REFERENCE = \"\", AUTHORS = \"Nishad R, Mukhi D, Menon R K, Pasupulati A.\", TITLE = \"Growth Hormone and Metabolic Homeostasis\", JOURNAL = \"European Medical Journal 6(1):78-87 (2018)\"), list(REFERENCE = \"PMID:30775002\", AUTHORS = \"Lu M, Flanagan JU, Langley RJ, Hay MP, Perry JK\", TITLE = \"Targeting growth hormone function: strategies and therapeutic applications.\", JOURNAL = c(\"Signal Transduct Target Ther 4:3 (2019)\", \"DOI:10.1038/s41392-019-0036-y\")), list(REFERENCE = \"PMID:8874495\", AUTHORS = \"Argetsinger LS, Carter-Su C\", TITLE = \"Mechanism of signaling by growth hormone receptor.\", JOURNAL = c(\"Physiol Rev 76:1089-107 (1996)\", \"DOI:10.1152/physrev.1996.76.4.1089\")), list(REFERENCE = \"PMID:8815791\", AUTHORS = \"Carter-Su C, Schwartz J, Smit LS\", TITLE = \"Molecular mechanism of growth hormone action.\", JOURNAL = c(\"Annu Rev Physiol 58:187-207 (1996)\", \"DOI:10.1146/annurev.ph.58.030196.001155\")), list(REFERENCE = \"PMID:10912517\", AUTHORS = \"Carter-Su C, Rui L, Herrington J\", TITLE = \"Role of the tyrosine kinase JAK2 in signal transduction by growth hormone.\", JOURNAL = c(\"Pediatr Nephrol 14:550-7 (2000)\", \"DOI:10.1007/s004670000366\")), list(REFERENCE = \"PMID:29487568\", AUTHORS = \"Dehkhoda F, Lee CMM, Medina J, Brooks AJ\", TITLE = \"The Growth Hormone Receptor: Mechanism of Receptor Activation, Cell Signaling, and Physiological Aspects.\", JOURNAL = c(\"Front Endocrinol (Lausanne) 9:35 (2018)\", \"DOI:10.3389/fendo.2018.00035\")), list(REFERENCE = \"PMID:26421979\", AUTHORS = \"Carter-Su C, Schwartz J, Argetsinger LS\", TITLE = \"Growth hormone signaling pathways.\", JOURNAL = c(\"Growth Horm IGF Res 28:11-5 (2016)\", \"DOI:10.1016/j.ghir.2015.09.002\")), list(REFERENCE = \"PMID:28435436\", AUTHORS = \"Berryman DE, Henry B, Hjortebjerg R, List EO, Kopchick JJ\", TITLE = \"Developments in our understanding of the effects of growth hormone on white adipose tissue from mice: implications to the clinic.\", JOURNAL = c(\"Expert Rev Endocrinol Metab 11:197-207 (2016)\", \"DOI:10.1586/17446651.2016.1147950\")), list(REFERENCE = \"PMID:9837978\", AUTHORS = \"Zhu T, Goh EL, LeRoith D, Lobie PE\", TITLE = \"Growth hormone stimulates the formation of a multiprotein signaling complex involving p130(Cas) and CrkII. Resultant activation of c-Jun N-terminal kinase/stress-activated protein kinase (JNK/SAPK).\", JOURNAL = c(\"J Biol Chem 273:33864-75 (1998)\", \"DOI:10.1074/jbc.273.50.33864\")), list(REFERENCE = \"\", AUTHORS = \"Choi S (ed).\", TITLE = \"Encyclopedia of Signaling Molecules\", JOURNAL = c(\"Spring