INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (368, '04940', 'Type I diabetes mellitus', 'c(\"Type I diabetes mellitus is a disease that results from autoimmune destruction of the insulin-producing beta-cells. Certain beta-cell proteins act as autoantigens after being processed by antigen-presenting cell (APC), such as macrophages and dendritic cells, and presented in a complex with MHC-II molecules on the surface of the APC. Then immunogenic signals from APC activate CD4+ T cells, predominantly of the Th1 subset. Antigen-activated Th1 cells produce IL-2 and IFNgamma. They activate macrophages and cytotoxic CD8+ T cells, and these effector cells may kill islet beta-cells by one or both of two types of mechanisms: (1) direct interactions of antigen-specific cytotoxic T cells with a beta-cell autoantigen-MHC-I complex on the beta-cell, and (2) non-specific inflammatory mediators, such as free radicals/oxidants and cytokines (IL-1, TNFalpha, TNFbeta, IFNgamma).\", \n\"Type I diabetes is a polygenic disease. One of the principle determining genetic factors in diabetes incidence is the inheritance of mutant MHC-II alleles. Another plausible candidate gene is the insulin gene.\")', 'Human Diseases; Endocrine and metabolic disease', 'Type I diabetes mellitus', 'Type 1 diabetes mellitus', 'ko04940', 'list(REFERENCE = \"PMID:9719467\", AUTHORS = \"Rabinovitch A, Suarez-Pinzon WL.\", TITLE = \"Cytokines and their roles in pancreatic islet beta-cell destruction and insulin-dependent diabetes mellitus.\", JOURNAL = c(\"Biochem Pharmacol 55:1139-49 (1998)\", \"DOI:10.1016/S0006-2952(97)00492-9\")), list(REFERENCE = \"PMID:11507694\", AUTHORS = \"Cox NJ, Wapelhorst B, Morrison VA, Johnson L, Pinchuk L, Spielman RS, Todd JA, Concannon P\", TITLE = \"Seven regions of the genome show evidence of linkage to type 1 diabetes in a consensus analysis of 767 multiplex families.\", JOURNAL = c(\"Am J Hum Genet 69:820-30 (2001)\", \"DOI:10.1086/323501\")), list(REFERENCE = \"PMID:8072542\", AUTHORS = \"Davies JL, Kawaguchi Y, Bennett ST, Copeman JB, Cordell HJ, Pritchard LE, Reed PW, Gough SC, Jenkins SC, Palmer SM, et al.\", TITLE = \"A genome-wide search for human type 1 diabetes susceptibility genes.\", JOURNAL = c(\"Nature 371:130-6 (1994)\", \"DOI:10.1038/371130a0\")), list(REFERENCE = \"PMID:8786033\", AUTHORS = \"Fujisawa T, Ikegami H, Kawaguchi Y, Yamato E, Takekawa K, Nakagawa Y, Hamada Y, Ueda H, Shima K, Ogihara T.\", TITLE = \"Class I HLA is associated with age-at-onset of IDDM, while class II HLA confers susceptibility to IDDM.\", JOURNAL = c(\"Diabetologia 38:1493-5 (1995)\", \"DOI:10.1007/BF00400620\")), list(REFERENCE = \"PMID:12796471\", AUTHORS = \"Jaeckel E, Klein L, Martin-Orozco N, von Boehmer H.\", TITLE = \"Normal incidence of diabetes in NOD mice tolerant to glutamic acid decarboxylase.\", JOURNAL = c(\"J Exp Med 197:1635-44 (2003)\", \"DOI:10.1084/jem.20030215\")), list(REFERENCE = \"PMID:15889095\", AUTHORS = \"Nakayama M, Abiru N, Moriyama H, Babaya N, Liu E, Miao D, Yu L, Wegmann DR, Hutton JC, Elliott JF, Eisenbarth GS.\", TITLE = \"Prime role for an insulin epitope in the development of type 1 diabetes in NOD mice.\", JOURNAL = c(\"Nature 435:220-3 (2005)\", \"DOI:10.1038/nature03523\")), list(REFERENCE = \"PMID:15889096\", AUTHORS = \"Kent SC, Chen Y, Bregoli L, Clemmings SM, Kenyon NS, Ricordi C, Hering BJ, Hafler DA.\", TITLE = \"Expanded T cells from pancreatic lymph nodes of type 1 diabetic subjects recognize an insulin epitope.\", JOURNAL = c(\"Nature 435:224-8 (2005)\", \"DOI:10.1038/nature03625\")), list(REFERENCE = \"PMID:14617043\", AUTHORS = \"Lieberman SM, DiLorenzo TP.\", TITLE = \"A comprehensive guide to antibody and T-cell responses in type 1 diabetes.\", JOURNAL = c(\"Tissue Antigens 62:359-77 (2003)\", \"DOI:10.1034/j.1399-0039.2003.00152.x\")), list(REFERENCE = \"PMID:12752668\", AUTHORS = \"Lord SJ, Rajotte RV, Korbutt GS, Bleackley RC.\", TITLE = \"Granzyme B: a natural born killer.\", JOURNAL = c(\"Immunol Rev 193:31-8 (2003)\", \"DOI:10.1034/j.1600-065X.2003.00044.x\"))', 'map04940', 'Type I diabetes mellitus', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (369, '04950', 'Maturity onset diabetes of the young', 'c(\"About 2-5% of type II diabetic patients suffer from a monogenic disease with autosomal dominant inheritance. This monogenic form of type II diabetes is called maturity onset diabetes of the young (MODY).\", \"We now know that MODY is caused by heterozygous mutations in at least five genes encoding transcription factors: HNF4alpha (MODY1), HNF1alpha (MODY3), PDX1 (MODY4), HNF1beta (MODY5) and NEUROD1 (MODY6). MODY2, which is so far the only subtype not related to a transcription factor, is caused by mutations in the glucokinase gene. Mutations of MODY transcription factor genes lead to abnormal expression of genes involved in pancreatic islet development and metabolism.\"\n)', 'Human Diseases; Endocrine and metabolic disease', 'Maturity onset diabetes of the young', 'ko04950', 'list(REFERENCE = \"PMID:15298336\", AUTHORS = \"Servitja JM, Ferrer J.\", TITLE = \"Transcriptional networks controlling pancreatic development and beta cell function.\", JOURNAL = c(\"Diabetologia 47:597-613 (2004)\", \"DOI:10.1007/s00125-004-1368-9\")), list(REFERENCE = \"PMID:15604203\", AUTHORS = \"Habener JF, Kemp DM, Thomas MK.\", TITLE = \"Minireview: transcriptional regulation in pancreatic development.\", JOURNAL = c(\"Endocrinology 146:1025-34 (2005)\", \"DOI:10.1210/en.2004-1576\")), list(REFERENCE = \"PMID:15028942\", AUTHORS = \"Ashizawa S, Brunicardi FC, Wang XP.\", TITLE = \"PDX-1 and the pancreas.\", JOURNAL = \"Pancreas 28:109-20 (2004)\"), list(REFERENCE = \"PMID:14614204\", AUTHORS = \"Yamagata K.\", TITLE = \"Regulation of pancreatic beta-cell function by the HNF transcription network: lessons from maturity-onset diabetes of the young (MODY).\", JOURNAL = c(\"Endocr J 50:491-9 (2003)\", \"DOI:10.1507/endocrj.50.491\"))', 'map04950', 'Maturity onset diabetes of the young', 'About 2-5% of type II diabetic patients suffer from a monogenic disease with autosomal dominant inheritance. This monogenic form of type II diabetes is called maturity onset diabetes of the young (MODY)., We now know that MODY is caused by heterozygous mutations in at least five genes encoding transcription factors: HNF4alpha (MODY1), HNF1alpha (MODY3), PDX1 (MODY4), HNF1beta (MODY5) and NEUROD1 (MODY6). MODY2, which is so far the only subtype not related to a transcription factor, is caused by mutations in the glucokinase gene. Mutations of MODY transcription factor genes lead to abnormal expression of genes involved in pancreatic islet development and metabolism.', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (370, '04960', 'Aldosterone-regulated sodium reabsorption', 'Sodium transport across the tight epithelia of Na+ reabsorbing tissues such as the distal part of the kidney nephron and colon is the major factor determining total-body Na+ levels, and thus, long-term blood pressure. Aldosterone plays a major role in sodium and potassium metabolism by binding to epithelial mineralocorticoid receptors (MR) in the renal collecting duct cells localized in the distal nephron, promoting sodium resorption and potassium excretion. Aldosterone enters a target cell and binds MR, which translocates into the nucleus and regulates gene transcription. Activation of MR leads to increased expression of Sgk-1, which phosphorylates Nedd4-2, an ubiquitin-ligase which targets ENAC to proteosomal degradation. Phosphorylated Nedd4-2 dissociates from ENAC, increasing its apical membrane abundance. Activation of MR also leads to increased expression of Na+/K+-ATPase, thus causing a net increase in sodium uptake from the renal filtrate. The specificity of MR for aldosterone is provided by 11beta-HSD2 by the rapid conversion of cortisol to cortisone in renal cortical collecting duct cells. Recently, besides genomic effects mediated by activated MR, rapid aldosterone actions that are independent of translation and transcription have been documented.', 'Organismal Systems; Excretory system', 'Aldosterone-regulated sodium reabsorption', 'Bartter syndrome, Liddle syndrome, Hyperkalemic distal renal tubular acidosis (RTA type 4), Hypertension exacerbated in pregnancy, Leprechaunism, Bronchiectasis with or without elevated sweat chloride, Rabson-Mendenhall syndrome, Alternating hemiplegia of childhood, Insulin-resistant diabetes mellitus with acanthosis nigricans, Renal tubular acidosis', 'ko04960', 'list(REFERENCE = \"PMID:16002531\", AUTHORS = \"Connell JM, Davies E\", TITLE = \"The new biology of aldosterone.\", JOURNAL = c(\"J Endocrinol 186:1-20 (2005)\", \"DOI:10.1677/joe.1.06017\")), list(REFERENCE = \"PMID:11850323\", AUTHORS = \"Booth RE, Johnson JP, Stockand JD\", TITLE = \"Aldosterone.\", JOURNAL = c(\"Adv Physiol Educ 26:8-20 (2002)\", \"DOI:10.1152/advan.00123.2015\")), list(REFERENCE = \"PMID:18197893\", AUTHORS = \"Lee IH, Campbell CR, Cook DI, Dinudom A\", TITLE = \"Regulation of epithelial Na+ channels by aldosterone: role of Sgk1.\", JOURNAL = c(\"Clin Exp Pharmacol Physiol 35:235-41 (2008)\", \"DOI:10.1111/j.1440-1681.2007.04844.x\")), list(REFERENCE = \"PMID:18174920\", AUTHORS = \"Viengchareun S, Le Menuet D, Martinerie L, Munier M, Pascual-Le Tallec L, Lombes M\", TITLE = \"The mineralocorticoid receptor: insights into its molecular and (patho)physiological biology.\", JOURNAL = c(\"Nucl Recept Signal 5:e012 (2007)\", \"DOI:10.1621/nrs.05012\")), list(REFERENCE = \"PMID:16286565\", AUTHORS = \"Fuller PJ, Young MJ\", TITLE = \"Mechanisms of mineralocorticoid action.\", JOURNAL = c(\"Hypertension 46:1227-35 (2005)\", \"DOI:10.1161/01.HYP.0000193502.77417.17\")), list(REFERENCE = \"PMID:12649599\", AUTHORS = \"Verrey F, Loffing J, Zecevic M, Heitzmann D, Staub O\", TITLE = \"SGK1: aldosterone-induced relay of Na+ transport regulation in distal kidney nephron cells.\", JOURNAL = c(\"Cell Physiol Biochem 13:21-8 (2003)\", \"DOI:10.1159/000070246\")), list(REFERENCE = \"PMID:17332888\", AUTHORS = \"Pearce D, Kleyman TR\", TITLE = \"Salt, sodium channels, and SGK1.\", JOURNAL = c(\"J Clin Invest 117:592-5 (2007)\", \"DOI:10.1172/JCI31538\")), list(REFERENCE = \"PMID:19549592\", AUTHORS = \"Grossmann C, Gekle M\", TITLE = \"New aspects of rapid aldosterone signaling.\", JOURNAL = c(\"Mol Cell Endocrinol 308:53-62 (2009)\", \"DOI:10.