INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (376, '04971', 'Gastric acid secretion', 'Gastric acid is a key factor in normal upper gastrointestinal functions, including protein digestion and calcium and iron absorption, as well as providing some protection against bacterial infections. The principal stimulants of acid secretion at the level of the parietal cell are histamine (paracrine), gastrin (hormonal), and acetycholine (ACh; neurocrine). Stimulation of acid secretion typically involves an initial elevation of intracellular calcium and cAMP, followed by activation of protein kinase cascades, which trigger the translocation of the proton pump, H+,K+-ATPase, from cytoplasmic tubulovesicles to the apical plasma membrane and thereby H+ secretion into the stomach lumen.', 'Organismal Systems; Digestive system', 'Gastric acid secretion', 'Hereditary pancreatitis, Zollinger-Ellison syndrome', 'GO: 0001696', 'ko04971', 'list(REFERENCE = \"PMID:18474247\", AUTHORS = \"Schubert ML, Peura DA\", TITLE = \"Control of gastric acid secretion in health and disease.\", JOURNAL = c(\"Gastroenterology 134:1842-60 (2008)\", \"DOI:10.1053/j.gastro.2008.05.021\")), list(REFERENCE = \"PMID:9307437\", AUTHORS = \"Urushidani T, Forte JG\", TITLE = \"Signal transduction and activation of acid secretion in the parietal cell.\", JOURNAL = c(\"J Membr Biol 159:99-111 (1997)\", \"DOI:10.1007/s002329900274\")), list(REFERENCE = \"PMID:12500969\", AUTHORS = \"Yao X, Forte JG\", TITLE = \"Cell biology of acid secretion by the parietal cell.\", JOURNAL = c(\"Annu Rev Physiol 65:103-31 (2003)\", \"DOI:10.1146/annurev.physiol.65.072302.114200\")), list(REFERENCE = \"PMID:17928547\", AUTHORS = \"Heitzmann D, Warth R\", TITLE