INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (386, '05012', 'Parkinson disease', 'Parkinson disease (PD) is a progressive neurodegenerative movement disorder that results primarily from the death of dopaminergic (DA) neurons in the substantia nigra pars compacta (SNc). Mutations in alpha-synuclein, UCHL1 (a ubiquitin carboxy-terminal hydrolase L1), parkin, DJ1 (a parkin-associated protein involved with oxidative stress), and PINK1 (a putative serine threonine kinase) are known to cause early-onset PD. Mutations or altered expression of these proteins contributes to the damage and subsequent loss of DA neurons through common mechanisms that result in proteasome dysfunction, mitochondrial impairment, and oxidative stress. The demise of DA neurons located in the SNc leads to a drop in the dopaminergic input to the striatum. This results in a reduced activation of the direct pathway and in a disinhibition of the indirect pathway, which is associated with the elevation of adenosine A2A receptor transmission. Such unbalanced activity of the striatal output pathway is at the basis of the motor impairment observed in PD.', 'Human Diseases; Neurodegenerative disease', 'Parkinson disease', 'Parkinson disease', 'ko05012', 'list(REFERENCE = \"PMID:17911161\", AUTHORS = \"Thomas B, Beal MF\", TITLE = \"Parkinson\'s disease.\", JOURNAL = c(\"Hum Mol Genet 16 Spec No. 2:R183-94 (2007)\", \"DOI:10.1093/hmg/ddm159\")), list(REFERENCE = \"PMID:12938733\", AUTHORS = \"Lim KL, Lim TM\", TITLE = \"Molecular mechanisms of neurodegeneration in Parkinson\'s disease: clues from Mendelian syndromes.\", JOURNAL = c(\"IUBMB Life 55:315-22 (2003)\", \"DOI:10.1080/1521654031000153030\")), list(REFERENCE = \"PMID:16022590\"