INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (388, '05016', 'Huntington disease', 'Huntington disease (HD) is an autosomal-dominant neurodegenerative disorder that primarily affects medium spiny striatal neurons (MSN). The symptoms are choreiform, involuntary movements, personality changes and dementia. HD is caused by a CAG repeat expansion in the IT15gene, which results in a long stretch of polyglutamine close to the amino-terminus of the HD protein huntingtin (Htt). Mutant Htt (mHtt) has effects both in the cytoplasm and in the nucleus. In the cytoplasm, full-length mHtt can interfere with BDNF vesicular transport on microtubules. This mutant protein also may lead to abnormal endocytosis and secretion in neurons, because normal Htt form a complex with the proteins Hip1, clathrin and AP2 that are involved in endocytosis. In addition, mHtt affects Ca2+ signaling by sensitizing InsP3R1 to activation by InsP3, stimulating NMDAR activity, and destabilizing mitochondrial Ca2+ handling. The mHtt translocates to the nucleus, where it forms intranuclear inclusions. Nuclear toxicity is believed to be caused by interference with gene transcription, leading to loss of transcription of neuroprotective molecules such as BDNF. While mHtt binds to p53 and upregulates levels of nuclear p53 as well as p53 transcriptional activity. Augmented p53 mediates mitochondrial dysfunction.', 'Human Diseases; Neurodegenerative disease', 'Huntington disease', 'Huntington disease', 'ko05016', 'list(REFERENCE = \"PMID:15459747\", AUTHORS = \"Landles C, Bates GP\", TITLE = \"Huntingtin and the molecular pathogenesis of Huntington\'s disease. Fourth in molecular medicine review series.\", JOURNAL = c(\"EMBO Rep 5:958-63 (2004)\", \"DOI:10.1038/sj.embor.7400250\")), list(REFERENCE = \"PMID:17041811\", AUTHORS = \"Borrell-Pages M, Zala D, Humbert S, Saudou F\", TITLE = \"Huntington\'s disease: from huntingtin function and dysfunction to therapeutic strategies.\", JOURNAL = c(\"Cell Mol Life Sci 63:2642-60 (2006)\", \"DOI:10.1007/s00018-006-6242-0\")), list(REFERENCE = \"PMID:18951640\", AUTHORS = \"Bossy-Wetzel E, Petrilli A, Knott AB\", TITLE = \"Mutant huntingtin and mitochondrial dysfunction.\", JOURNAL = c(\"Trends Neurosci 31:609-16 (2008)\", \"DOI:10.1016/j.tins.2008.09.004\")), list(REFERENCE = \"PMID:16288298\", AUTHORS = \"Cattaneo E, Zuccato C, Tartari M\", TITLE = \"Normal huntingtin function: an alternative approach to Huntington\'s disease.\", JOURNAL = c(\"Nat Rev Neurosci 6:919-30 (2005)\", \"DOI:10.1038/nrn1806\")), list(REFERENCE = \"PMID:15336977\", AUTHORS = \"Bezprozvanny I, Hayden MR\", TITLE = \"Deranged neuronal calcium signaling and Huntington disease.\", JOURNAL = c(\"Biochem Biophys Res Commun 322:1310-7 (2004)\", \"DOI:10.1016/j.bbrc.2004.08.035\")), list(REFERENCE = \"PMID:14526190\", AUTHORS = \"Sawa A, Tomoda T, Bae BI\", TITLE = \"Mechanisms of neuronal cell death in Huntington\'s disease.\", JOURNAL = c(\"Cytogenet Genome Res 100:287-95 (2003)\", \"DOI:10.1159/000072864\")), list(REFERENCE = \"PMID:12372277\", AUTHORS = \"Ross CA.\", TITLE = \"Polyglutamine pathogenesis: emergence of unifying mechanisms for Huntington\'s disease and related disorders.\", JOURNAL = c(\"Neuron 35:819-22 (2002)\", \"DOI:10.1016/S0896-6273(02)00872-3\")), list(REFERENCE = \"PMID:15242639\", AUTHORS = \"Ross CA\", TITLE = \"Huntington\'s disease: new paths to pathogenesis.\", JOURNAL = c(\"Cell 118:4-7 (2004)\", \"DOI:10.1016/j.cell.2004.06.022\")), list(REFERENCE = \"PMID:15695335\", AUTHORS = \"Tang TS, Slow E, Lupu V, Stavrovskaya IG, Sugimori M, Llinas R, Kristal BS, Hayden MR, Bezprozvanny I\", TITLE = \"Disturbed Ca2+ signaling and apoptosis of medium spiny neurons in Huntington\'s disease.\", JOURNAL = c(\"Proc Natl Acad Sci U S A 102:2602-7 (2005)\", \"DOI:10.1073/pnas.0409402102\")), list(REFERENCE = \"PMID:11807533\", AUTHORS = \"Mattson MP.\", TITLE = \"Accomplices to neuronal death.\", JOURNAL = c(\"Nature 415:377-9 (2002)\", \"DOI:10.1038/415377a\")), list(REFERENCE = \"PMID:17081970\", AUTHORS = \"McGill JK, Beal MF\", TITLE = \"PGC-1alpha, a new therapeutic target in Huntington\'s disease?\", JOURNAL = c(\"Cell 127:465-8 (2006)\", \"DOI:10.1016/j.cell.2006.10.023\")), list(REFERENCE = \"PMID:15996541\", AUTHORS = \"La Spada AR, Morrison RS\", TITLE = \"The power of the dark side: Huntington\'s disease protein and p53 form a deadly alliance.\", JOURNAL = c(\"Neuron 47:1-3 (2005)\", \"DOI:10.1016/j.neuron.2005.06.023\")), list(REFERENCE = \"PMID:11738471\", AUTHORS = \"Lesort M, Chun W, Tucholski J, Johnson GV\", TITLE = \"Does tissue transglutaminase play a role in Huntington\'s disease?\", JOURNAL = c(\"Neurochem Int 40:37-52 (2002)\", \"DOI:10.1016/S0197-0186(01)00059-6\")), list(REFERENCE = \"PMID:15996546\", AUTHORS = \"Bae BI, Xu H, Igarashi S, Fujimuro M, Agrawal N, Taya Y, Hayward SD, Moran TH, Montell C, Ross CA, Snyder SH, Sawa A\", TITLE = \"p53 mediates cellular dysfunction and behavioral abnormalities in Huntington\'s disease.\", JOURNAL = c(\"Neuron 47:29-41 (2005)\", \"DOI:10.1016/j.neuron.2005.06.005\")), list(REFERENCE = \"PMID:11839795\", AUTHORS = \"Li SH, Cheng AL, Zhou H, Lam S, Rao M, Li H, Li XJ\", TITLE = \"Interaction of Huntington disease protein with transcriptional activator Sp1.\", JOURNAL = c(\"Mol Cell Biol 22:1277-87 (2002)\", \"DOI:10.1128/MCB.22.5.1277-1287.2002\"))', 'map05016', 'Huntington disease', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (389, '05020', 'Prion diseases', 'Prion diseases, also termed transmissible spongiform encephalopathies (TSEs), are a group of fatal neurodegenerative diseases that affect humans and a number of other animal species. The etiology of these diseases is thought to be associated with the conversion of a normal protein, PrPC, into an infectious, pathogenic form, PrPSc. The conversion is induced by prion infections (for example, variant Creutzfeldt-Jakob disease (vCJD), iatrogenic CJD, Kuru), mutations (familial CJD, Gerstmann-Straussler-Scheinker syndrome, fatal familial insomnia (FFI)) or unknown factors (sporadic CJD (sCJD)), and is thought to occur after PrPC has reached the plasma membrane or is re-internalized for degradation. The PrPSc form shows greater protease resistance than PrPC and accumulates in affected individuals, often in the form of extracellular plaques. Pathways that may lead to neuronal death comprise oxidative stress, regulated activation of complement, ubiquitin-proteasome and endosomal-lysosomal systems, synaptic alterations and dendritic atrophy, corticosteroid response, and endoplasmic reticulum stress. In addition, the conformational transition could lead to the lost of a beneficial activity of the natively folded protein, PrPC.', 'Human Diseases; Neurodegenerative disease', 'Prion diseases', 'Prion disease', 'ko05020', 'list(REFERENCE = \"PMID:18245809\", AUTHORS = \"Kovacs GG, Budka H\", TITLE = \"Prion diseases: from protein to cell pathology.\", JOURNAL = c(\"Am J Pathol 172:555-65 (2008)\", \"DOI:10.2353/ajpath.2008.070442\")), list(REFERENCE = \"PMID:15695097\", AUTHORS = \"Campana V, Sarnataro D, Zurzolo C\", TITLE = \"The highways and byways of prion protein trafficking.\", JOURNAL = c(\"Trends Cell Biol 15:102-11 (2005)\", \"DOI:10.1016/j.tcb.2004.12.002\")), list(REFERENCE = \"PMID:14661267\", AUTHORS = \"Nunziante M, Gilch S, Schatzl HM\", TITLE = \"Prion diseases: from molecular biology to intervention strategies.\", JOURNAL = c(\"Chembiochem 4:1268-84 (2003)\", \"DOI:10.1002/cbic.200300704\")), list(REFERENCE = \"PMID:17051207\", AUTHORS = \"Caughey B, Baron GS\", TITLE = \"Prions and their partners in crime.\", JOURNAL = c(\"Nature 443:803-10 (2006)\", \"DOI:10.1038/nature05294\")), list(REFERENCE = \"PMID:18391177\", AUTHORS = \"Linden R, Martins VR, Prado MA, Cammarota M, Izquierdo I, Brentani RR\", TITLE = \"Physiology of the prion protein.\", JOURNAL = c(\"Physiol Rev 88:673-728 (2008)\", \"DOI:10.1152/physrev.00007.2007\")), list(REFERENCE = \"PMID:11592845\", AUTHORS = \"Chiesa R, Harris DA\", TITLE = \"Prion diseases: what is the neurotoxic molecule?\", JOURNAL = c(\"Neurobiol Dis 8:743-63 (2001)\", \"DOI:10.1006/nbdi.2001.0433\")), list(REFERENCE = \"PMID:10398674\", AUTHORS = \"Harris DA\", TITLE = \"Cellular biology of prion diseases.\", JOURNAL = \"Clin Microbiol Rev 12:429-44 (1999)\"), list(REFERENCE = \"PMID:15528202\", AUTHORS = \"Marella M, Gaggioli C, Batoz M, Deckert M, Tartare-Deckert S, Chabry J\", TITLE = \"Pathological prion protein exposure switches on neuronal mitogen-activated protein kinase pathway resulting in microglia recruitment.\", JOURNAL = c(\"J Biol Chem 280:1529-34 (2005)\", \"DOI:10.1074/jbc.M410966200\")), list(REFERENCE = \"PMID:15611724\", AUTHORS = \"Mallucci G, Collinge J\", TITLE = \"Rational targeting for prion therapeutics.\", JOURNAL = c(\"Nat Rev Neurosci 6:23-34 (2005)\", \"DOI:10.1038/nrn1584\")), list(REFERENCE = \"PMID:12850426\", AUTHORS = \"Hegde RS, Rane NS\", TITLE = \"Prion protein trafficking and the development of neurodegeneration.\", JOURNAL = c(\"Trends Neurosci 26:337-9 (2003)\", \"DOI:10.1016/S0166-2236(03)00143-7\")), list(REFERENCE = \"PMID:17085779\", AUTHORS = \"Fasano C, Campana V, Zurzolo C\", TITLE = \"Prions: protein only or something more? Overview of potential prion cofactors.\", JOURNAL = c(\"J Mol Neurosci 29:195-214 (2006)\", \"DOI:10.1385/JMN:29:3:195\")), list(REFERENCE = \"PMID:14705136\", AUTHORS = \"Roucou X, Gains M, LeBlanc AC\", TITLE = \"Neuroprotective functions of prion protein.\", JOURNAL = c(\"J Neurosci Res 75:153-61 (2004)\", \"DOI:10.1002/jnr.10864\")), list(REFERENCE = \"PMID:15890824\", AUTHORS = \"Novakofski J, Brewer MS, Mateus-Pinilla N, Killefer J, McCusker RH\", TITLE = \"Prion biology relevant to bovine spongiform encephalopathy.\", JOURNAL = c(\"J Anim Sci 83:1455-76 (2005)\", \"DOI:10.2527/2005.8361455x\")), list(REFERENCE = \"PMID:18558863\", AUTHORS = \"Aguzzi A, Baumann F, Bremer J\", TITLE = \"The prion\'s elusive reason for being.\", JOURNAL = c(\"Annu Rev Neurosci 31:439-77 (2008)\", \"DOI:10.1146/annurev.neuro.31.060407.125620\")), list(REFERENCE = \"PMID:15342004\", AUTHORS = \"Mabbott NA\", TITLE = \"The complement system in prion diseases.\", JOURNAL = c(\"Curr Opin Immunol 16:587-93 (2004)\", \"DOI:10.1016/j.coi.2004.07.002\"))', 'map05020', 'Prion diseases', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (390, '05030', 'Cocaine addiction', 'Drug addiction is a chronic, relapsing disorder in which compulsive drug-seeking and drug-taking behavior persists despite serious negative consequences.There is strong evidence that the dopaminergic system that projects from the ventral tegmental area (VTA) of the midbrain to the nucleus accumbens (NAc), and to other forebrain sites, is the major substrate of reward and reinforcement for both natural rewards and addictive drugs. Cocaine binds strongly to the dopamine-reuptake transporter, preventing the reuptake of dopamine into the nerve terminal. Because of this blocking effect, dopamine remains at high concentrations in the synapse and continues to affect adjacent neurons, producing the characteristic cocaine \"high.\" Activated D1 receptor activates the PKA signaling pathway, and this pathway plays a critical role in mediating the behavioral responses to cocaine administration. Cocaine-induced neuroadaptations, including dopamine depletion, may underlie craving and hedonic dysregulation.', 'Human Diseases; Substance dependence', 'Cocaine addiction', 'GO: 0042220', 'ko05030', 'list(REFERENCE = \"PMID:16278489\", AUTHORS = \"Ron D, Jurd R\", TITLE = \"The \\\"ups and downs\\\" of signaling cascades in addiction.\", JOURNAL = c(\"Sci STKE 2005:re14 (2005)\", \"DOI:10.1126/stke.3092005re14\")), list(REFERENCE = \"PMID:18440072\", AUTHORS = \"Cunha-Oliveira T, Rego AC, Oliveira CR\", TITLE = \"Cellular and molecular mechanisms involved in the neurotoxicity of opioid and psychostimulant drugs.\", JOURNAL = c(\"Brain Res Rev 58:192-208 (2008)\", \"DOI:10.1016/j.brainresrev.2008.03.002\")), list(REFERENCE = \"PMID:1